float smin( float a, float b){
    a = pow(a, 8.0);
    b = pow(b, 8.0);
    return pow(a*b/(a+b), 0.125);
}

bool isWater(vec2 p){
    return texelFetch(iChannel0,ivec2(mod(p,64.0)),0).r*texelFetch(iChannel0,ivec2(mod(p/2.0,64.0)),0).r < 0.5;
}

float wave(float x){
    return 2.0*abs(fract(x/16.0) - 0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = floor(fragCoord);
    vec2 block = floor(uv/16.0);

    vec3 col = mix(vec3(0.7,1.0,0.6), vec3(0.1,0.7,0.5), (texelFetch(iChannel0,ivec2(mod(uv,16.0)),0).r + 0.5)*(texelFetch(iChannel0,ivec2(mod(uv/2.0,8.0)),0).r + 0.1));

    if (isWater(block)){
        vec2 m = uv.xy/16.0 - block.xy;
        vec2 p = 1.0 - m - 0.0625;
        float d = !isWater(block + vec2(1,0)) ? p.x : 1.0;
        d = smin(d, !isWater(block - vec2(1,0)) ? m.x : 1.0);
        d = smin(d, !isWater(block + vec2(0,1)) ? p.y : 1.0);
        d = smin(d, !isWater(block - vec2(0,1)) ? m.y : 1.0);
        if (d >= 0.5){
            d = smin(d, !isWater(block + vec2(1,1)) ? sqrt(p.x*p.x + p.y*p.y) : 1.0);
            d = smin(d, !isWater(block + vec2(-1,1)) ? sqrt(m.x*m.x + p.y*p.y) : 1.0);
            d = smin(d, !isWater(block + vec2(1,-1)) ? sqrt(p.x*p.x + m.y*m.y) : 1.0);
            d = smin(d, !isWater(block + vec2(-1,-1)) ? sqrt(m.x*m.x + m.y*m.y) : 1.0);
        }

        vec2 wavecoord = (uv + vec2(iTime + wave(iTime+uv.y),0))/vec2(2,1);
        float shorewaveheight = float(max(wave(floor(d*16.0 + iTime*4.0)), wave(floor(d*16.0 - iTime*4.0))) == 1.0);
        float oceanwaveheight = max(texelFetch(iChannel0,ivec2(mod(wavecoord,64.0)),0).r - 0.99,0.0)*100.0;
        float k = d == 0.0 ? 1.0 : shorewaveheight*clamp(1.0 - d*4.0,0.0,1.0);
        if (d == 1.0/16.0) k = max(k,max(texelFetch(iChannel0,ivec2(mod(uv/2.0,64.0)),0).r - 0.3,0.0));
        vec3 basecol = mix(vec3(0.28,0.85,0.91),vec3(0.17,0.54,0.71),clamp(floor(d*d*8.0)/4.0,0.0,1.0));
        col = mix(basecol,vec3(1),clamp(max(floor(k*4.0)/4.0, oceanwaveheight),0.0,1.0));
    }

    fragColor = vec4(col,1.0);
}

