// License: CC0 (https://creativecommons.org/publicdomain/zero/1.0/)


float capsule_aa(vec2 tc, float r, float s, vec2 a, vec2 b)
{
    vec2 vec = b - a;

    tc -= a;

    float tt = dot(vec, vec);

    float rcptt = 1.0 / tt;

    vec2 p = vec * (tt == 0.0 ? 0.0 : clamp(dot(tc, vec * rcptt), 0.0, 1.0));

    float l = length(tc - p);

    float v = clamp((r - l) * s * 0.5, 0.0, 1.0);

    v = v*v*(3.0-2.0*v);

    return v;
}

float lineseg_mb(float x, float r, float t)
{
    float u = r + t * 0.5;

    return clamp(u - abs(-x - r + u), 0.0, min(2.0 * r, t)) * (1.0/t);
}

float disk_mb(vec2 tc, float r, float t)
{
    float r0 = sqrt(max(0.0, r*r - tc.y*tc.y));

    return lineseg_mb(tc.x, r0, t);
}

