	vec2 i = vec2(p);
	float c = 1.0;
    float c1 = 1.0;
	float inten = .009;

    //pattern control
    float iTimeCtrl = iTime * 0.12 - 1.0;
    //iTimeCtrl = 1.0;
    float scale = 2.0; 
    float timetest = -sin((iTimeCtrl * 1.0/scale - 0.25) * 3.1415926 * 4.0/2.0) * 0.5 + 0.5;
    float endPattern = mix(5.0, 3.5, uv.y + pow(1.0-uv.y, 5.0));
    float pattern =  mix(3.0, endPattern, timetest * 1.2);

    //waterpatterntilingpattern
	for (int n = 0; n < int(MAX_ITER); n++) 
	{
		float t = time * (1.0 - (2.5/ float(n+1)));
        float t1= time * .2;
		i = p + vec2(cos(t1 - i.x) + sin(t + i.y), sin(t - i.y) + cos(t1 + i.x));
		c += 1.0/length(vec2(p.x / (sin(i.x+ t1)/inten),p.y / (cos(i.y+t)/inten)));
        c1 += 1.0/length(vec2(p.x / (sin(i.x+ t1)/inten),p.y / (cos(i.y+t)/inten)));
	}

	c /= float(pattern);
    c1 /= float(3);

    //1.12 
    //1. 1,
	c = 1.25 - pow(c, 1.1);
    c1 = 1.25 - pow(c1, 1.1);

    float intensityblue = mix(13.0, 9.0, timetest);
    vec3 blueChange = mix(vec3(0.000,0.212,0.302), vec3(0.000,0.349,0.502), timetest);
    //7.5
	vec3 colour = vec3(pow(abs(c), intensityblue));
    colour = clamp(colour + blueChange, 0.0, 1.0);

    vec3 colour1 = vec3(pow(abs(c1), 9.0));
    colour1 = clamp(colour1 + blueChange, 0.0, 1.0);

	#ifdef SHOW_TILING
	// Flash tile borders...
	vec2 pixel = 2.0 / iResolution.xy;
	uv *= 2.0;
	float f = floor(mod(iTime*.5, 2.0)); 	// Flash value.
	vec2 first = step(pixel, uv) * f;		   	// Rule out first screen pixels and flash.
	uv  = step(fract(uv), pixel);				// Add one line of pixels per tile.
	colour = mix(colour, vec3(1.0, 1.0, 0.0), (uv.x + uv.y) * first.x * first.y); // Yellow line
	#endif

    //WhiteEdge
    float offset = uv.y + iTimeCtrl;
    float y = mod(offset, scale);

    float d = smoothstep(0.2 * scale, 0.0  * scale, y) * 1.0;
    float d1 = smoothstep(0.9  * scale, 1.0  * scale, y) * 1.0;
    vec3 maskColor =  vec3(d) + vec3(d1);

    //maskColor
    //maskColor *= pow(1.0 - uv.y, 2.0);   //test0 0-1.0
    //maskColor *= pow(1.0 - uv.y, 2.0)/2.0 + 0.5; //test1 0.5-1.0
    maskColor *= smoothstep(0.0, 1.0, pow(1.0 - uv.y, 1.0) + 0.15) * 0.8; //test2 0.2-1.0

    float near = pow(uv.y, 3.5) * mix(0.75, 0.5, timetest);
    vec3 frontEdge = vec3(near);

    fragColor.rgb = colour;
    fragColor.rgb = colour + maskColor + frontEdge ; 

    //debug
    //fragColor.rgb = colour1 + frontEdge;
    float button;
    //button = 1.0;
    button = smoothstep(0.0, 1.0, iTime * 0.05);

    fragColor.rgb = mix(colour1 + pow(uv.y, 6.0), fragColor.rgb, button);

    //mask
    float mask = pow(uv.y, 1.0);
    //0.492
    mask = smoothstep(0.0,0.472,uv.y);

    //fragColor *= (mask);
    //fragColor = vec4(endColor, 1.0);
    //fragColor = vec4(frontEdge + maskColor,1.0);

    if (uv.x < 0. || uv.x > 1. || uv.y < 0. || uv.y > 1.) fragColor = vec4(0);    
}

#define PI acos(-1.)
#define TAU (PI*2.)

#define sat(x) clamp(x, 0.0, 1.0)
#define s2u(x) (x*.5+.5)
#define u2s(x) ((x*2.)-1.)

// "hash11()" - "hash44()"
// by David Hoskins
// https://www.shadertoy.com/view/4djSRW
float hash11(float p){
	p = fract(p * .1031);
	p *= p + 33.33;
	p *= p + p;
	return fract(p);
}
float hash13(vec3 p3) {
    p3 = fract(p3 * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}
vec2 hash21(float p) {
    vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx + p3.yz) * p3.zy);
}
vec2 hash22(vec2 p)
{
    vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}
vec3 hash33(vec3 p3) {
    p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz + 33.33);
    return fract((p3.xxy + p3.yxx) * p3.zyx);
}

vec4 bUni(vec4 a, vec4 b){
	return a.x<b.x?a:b;
}

// from iq
// https://iquilezles.org/articles/distfunctions/
float sdCapsule( vec3 p, vec3 a, vec3 b, float r ){
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}

float sdBox( in vec2 p, in vec2 b ){
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

#define MIN_DIST 0.001
#define MAX_DIST 1500.0
#define COL_AMB vec3(0.541,0.718,1.000)
#define COL_L0 vec3(1.000,0.965,0.898)
#define COL_FLR vec3(0.792,0.769,0.780)*(1./PI)
#define COL_S1 vec3(0.537,0.561,0.580)*(1./PI)
#define COL_S2 vec3(0.259,1.000,0.545)*(1./PI)
#define COL_S3 vec3(0.224,0.596,0.996)*(1./PI)
#define COL_S4 vec3(0.545,0.302,1.000)*(1./PI)

// i forgot where i found these bx_cossin()...
// maybe from eiffie's shader
vec2 bx_cos(vec2 a){return clamp(abs(mod(a,8.0)-4.0)-2.0,-1.0,1.0);}
vec2 bx_cossin(float a){return bx_cos(vec2(a,a-2.0));}
vec3 grd = vec3(0);
vec4 sdScene(vec3 p){
	float d = MAX_DIST;
	vec4 res = vec4(MAX_DIST, vec3(0));

    float sphereRad = .5*(cos(iTime*.25)+1.)+.2;
    float gridSize = pow(sin(iTime)*5.+5.,1.5)*2.+sphereRad*1.5;

    // grid
    vec3 q = p;
    q.xz = mod(q.xz, gridSize);
    q.y-= sphereRad;

    vec2 iqq = floor(p.xz/gridSize);

    bool bw = sin(iTime*.5+PI*.25)>0.;

    // tap only 4 neighbors
    // see the image posted as comment
    for(int x=-1; x<=0; x++)
    for(int z=-1; z<=0; z++){

        vec2 offset = vec2(x,z);
        vec2 rnd = hash22(iqq-offset);

        // neighbor cell position
        vec3 qq = q;
        qq.xz+=offset*gridSize; 

        // animation
        float v =  rnd.x * iTime*.5 * sign(rnd.y-rnd.x); 
        vec2 sq = bx_cossin(v)*(gridSize-sphereRad)*rnd.y;
        qq.xz+= sq;

        // jumping. just for fun.
        float j = abs(sin(rnd.x*3.+iTime*3.*4.))*rnd.x*5.;
        qq.y-=j;

        // random color
        vec2 iq = abs(iqq-offset);
        iq = vec2(int(iq.x)%2, int(iq.y)%2);
        float tile = (iq.x==0. || iq.y==0.) ? (iq.x==1. || iq.y==1.) ? 1. : .05 : .05;
        vec3 m = (rnd.y<.5) ? (tile<.5)?COL_S1:COL_S2 : (tile<.5)?COL_S3:COL_S4;

        // me
        if(length(vec3(p.x,j,p.z))<sphereRad+.01 )m=vec3(bw?.9:.005);

        res = bUni(vec4(length(qq)-sphereRad, m), res);
        res = bUni(vec4(sdCapsule(qq, vec3(0), vec3(sq.x, -sphereRad-j, sq.y), .03), m), res);
        res = bUni(vec4(length(q+vec3(offset.x*gridSize,sphereRad,offset.y*gridSize))-.15, m), res);
    }

    // prevent overshooting
    res.x = min(res.x, -sdBox(q.xz-vec2(gridSize)*.5, vec2(gridSize)*.75));

    // floor
    vec2 iq = abs(floor(iqq));
    iq = vec2(int(iq.x)%2, int(iq.y)%2);
    float tile = max(smoothstep(gridSize*.995*.5, gridSize*1.0005*.5, abs(q.x-gridSize*.5)),smoothstep(gridSize*.995*.5, gridSize*1.0005*.5, abs(q.z-gridSize*.5)));
    if(!bw)tile=1.-tile;
    else tile=.025+.975*tile;
    res = bUni(vec4(p.y, COL_FLR*tile),res);

	return res;
}

vec4 intersect(vec3 ro, vec3 rd){
	float d = 1.;
	vec3  m = vec3(0);
    grd=rd;
	for (int i = 0; i < 600; i++){
		vec4 res = sdScene(ro + d * rd);
		m = res.yzw;
		if (abs(res.x) < MIN_DIST || res.x >= MAX_DIST) break;
		d += res.x;
		if (d >= MAX_DIST) break;
	}
	return vec4(d,m);
}

vec3 normal(vec3 p){
    // Copy from iq shader.
    // inspired by tdhooper and klems - a way to prevent the compiler from inlining map() 4 times
    vec3 n = vec3(0.0);
    for( int i=0; i<4; i++ ){
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*sdScene(p+0.0005*e).x;
    }
    return normalize(n);
}

float shadow(vec3 o, vec3 ldir){
	float mint=.001;
	float maxt=5.;
	float k = 40.;
	float res = 1.;
	float t=mint;
	for( int i=0; i < 80; i++){
		float h = sdScene(o + ldir*t).x;
		res = min( res, k*h/t);
		t += h;
		if( res<0.01 || t>maxt ) break;
	}
	return sat(res);
}

// "Multi Level AO" by iY0Yi
// https://www.shadertoy.com/view/fsBfDR
float aoSeed = 0.;
const float MAX_SAMP = 4.;
float ao(vec3 p, vec3 n, float sphereRadius) {
    float ao = 0.;
    for(float i = 0.; i <= MAX_SAMP; i++) {
        vec2 rnd = hash21(i + 1. + aoSeed);

        float scale = (i + 1.)/MAX_SAMP;
        scale = mix(.0, 1., pow(scale, .5));

        rnd.x = (rnd.x * 2. - 1.) * PI * .5;
        rnd.y = (rnd.y * 2. - 1.) * PI;
        vec3 rd = normalize(n + hash21(i + 2. + aoSeed).xyx);
        rd.xy *= mat2(cos(rnd.x), sin(rnd.x), -sin(rnd.x), cos(rnd.x));
        rd.xz *= mat2(cos(rnd.y), sin(rnd.y), -sin(rnd.y), cos(rnd.y));

        rd *= sign(dot(rd, n));

        float raylen = sphereRadius * scale;
        vec3 rndp = p + normalize(n + rd) * raylen;
        float res = sdScene(rndp).x;
        ao += res;
        aoSeed++;
    }
    return ao/float(MAX_SAMP);
}

// https://hanecci.hatenadiary.org/entry/20130505/p2
// http://www.project-asura.com/program/d3d11/d3d11_006.html
float normalizedBlinnPhong(float shininess, vec3 n, vec3 vd, vec3 ld){
	float norm_factor = (shininess+1.) / (2.*PI);
	vec3 h  = normalize(-vd+ld);
	return pow(max(0., dot(h, n)), shininess) * norm_factor;
}

vec3 mmr=vec3(0);

vec3 render(vec2 uv){

    // camera
    float cL = 20.;
    const vec3 forcus = vec3(0,-.5,0);
    const float fov = .75;

    vec3 up = vec3(0,1,0);
    vec3 ro = vec3(0,14.,0);
    float t = iTime;
    ro.xz = vec2(sin(t),cos(t))*cL;
    if(iMouse.z>.5)
        ro.xz = vec2(sin(iMouse.x/iResolution.x*TAU),cos(iMouse.x/iResolution.x*TAU))*cL;
    vec3 dir = normalize(forcus-ro);
    vec3 target = ro-dir;
    vec3 cw = normalize(target - ro);
    vec3 cu = normalize(cross(cw, up));
    vec3 cv = normalize(cross(cu, cw));

    mat3 camMat = mat3(cu, cv, cw);
    vec3 rd = normalize(camMat * normalize(vec3(sin(fov) * uv.x, sin(fov) * uv.y, -cos(fov))));

    // ray march
	vec4 res = intersect(ro, rd);

	vec3 p = ro + res.x * rd;
    vec3 m = res.yzw;
    vec3 col = COL_AMB;
	if (res.x<MAX_DIST){
        vec3 ldir = normalize(vec3(-0.0875, 0.7071, -0.3935));
		vec3 n = normal(p);

		float lamb1 = sat(dot(n, ldir))*(1./PI);
		float lamb2 = sat(dot(n, -ldir))*(1./PI);

		float sh = shadow(p+n*.01, ldir);
        float rgh = (length(m)<.3) ? .9 : 1.*texture(iChannel0, p.xz*.02).g*.01;
        float spec = normalizedBlinnPhong(1./rgh, n, rd, ldir);
        float a = ao(p-n*.1, n, .25);
        a += ao(p-n*.25, n, .5);
        a /= 2.;

		float dif = lamb1;

        float hl = dot(n, ldir)*.5+.5;
        vec3 mr = mix(vec3(0), mix(sdScene(p+ldir*.5).yzw, sdScene(p+ldir*1.5).yzw, .5), a);mmr=mr;
		dif *= sh;
		col = mix(vec3(0), COL_L0*9., dif);

		col += mix(vec3(0), COL_AMB, a)*1.5;
		col*= m;
        col += mix(vec3(0), mr, a*hl)*(.2+.8-sh*hl*.8)*1.5;

        float intensity = .1*texture(iChannel0, p.xz*.02).g;
		spec = spec*intensity;
		col = mix(col, col+COL_L0, spec*sh);
		col = mix(col, COL_AMB, sat(pow(distance(ro, p)/MAX_DIST*4., 3.)));
	}
    return col;
}

// "Physically-based SDF" by romainguy:
//https://www.shadertoy.com/view/XlKSDR
vec3 Tonemap_ACES(const vec3 x){
    const float a = 2.51;
    const float b = 0.03;
    const float c = 2.43;
    const float d = 0.59;
    const float e = 0.14;
    return (x * (a * x + b)) / (x * (c * x + d) + e);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv = (uv*2.-1.)*iResolution.y/iResolution.x;
	uv.x *= iResolution.x / iResolution.y;

	vec3 col = render(uv);
    col = Tonemap_ACES(col);
    //col=mmr;
    col = mix(col,smoothstep(.0,1.,col),.3);
	col = pow(col, vec3(.45454));
	fragColor = vec4(col,1);
}

