// Signed distance to a line segment

// List of some other 2D distances: https://www.shadertoy.com/playlist/MXdSRf
//
// and iquilezles.org/articles/distfunctions2d
//
// sdSegment from:
// https://www.youtube.com/watch?v=PMltMdi1Wzg


float sdCircle(in vec2 p, in float r) {
    return length(p)-r;
}

float sdSegment(in vec2 p, in vec2 a, in vec2 b, in float r) {
    float h = min(1.,
              max(0.,
                  dot(p-a, b-a) /
                  dot(b-a, b-a)));
    return length(p-a-(b-a)*h)-r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    float r = texture(iChannel0, p).r * 0.3 + 0.1;

	float d = sdSegment(p, vec2(0, 0), m, r);

	// coloring
    vec3 col = (d>0.0) ? vec3(0.9,0.6,0.3) : vec3(0.65,0.85,1.0);
    col *= 1.0 - exp(-6.0*abs(d));
	col *= 0.8 + 0.2*cos(150.0*min(0., d));
	col = mix(col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(d)));

	fragColor = vec4(col,1.0);
}

