// Signed distance to a disk

// List of some other 2D distances: https://www.shadertoy.com/playlist/MXdSRf
//
// and iquilezles.org/articles/distfunctions2d


float sdCircle( in vec2 p, in float r ) {
    return length(p)-r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    float r = texture(iChannel0, vec2(p.x, p.y)).r * (cos(iTime * 0.1) * 0.5 + 0.5) + 0.1;

	float d = sdCircle(p,r);

	// coloring
    vec3 col = (d>0.0) ? vec3(0.9,0.6,0.3) : vec3(0.65,0.85,1.0);
    col *= 1.0 - exp(-6.0*abs(d));
	col *= 0.8 + 0.2*cos(150.0*d);
	col = mix(col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(d)));

    if(iMouse.z>0.001) {
        d = sdCircle(m,r);
        col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0025));
        col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }

	fragColor = vec4(col,1.0);
}

#define LINEWIDTH 0.01
#define WAVES 16.0

vec3 palette(float t)
{
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(2.0, 1.0, 0.0);
    vec3 d = vec3(0.50, 0.20, 0.25);

    return a + b * cos(6.28318*(c * t +d ));
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

    vec3 col = vec3(0.0);
    for (float i = 0.5; i < WAVES; ++i)
    {
        float r = i / iTime;
        float y = (1.0 - r) * sin((5.0 * uv.x + r) + (2.0 * r)) + 0.5;

        if(uv.y > y - LINEWIDTH && uv.y < y + LINEWIDTH)
            col = palette(r) * uv.y;
    }

	fragColor = vec4(col, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Time in seconds
    float time = iTime;

    // Number of lights
    const int numLights = 20;

    // Frequency of the color cycle
    const float cycleFrequency = 0.2;

    // Light positions and sizes
    vec2 lightPositions[numLights];
    float lightSizes[numLights];

    for (int i = 0; i < numLights; i++)
    {
        float angle = float(i) * 2.0 * 3.14159 / float(numLights);
        float radius = 0.3 + 0.2 * sin(time + angle * 3.0);

        lightPositions[i] = vec2(0.5 + radius * cos(angle), 0.5 + radius * sin(angle));
        lightSizes[i] = 1.15 + 10.1 * sin(time + angle * 2.0);
    }

    // Calculate distance and intensity for each light
    vec3 finalColor = vec3(0.0);

    for (int i = 0; i < numLights; i++)
    {
        float dist = distance(fragCoord, iResolution.xy * lightPositions[i]);

        // Calculate time-based color values for each light
        float red = 0.5 + 0.5 * sin(2.0 * 3.14159 * (time * cycleFrequency + float(i) / float(numLights)));
        float green = 0.5 + 0.5 * sin(2.0 * 3.14159 * (time * cycleFrequency + (2.0 / 3.0) + float(i) / float(numLights)));
        float blue = 0.5 + 0.5 * sin(2.0 * 3.14159 * (time * cycleFrequency + (4.0 / 3.0) + float(i) / float(numLights)));

        // Calculate light intensity based on distance (inverse square law) and light size
        float intensity = lightSizes[i] / (0.1 + 0.1 * dist * dist);

        // Combine the colors and intensities
        finalColor += vec3(red * intensity, green * intensity, blue * intensity);
    }

    // Output the final color
    fragColor = vec4(finalColor, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    float t =  -iTime * 3. + 5000. +  sin(iTime / 3.) * 5.;

    float dist = distance(uv, vec2(0., 0.)) * .3;
    float maxDist = .5;
    vec4 color;

    float expDist = dist * dist * dist;
    float strength = (sin(expDist * 100.)+1.)/2.;
    float height = (sin(t * strength)+1.)/2.;
    float alpha = 1. - expDist / (maxDist * maxDist * maxDist) + (1. - height) * -0.014  ;
    color = vec4(.9,.9,.9, 9.) * height - (1. - alpha) * 0.652;
    color.a = alpha;
    if(dist > maxDist) color = vec4(.1,.1,.1, 0.);
    fragColor = color;
}

