const vec3 sundir = vec3(-0.7071,0.0,-0.7071);

#define MARCH(STEPS,MAPLOD) for(int i=0; i<STEPS; i++) { vec3 pos = ro + t*rd; if( pos.y<-3.0 || pos.y>2.0 || sum.a>0.99 ) break; float den = MAPLOD( pos ); if( den>0.01 ) { float dif = clamp((den - MAPLOD(pos+0.3*sundir))/0.6, 0.0, 1.0 ); vec3  lin = vec3(1.0,0.6,0.3)*dif+vec3(0.91,0.98,1.05); vec4  col = vec4( mix( vec3(1.0,0.95,0.8), vec3(0.25,0.3,0.35), den ), den ); col.xyz *= lin; col.xyz = mix( col.xyz, bgcol, 1.0-exp(-0.003*t*t) ); col.w *= 0.4; col.rgb *= col.a; sum += col*(1.0-sum.a); } t += max(0.06,0.05*t); }

vec4 raymarch( in vec3 ro, in vec3 rd, in vec3 bgcol, in ivec2 px )
{    
    vec4 sum = vec4(0.0);    
    float t = 0.05*texelFetch( iChannel1, px&255, 0 ).x;    
    MARCH(40,map5);    
    MARCH(40,map4);    
    MARCH(30,map3);    
    MARCH(30,map2);    
    return clamp( sum, 0.0, 1.0 );
}

vec4 render( in vec3 ro, in vec3 rd, in ivec2 px )
{
    // background sky         
    float sun = clamp( dot(sundir,rd), 0.0, 1.0 );    
    vec3 col = vec3(0.6,0.71,0.75) - rd.y*0.2*vec3(1.0,0.5,1.0) + 0.15*0.5;    
    col += 0.2*vec3(1.0,.6,0.1)*pow( sun, 8.0 );    
    // clouds        
    vec4 res = raymarch( ro, rd, col, px );    
    col = col*(1.0-res.w) + res.xyz;        
    // sun glare        
    col += vec3(0.2,0.08,0.04)*pow( sun, 3.0 );    
    return vec4( col, 1.0 );
}

#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m =                iMouse.xy      /iResolution.xy;

    // camera
    vec3 ro = 4.0*normalize(vec3(sin(3.0*m.x), 0.8*m.y, cos(3.0*m.x))) - vec3(0.0,0.1,0.0);
	vec3 ta = vec3(0.0, -1.0, 0.0);
    mat3 ca = setCamera( ro, ta, 0.07*cos(0.25*iTime) );
    // ray
    vec3 rd = ca * normalize( vec3(p.xy,1.5));

    fragColor = render( ro, rd, ivec2(fragCoord-0.5) );
}

#define ITERS 8
#define SCALE 2.0
#define SPEED 0.25;

vec3 hsv(float h, float s, float v) {
  return mix(vec3(1.), clamp((abs(fract(h + vec3(3., 2., 1.) / 3.) * 6. - 3.) - 1.), 0., 1.), s) * v;
}

float col(vec2 p, float t) {
  float a = length(p) - p.x;
  float b = max(abs(p.x - 1.0), abs(p.y - 1.0));
  float c = max(p.x, p.y);
  float d = b * a;
  return t * 0.1 + mix(mix(a, b, sin(t * 2.31) * 0.5 + 0.5), mix(c, d, sin(t + 1.0) * 0.5 + 0.5), sin(t * 0.46) * 0.5 + 0.5);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  float aspect = iResolution.x / iResolution.y;
  vec2 p = (-1.0 + 2.0 * fragCoord.xy / iResolution.xy);
  p.x *= aspect;
  p += 1.0;
  vec3 c = vec3(1);
  float t = 5.0 + iTime * SPEED;
  for (int j = 3; j < ITERS; j++) {
    p = abs(p - 1.0);
    float h = col(p, t);
    c += hsv(sin(h * 8.0) + 0.9, 2.0, 0.30); // <-- change this line
    p *= SCALE;
  }
  fragColor = vec4(c, 200.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ix = texelFetch(iChannel1, ivec2(0,0), 0 ).x;
    int mono = int(texelFetch(iChannel1, ivec2(3,0), 0 ).x);
    int showtext = int(texelFetch(iChannel1, ivec2(4,0), 0 ).x);
    int aa = 1-int(texelFetch(iChannel1, ivec2(5,0), 0 ).x);

    float mx= iMouse.x / iResolution.x;
    vec2 mid = iResolution.xy / 2.;
    float mmul = 1.-mx;
    fragCoord = mid - (mid*mmul-fragCoord*mmul);

    vec4 val = vec4(0.);
    int am=aa, ap=aa, j=0;
    for (int j = -am; j <= ap; j++)
        for (int i = -am; i <= ap; i++) {
            vec4 v1= texelFetch( iChannel0, ivec2(int(fragCoord.x)+i,int(fragCoord.y)+j), 0 );
            vec4 v2= vec4(0.03+0.1,0.29+0.1,0.35+0.1,1.0)- texelFetch( iChannel2, ivec2(int(fragCoord.x*0.25)+i,int(fragCoord.y*0.25)+j), 0 );
            if (v1.x > 0.03+0.4  && v1.y > 0.29+0.4 && v1.z > 0.35+0.4)
                val += v1; else val+= v2*0.1+v1*0.85;
         }
    float n=float((am+ap+1));
    if (am+ap > 0) val /= n*n-3.5;

    if (aa == 0) val/=0.6;

    if (mono == 0) {
        fragColor = val;
    } else {
        float v=(1./liveval)*val.w;
        fragColor = vec4( v*0.9, v*0.95, v, 1.0 );
    }
}

