void setRules(int index, vec3 col) {
    int v1, v2;
    decimate = 1.;liveval=2.;

    rp=4.,gp=3.,bp=10., rm=2.,gm=2.,bm=8., ra=7,ga=7,ba=7,staypatt=1,stayval=1.,colch=0, clampstay=false, setmethod=0; nh=10, wrap=0;

    index = 0;
    if (index == 0) {wrap=1; nh=13; v1 = 64863, v2=21948; setmethod=0, density=14., liveval=4., decimate=1.0, colch=0, staypatt=0, rp=5.5, gp=4.8, bp=4.6, rm=45., gm=61.,bm=61.,  ra=7,ga=7,ba=7; }

    rp=rp+col.x; gp=gp+col.y; bp=bp+col.z;
