// Persistive Keyboard Input buffer & resolution change check

const int KEY_LEFT  = 37;
const int KEY_RIGHT = 39;
const int KEY_UP    = 38;
const int KEY_DOWN  = 40;
const int KEY_1     = 49;
const int KEY_2     = 50;
const int KEY_3     = 51;
const int KEY_4     = 52;
const int KEY_5     = 53;
const int KEY_7     = 55;
const int KEY_8     = 56;
const int KEY_9     = 57;
const int KEY_A     = 65;
const int KEY_C     = 67;
const int KEY_H     = 72;
const int KEY_M     = 77;
const int KEY_W     = 87;

bool readKey( int key ) {
	float keyVal = texture( iChannel1, vec2( (float(key)+.5)/256.0, .25 ) ).x;
	return (keyVal>.5)?true:false;
}

vec4 keyStep(int ix, int iy, int key, float delta, float maxval) {
    vec3 keystate = texelFetch(iChannel0, ivec2(ix,iy), 0 ).xyz;
    float kx=keystate.x;
    float ky=keystate.y;
    if( readKey(key) ) {
        if (ky == 0.) {
            kx+=1.;
            if (kx >= maxval) kx=0.;
            if (kx < 0.) kx=maxval-delta;
        }
        ky+=0.01;
        if (ky >= 1.0) ky=0.;
    }
    else {
        ky = 0.;
    }
    return vec4(kx,ky,0.,0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int ix = int(fragCoord.x), iy = int(fragCoord.y);

    if (ix == 0 && iy == 0) {
        vec3 keystate = texelFetch(iChannel0, ivec2(ix,iy), 0 ).xyz;
        float kx=keystate.x;
        float ky=keystate.y;
        if( readKey(KEY_UP)||readKey(KEY_DOWN)||readKey(KEY_LEFT)||readKey(KEY_RIGHT) ) {
            if (ky == 0.) {
                if (readKey(KEY_DOWN) || readKey(KEY_RIGHT)) {
                    kx+=0.1;
                    if (kx > LAST_PATT*0.1+0.05) kx=0.05;
                } else {
                    kx-=0.1;
                    if (kx < 0.) kx=LAST_PATT*0.1+0.05;
                }
            }
            ky+=0.01;
            if (ky >= 1.0) ky=0.;
        }
        else {
            ky = 0.;
        }
        fragColor = vec4(kx,ky,0.,0.);
        return;
    }

    if (ix == 1 && iy == 0) {
        float method = texelFetch(iChannel0, ivec2(ix,iy), 0 ).x;
        if( readKey(KEY_1)) method = 0.5;
        if( readKey(KEY_2)) method = 1.5;
        if( readKey(KEY_3)) method = 2.5;
        if( readKey(KEY_4)) method = 3.5;
        if( readKey(KEY_5)) method = 4.5;
        fragColor = vec4(method,0.,0.,0.);
        return;
    }

    if (ix == 2 && iy == 0) {
        float speed = texelFetch(iChannel0, ivec2(ix,iy), 0 ).x;
        if( readKey(KEY_7)) speed = 0.;
        if( readKey(KEY_8)) speed = 1.;
        if( readKey(KEY_9)) speed = 5.;
        fragColor = vec4(speed,0.,0.,0.);
        return;
    }

    if (ix == 3 && iy == 0) {
        fragColor = keyStep(ix, iy, KEY_M, 1., 2.);
        return;
    }

    if (ix == 4 && iy == 0) {
        fragColor = keyStep(ix, iy, KEY_H, 1., 2.);
        return;
    }

    if (ix == 5 && iy == 0) {
        fragColor = keyStep(ix, iy, KEY_A, 1., 2.);
        return;
    }

    if (ix == 6 && iy == 0) {
        fragColor = keyStep(ix, iy, KEY_C, 1., 2.);
        return;
    }

    if (ix == 7 && iy == 0) {
        fragColor = keyStep(ix, iy, KEY_W, 1., 2.);
        return;
    }


    if (ix == 0 && iy == 1) {
        fragColor = vec4(iResolution.x,0.,0.,0.);
        return;
    }

    fragColor = vec4(0.,0.,0.,1.0);
}

