    int speed = 4+int(texelFetch(iChannel2, ivec2(2,0), 0 ).x);
    int mono = int(texelFetch(iChannel2, ivec2(3,0), 0 ).x);
    int usecolor = 1-int(texelFetch(iChannel2, ivec2(6,0), 0 ).x);
    int usewrap = 1-int(texelFetch(iChannel2, ivec2(7,0), 0 ).x);

    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(-3.5*sin((iTime-10.)*0.012), 3.5*sin(iTime*0.0105), 3.5*sin(iTime*0.02));
    if (usecolor == 0) col = vec3(0.);
    col = vec3(0.);

    setRules(int(ix*10.), col);
    uv.x-=0.4;
    float axx = sin((uv.x*5.+uv.y)-0.8)*cos(uv.y*2.+2.1)*12.5+12.5;
    axx=clamp(axx, 1.0, 5.0);

    rm/=axx;
    gm/=axx;
    bm/=axx;
    bp*=0.15;
    gp*=0.15;
    rp*=0.15;

    wrap = usewrap;

    ivec2 px = ivec2( fragCoord );
    vec4 curr = cellval(px);
    float ev = curr.w;

    if (wrap == 0 && (fragCoord.y < 2. || fragCoord.x < 2.) ) {
        fragColor = vec4( 0.0, 0.0, 0.0, 0.);
        return;
    }

    if( iFrame==0 || readKey(KEY_SPACE) || readKey(KEY_UP) || readKey(KEY_DOWN) || int(xres) != int(iResolution.x)  ) {
        fragColor = vec4( 0.0, 0.0, 0.0, restart(fragCoord, setmethod, false));
        return;
    }

    if( readKey(KEY_1) || readKey(KEY_2) || readKey(KEY_3) || readKey(KEY_4) || readKey(KEY_5) ) {
        fragColor = vec4( 0.0, 0.0, 0.0, restart(fragCoord, method, false));
        return;
    }

    if (iFrame % (speed+1) > 0) {
        fragColor = curr;
        return;
    }

    int k=0;

    if (nh == 0) {
        // ..X..
        // .X.X.
