image_code:
#define MAX_DIST 10000.
#define MAX_STEPS 100
#define SURF_DIST .1
float smoothMax(float a,float b, float k)
{
    return log(exp(k*a)+exp(k*b))/k;
}
float smoothMin(float a,float b,float k)
{
    return -smoothMax(-a,-b,k);
}
float mixuj(float a,float b,float c)
{
    //return smoothMin(a,b,.1);
    //return (a+b+c)/2.;
    //return max(a,b);
    //return smoothMin(smoothMin(a,b,.1),c,.1);
    return min(min(a,b),c);
}
float GetDist(vec3 p)
{
    vec4 s1 = vec4(40.*sin(iTime),30.,0.,36.);
    vec4 s2 = vec4(-40.*sin(iTime),30.,0.,36.);

    float sphereDist1 = length(p-s1.xyz)-s1.w;
    float sphereDist2 = length(p-s2.xyz)-s2.w;
    float planeDist = p.y;
    float d = mixuj(sphereDist1,sphereDist2,planeDist);
    return d;
}
float GetDist(vec3 p,vec4 s1)
{
    return length(p-s1.xyz)-s1.w;
}
float RayMarch(vec3 ro,vec3 rd)
{
    float dist=0.0;
    for(int i=0;i<MAX_STEPS;++i)
    {
        vec3 p =ro+dist*rd;
        float cur=GetDist(p);
        dist=dist+cur;
        if(dist>MAX_DIST||cur<SURF_DIST)break;
    }
    return dist;
}
vec3 GetNormal(vec3 p)
{
    vec2 e = vec2(.01,0.);
    return normalize(vec3(GetDist(p+e.xyy),GetDist(p+e.yxy),GetDist(p+e.yyx))-vec3(GetDist(p-e.xyy),GetDist(p-e.yxy),GetDist(p-e.yyx)));
}
float GetLight(vec3 p)
{
    vec3 lightPos = vec3(0,500,500);
    vec3 normal = GetNormal(p);
    vec3 lightVec = normalize(lightPos-p);
    float dist = RayMarch(p+20.*SURF_DIST*normal,lightVec);
    if(dist<MAX_DIST)return 0.;
    return clamp(dot(lightVec,normal),0.0,1.0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 s1 = vec4(40.*sin(iTime),30.,0.,36.);
    vec4 s2 = vec4(-40.*sin(iTime),30.,0.,36.);
    //-1. to 1.
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec3 ro=vec3(0.,30.,70.);
    vec3 rd=vec3(uv.xy,-1);
    float dist=RayMarch(ro,rd);
    vec3 p = ro+dist*rd;
    vec3 col = vec3(0.0);
    //col=GetNormal(ro+dist*rd);
    //col=vec3(dist)/6.;
    float dif = GetLight(ro+dist*rd);
    col=vec3(.7,.0,.0);
    if(dist>100.)col=vec3(135, 206, 235)/255.;
    else
    {
        if(GetDist(p,s1)-RayMarch(p,rd)<0.1)col=vec3(0,1,0);
        if(GetDist(p,s2)-RayMarch(p,rd)<0.1)col=vec3(0,0,1);
        col *= dif;
        col = pow(col, vec3(1.0/2.2));
    }
    fragColor=vec4(col,1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finCol = vec3(0.0);

    for (float i = 0.0; i < 3.0; i++)
    {
        uv = fract(uv * 2.5) - 0.5;
        float d = length(uv) * exp(-length(uv0));
        vec3 col = 0.5 * cos(iTime * 0.5 + uv.xyx + vec3(0, 2, 4) + iTime * 0.5);
        d = sin(d * 2.5 + iTime * 0.5) / 5.0;
        d = pow(0.01 / d, 1.1);
        finCol += col * d;
    }

    float d2 = length(uv0);
    d2 = sin(d2 * 2.5 + iTime * 0.5) / 5.0;
    d2 = abs(d2);
    d2 = pow(0.1 / d2, 1.2);
    vec3 finCol2 = finCol * d2;

    // Set vectors to black with 10% opacity
    fragColor.rgb = vec3(0.0);
    fragColor.a = 0.1;

    if (length(finCol2) > 0.1)
    {
        // Set the background to white with 100% opacity
        fragColor.rgb = vec3(1.0);
        fragColor.a = 0.1;
    }
}

// A convenient system for easily loading and storing state variables
// across frames/buffers without manually figuring out locations to load
// and store them in the buffers. More info in Common tab.
// 
// In this example, a ball's position and velocity is tracked, with a
// mouse click changing the velocity. Colour changes every bounce/click.

uint PCGHash(uint seed) {
    uint state = seed * 747796405u + 2891336453u;
    uint word = ((state >> ((state >> 28u) + 4u)) ^ state) * 277803737u;
    return (word >> 22u) ^ word;
}

float rand(uint seed) {
    return float(PCGHash(seed)) / float(0xffffffffu);
}

float sdCircle(vec2 p, vec2 c, float r) {
    return distance(p, c) - r;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    // Load the state variables
    loadState();

    // Generate a random color based on how many times it's bounced
    vec3 ballColor = vec3(
        rand(numBounces * 3u + 0u),
        rand(numBounces * 3u + 1u),
        rand(numBounces * 3u + 2u)
    );

    // Draw the ball
    float d = sdCircle(fragCoord, position, radius);
    fragColor.rgb = mix(ballColor, vec3(1.0), clamp(d + 0.5, 0.0, 1.0));
    fragColor.a = 1.0;
}

