// Usage:
// Add to the STATE #define below to declare a new state variable. State
// variables are automatically loaded and stored across buffers/frames.
// Simply call loadState() at the beginning of mainImage() to make the
// state variables available for use as global variables, and call
// storeState() at the end of mainImage() to save any modifications made
// to the state variables*. Modifications to state variables must be
// uniform across all shader invocations. loadState() loads state
// variables from the texture set as iChannel0. storeState() stores
// state variables in the alpha channel of the current buffer.
//
// *Modifications to state variables are not stored if they are done
// inside of the Image buffer.
//
// All types are loaded and stored by casting from and to floats, so the
// maximum safe integer is 2^24-1.

#define STATE        \
    UINT(numBounces) \
    VEC2(position)   \
    VEC2(velocity)   \
    FLOAT(radius)    \

// Declare the global variables
#define BOOL(name) bool name;
#define BVEC2(name) bvec2 name;
#define BVEC3(name) bvec3 name;
#define BVEC4(name) bvec4 name;
#define INT(name) int name;
#define IVEC2(name) ivec2 name;
#define IVEC3(name) ivec3 name;
#define IVEC4(name) ivec4 name;
#define UINT(name) uint name;
#define UVEC2(name) uvec2 name;
#define UVEC3(name) uvec3 name;
#define UVEC4(name) uvec4 name;
#define FLOAT(name) float name;
#define VEC2(name) vec2 name;
#define VEC3(name) vec3 name;
#define VEC4(name) vec4 name;
STATE
#undef BOOL
#undef BVEC2
#undef BVEC3
#undef BVEC4
#undef INT
#undef IVEC2
#undef IVEC3
#undef IVEC4
#undef UINT
#undef UVEC2
#undef UVEC3
#undef UVEC4
#undef FLOAT
#undef VEC2
#undef VEC3
#undef VEC4

#ifndef HW_PERFORMANCE
uniform vec3 iResolution;
uniform sampler2D iChannel0;
#endif

// Set the global variables to their appropriate values
void loadState() {
    int i = 0;
    int w = textureSize(iChannel0, 0).x;
    #define GET(i) texelFetch(iChannel0, ivec2(i % w, i / w), 0).a
    #define BOOL(name) name = bool(GET(i)); i += 1;
    #define BVEC2(name) BOOL(name.x) BOOL(name.y)
    #define BVEC3(name) BVEC2(name.xy) BOOL(name.z)
    #define BVEC4(name) BVEC3(name.xyz) BOOL(name.w)
    #define INT(name) name = int(GET(i)); i += 1;
    #define IVEC2(name) INT(name.x) INT(name.y)
    #define IVEC3(name) IVEC2(name.xy) INT(name.z)
    #define IVEC4(name) IVEC3(name.xyz) INT(name.w)
    #define UINT(name) name = uint(GET(i)); i += 1;
    #define UVEC2(name) UINT(name.x) UINT(name.y)
    #define UVEC3(name) UVEC2(name.xy) UINT(name.z)
    #define UVEC4(name) UVEC3(name.xyz) UINT(name.w)
    #define FLOAT(name) name = GET(i); i += 1;
    #define VEC2(name) FLOAT(name.x) FLOAT(name.y)
    #define VEC3(name) VEC2(name.xy) FLOAT(name.z)
    #define VEC4(name) VEC3(name.xyz) FLOAT(name.w)
    STATE
    #undef BOOL
    #undef BVEC2
    #undef BVEC3
    #undef BVEC4
    #undef INT
    #undef IVEC2
    #undef IVEC3
    #undef IVEC4
    #undef UINT
    #undef UVEC2
    #undef UVEC3
    #undef UVEC4
    #undef FLOAT
    #undef VEC2
    #undef VEC3
    #undef VEC4
    #undef GET
}

// Store the state variables in fragColor.a
void storeState(inout vec4 fragColor, vec2 fragCoord) {
    int i = int(fragCoord.x) + int(fragCoord.y) * int(iResolution.x);
    int j = 0;
    #define SET(j, v) if (i == j) fragColor.a = float(v)
    #define BOOL(name) SET(j, name); j += 1;
    #define BVEC2(name) BOOL(name.x) BOOL(name.y)
    #define BVEC3(name) BVEC2(name.xy) BOOL(name.z)
    #define BVEC4(name) BVEC3(name.xyz) BOOL(name.w)
    #define INT(name) SET(j, name); j += 1;
    #define IVEC2(name) INT(name.x) INT(name.y)
    #define IVEC3(name) IVEC2(name.xy) INT(name.z)
    #define IVEC4(name) IVEC3(name.xyz) INT(name.w)
    #define UINT(name) SET(j, name); j += 1;
    #define UVEC2(name) UINT(name.x) UINT(name.y)
    #define UVEC3(name) UVEC2(name.xy) UINT(name.z)
    #define UVEC4(name) UVEC3(name.xyz) UINT(name.w)
    #define FLOAT(name) SET(j, name); j += 1;
    #define VEC2(name) FLOAT(name.x) FLOAT(name.y)
    #define VEC3(name) VEC2(name.xy) FLOAT(name.z)
    #define VEC4(name) VEC3(name.xyz) FLOAT(name.w)
    STATE
    #undef BOOL
    #undef BVEC2
    #undef BVEC3
    #undef BVEC4
    #undef INT
    #undef IVEC2
    #undef IVEC3
    #undef IVEC4
    #undef UINT
    #undef UVEC2
    #undef UVEC3
    #undef UVEC4
    #undef FLOAT
    #undef VEC2
    #undef VEC3
    #undef VEC4
    #undef SET
}

