// This buffer simulates the ball

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    // Load state variables
    loadState();

    // Initialize state variables on the first frame
    if (iFrame == 0) {
        numBounces = 0u;
        position = 0.5 * iResolution.xy;
        velocity = vec2(0.0);
        radius = 0.1 * iResolution.y;
    }

    // Change ball velocity if mouse was clicked
    if (iMouse.w > 0.0) {
        ++numBounces;
        velocity = 4.0 * (iMouse.xy + 0.5 - position);
    }

    // Apply gravity and update velocity and position
    vec2 gravity = vec2(0.0, -4.0 * iResolution.y);
    velocity += gravity * iTimeDelta;
    position += velocity * iTimeDelta;

    // Collide ball with wall
    vec2 clamped = clamp(
        position,
        vec2(radius),
        iResolution.xy - radius
    );

    if (position != clamped) {
        ++numBounces;
        velocity *= mix(
            vec2(-0.9),
            vec2(1.0),
            equal(position, clamped)
        );
        position = clamped;
    }

    // Store state variables
    storeState(fragColor, fragCoord);
}

