vec3 drawBg(vec2 V) {
    V += iTime;
    return mix(vec3(99, 155, 255),
               vec3(91, 110, 225),
               mod(floor(V.x/12.)+floor(V.y/12.), 2.)) / 255.;
}

vec3 showMap(vec2 V) {
    float t = cell(ch0, V + vec2(0, 1)).r;
    float b = cell(ch0, V - vec2(0, 1)).r;
    float l = cell(ch0, V - vec2(1, 0)).r;
    float r = cell(ch0, V + vec2(1, 0)).r;
    float c = cell(ch0, V).r;

    float sum = t + b + l + r;

    if (c == 1.) {
        if (t == 0.) {
            if (l + r == 2.) return vec3(1);
            else if (l + r == 1.) return vec3(.85);
            else return vec3(.7);
        }
        return vec3(.5);
    } 
    else if (sum > 0.) return .8 * drawBg(V);
    else return drawBg(V);
}

void mainImage( out vec4 O, vec2 I )
{
    vec2 U = (I-.5*R)/R.y;

    vec4 player   = cell(ch1, cPlayer);
    vec4 follower = cell(ch1, cFollower);
    vec4 space    = cell(ch1, cSpace);

    // Zoom
    float zm = .06;

    // uv moves with player position so player is centered
    vec2 V = vec2(zm * (I-.5*R) + player.xy - .5);

    // Player
    float dPlay = max(abs(U.x), abs(U.y));
    float sPlay = step(dPlay, .5/R.y/zm);
    float sPlay2 = step(dPlay, 10.5/R.y/zm);

    // Follower
    vec2 vf = V - follower.xy;
    float dFol = max(abs(vf.x), abs(vf.y));
    float sFol = step(dFol, .25);

    // Spacebar effect
    float dSpace = length(V - player.xy + .5 + 4. * player.zw);
    float rSpace = space.x / 12.;
    float sSpace = step(abs(dSpace - 4. * rSpace * rSpace), .5 * rSpace);

    // Trail
    float trail = cell(ch0, V).g;
    vec3 trailCol = pal(.5 * trail + .4 + .25 * vec3(0,1,2)/3.);
    vec3 playerCol = mix(vec3(1), vec3(1,0,0), exp(-2.*length(player.zw)));

    // Colors
    vec3 col = showMap(V);
    col = mix(col, vec3(1), .7 * trail);
    col = mix(col, vec3(1), sSpace * rSpace);  
    //col = mix(0.5*col, col, sPlay2); // Cute mode
    col = mix(col, vec3(1.-exp(-8.*length(player.zw))), sPlay);
    col = mix(col, vec3(1), sFol);
    O = vec4(col, 1);
}

