#define pi 3.14159
#define R iResolution.xy

#define pal(a) .5 + .5 * cos(2. * pi * (a))

#define ch0 iChannel0
#define ch1 iChannel1
#define ch2 iChannel2

#define KEY_LEFT 65.
#define KEY_RIGHT 68.
#define KEY_UP 87.
#define KEY_DOWN 83.
#define KEY_SPACE 32.

#define Dir(a) vec2(cos(a),sin(a))

#define cell(ch, J) texelFetch(ch, ivec2(J), 0)
#define key_val(key) texelFetch(ch2, ivec2(key, 0), 0).x
//#define key_down(key) key_val(key) == 1.
#define key_pressed(key) texelFetch(ch2, ivec2(key, 1), 0).x

// Object cells
#define cPlayer   vec2(.5)
#define cFollower vec2(1.5, .5)
#define cSpace    vec2(2.5, .5)


// From iq: https://www.shadertoy.com/view/Msf3WH
vec2 hash( vec2 p ) // replace this by something better
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot( n, vec3(70.0) );
}

