// this buffer stores object data
// (player, follower, spacebar)

#define is I == 

void mainImage( out vec4 O, vec2 I )
{
    // Initialize values
    if (iFrame < 1) {
        if (is cPlayer || is cFollower)
            O = vec4(.5 * R, 0, 0);
        else if (is cSpace)
            O = vec4(0);
        return;
    }

    // Previous values
    O = cell(ch1, I);

    // Player (position, velocity)
    if (is cPlayer) {
        // Update velocity 
        O.z += .05 * (key_val(KEY_RIGHT) - key_val(KEY_LEFT));
        O.w += key_pressed(KEY_SPACE) -.03;
        O.zw *= vec2(.9, 1);

        // Update position
        O.xy += O.zw;

        vec2 f = floor(O.xy);

        // Player position is always within 2x2 block of cells
        // v = (TL, TR, 
        //      BL, BR)
        // 1 if cell.r == 1., 0 otherwise
        vec4 v = vec4(cell(ch0, f + vec2(-.5,  .5)).r == 1.,
                      cell(ch0, f + vec2( .5,  .5)).r == 1.,
                      cell(ch0, f + vec2(-.5, -.5)).r == 1.,
                      cell(ch0, f + vec2( .5, -.5)).r == 1.); 

        float sum = v.x + v.y + v.z + v.w;

        // Copy O to compare before and after collision resolution
        vec4 C = O;       

        // Stuck inside region, move up
        if (v == vec4(1)) O.y++;  

        // Push away from walls
        else if (sum >= 2.) {
                 if (v.xy == vec2(1)) O.y = f.y;      // Top 2 cells
            else if (v.zw == vec2(1)) O.y = f.y + 1.; // Bottom
                 if (v.yw == vec2(1)) O.x = f.x;      // Right
            else if (v.xz == vec2(1)) O.x = f.x + 1.; // Left
        }

        // If resolved in x or y, set x or y velocity = 0  
        O.zw *= vec2(C.x == O.x, C.y == O.y);
    }

    // Follower (position, velocity)
    else if (is cFollower) {
        vec4 player = cell(ch1, vec2(.5));
        O.xy = mix(O.xy, player.xy - .5 + 2. * Dir(pi/2.-cos(iTime*3.)), .1);
        O.xy += O.zw;
    }

    // Spacebar (effect cooldown)
    else if (is cSpace) {
        O.x = max(0., O.x - 1.);
        if (key_pressed(KEY_SPACE) == 1.)
            O.x = 12.;
    }
}

image_code:
float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

float noise (vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
           (c - a) * u.y * (1.0 - u.x) +
           (d - b) * u.x * u.y;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float time = iTime * 0.1;
    float speed = 5.0;
    float waveHeight = 0.1;
    float frequency = 3.0;

    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    uv.y += sin(uv.x * frequency + time * speed) * waveHeight;

    float noiseValue = (noise(uv * 8.0) + 1.0) * 0.5;

    vec3 color = vec3(0.0, 0.2, 0.5) + vec3(noiseValue * 0.1, noiseValue * 0.2, noiseValue * 0.4);

    float foam = pow(1.0 - abs(uv.y), 6.0);
    color += vec3(1.0) * foam * 0.2;

    fragColor = vec4(color, 1.0);
}

// Elements are columns
// Modified matrix from the "JPEG conversion" section of
// https://en.wikipedia.org/wiki/YCbCr
mat3 ycbcr_to_rgb = mat3(vec3(1.), vec3(0., -.344136, 1.772), vec3(1.402, -.714136, 0.));
// I'm not worrying about 128/255 vs. .5 here.
vec3 ycbcr_offset_to_rgb = vec3(0., -.5, -.5);

bool show_effect(int effect, vec2 uv){
#if EFFECT == E_MULTI
    return float(effect)/4. <= uv.x && uv.x <= float(effect+1)/4.;
#else
    return (effect == EFFECT);
#endif
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 effect_col;
    vec2 src_uv = fragCoord/iResolution.xy;

if(show_effect(E_PIXELATE, src_uv)){
    // Read the depth from the original render
    // We read from mip 3, without linear filtering,
    // to try to get a constant value in each 8x8 block.
    // Otherwise, our pixels have unusual shapes.
    // uh, don't look at the top row of pixels too closely
    // if your preview height is not divisible by 8
    float depth = texelFetch(iChannel0, ivec2(fragCoord)/8, 3).a;
    float cc = EFFECT_STRENGTH * 60.f * cc_from_depth(depth);

#if 1
    // Optional: If cc is restricted to powers of integers,
    // then we never run into the unusual shape issue!
    // But then depths look kind of quantized.
    if(cc > 0.){
        cc = exp2(round(log2(cc)));
    }
#endif

    // Pixelate fragCoord
    fragCoord = round(fragCoord/cc)*cc;
    vec2 pixelated_uv = fragCoord/iResolution.xy;

    // Output to screen
    effect_col = texture(iChannel0, pixelated_uv).rgb;

}else if(show_effect(E_JPEG, src_uv)){

    // Split the image into 8x1 blocks. What's my x index inside the block?
    int block_x = int(fragCoord.x) % 8;
    // Go to the start of the row in this 8x8 block
    ivec2 block_start = ivec2(fragCoord) - ivec2(block_x, 0);
    // Fetch 8 pixels in this row
    vec3 row[8];
    for(int i = 0; i < 8; i++){
        row[i] = texelFetch(iChannel1, block_start + ivec2(i, 0), 0).rgb;
    }
    // Invert the horizontal DCT. This is the same trick
    // as in Buffer D!
    vec3 dct_out = vec3(0.);
    for(int i = 0; i < 8; i++){
        dct_out += dct[8*i + block_x] * row[i];
    }

    effect_col = ycbcr_to_rgb * (dct_out + ycbcr_offset_to_rgb);

}else if(show_effect(E_CHROMATIC, src_uv)){

    // Some sort of chromatic aberration
    float depth = texelFetch(iChannel0, ivec2(fragCoord), 0).a;
    float cc = EFFECT_STRENGTH * 200.f * cc_from_depth(depth);
    vec2 rcp_res = 1. / iResolution.xy;
    effect_col = vec3(0.);
    vec3 total_weight = vec3(0.);
    float d_dx = .25;
    // If our step size is larger than a pixel, we sample
    // higher resolutions:
    float sampled_lod = log2(max(1., d_dx*cc));
    for(float dx = -1.; dx <= 1.; dx += d_dx){
        vec3 val = textureLod(iChannel0, (fragCoord + vec2(dx*cc, 0.))*rcp_res, sampled_lod).rgb;
        // sRGB -> linear
        val = pow(max(val, vec3(0.)), vec3(2.2f));
        // r  g  b
        // \ /^\ /
        //  \   \
        // / \ / \
        //-1  0  1
        vec3 weight = clamp(vec3(-dx, 1.-abs(dx), dx), vec3(0.), vec3(1.));
        effect_col += val*weight;
        total_weight += weight;
    }
    effect_col = effect_col / total_weight;
    // linear -> sRGB
    effect_col = pow(effect_col, vec3(1./2.2f));

}else if(show_effect(E_FLOW, src_uv)){

    // er, sample along a curve, I guess?
    vec2 uv = src_uv;
    float depth = texelFetch(iChannel0, ivec2(fragCoord), 0).a;
    // Total length, roughly in pixels
    float cc = EFFECT_STRENGTH * 250.f * cc_from_depth(depth);
    int num_steps = clamp(int(ceil(cc/4.)), 1, 16);
    float step_size = (cc/256.)/float(num_steps); // Now to UV
    effect_col = vec3(0.);
    for(int i = 0; i < num_steps; i++){
        effect_col += textureLod(iChannel0, uv, 0.f).rgb;
        uv += (textureLod(iChannel2, uv, 0.).rg - vec2(.5)) * step_size;
    }
    effect_col = effect_col / float(num_steps);

}else if(show_effect(E_CHEAP_DOF, src_uv)){

    float depth = texelFetch(iChannel0, ivec2(fragCoord), 0).a;
    float cc = EFFECT_STRENGTH * iResolution.x * .15f * cc_from_depth(depth);
    float sample_lod = log2(max(1., cc)) - .7;
    effect_col = vec3(0.);
    for(int i = 0; i < 3; i++){
        float t = float(i) * 6.28 / 3.;
        vec2 dp = .5 * (cc/iResolution.x) * vec2(cos(t), sin(t));
        vec3 val = textureLod(iChannel0, src_uv + dp, sample_lod).rgb;
        effect_col += pow(val, vec3(2.2));
    }
    effect_col = pow(effect_col / 3., vec3(1./2.2));

}

#ifdef VIEW_ERROR
    effect_col = vec3(.5) + effect_col - texelFetch(iChannel0, ivec2(fragCoord), 0).rgb;
#endif

#if EFFECT == E_MULTI
    // Draw labels at the top for each column
    int chars[] = int[](80, 105, 120, 101, 108, 97, 116, 101, 74, 80, 69, 71, 67, 104, 114, 
111, 109, 97, 116, 105, 99, 70, 108, 111, 119);
    int string_starts[] = int[](0, 8, 12, 21, 25);
    vec2 char_size_uv = 0.03 * vec2(.6, 1.) * vec2(1.,iResolution.x/iResolution.y);
    float v_from_top = 1.-src_uv.y - .01;
    if(v_from_top < char_size_uv.y && v_from_top > 0.) {
        int str_idx = clamp(int(floor(src_uv.x * 4.)), 0, 3);
        int str_len = string_starts[str_idx + 1] - string_starts[str_idx];
        float str_left_u = (float(str_idx) + .5)/4. - float(str_len) * char_size_uv.x * .5;
        float char_idx_f = (src_uv.x - str_left_u)/char_size_uv.x;
        int char_idx_i = int(floor(char_idx_f));
        if(0 <= char_idx_i && char_idx_i < str_len) {
            vec2 uv_in_letter = vec2(char_idx_f - float(char_idx_i), v_from_top/char_size_uv.y);
            // Scale letters horizontally so they're more closely spaced
            uv_in_letter.x = (uv_in_letter.x - .5) / 2. + .5;
            int char_code = chars[string_starts[str_idx] + char_idx_i];
            vec2 letter_in_grid = vec2(float(char_code % 16), float(char_code / 16));
            float letter_sdf = texture(iChannel3, (uv_in_letter + letter_in_grid)/16.).a;
            // Ad-hoc antialiasing
            float step_width = 30. / iResolution.x;
            effect_col = mix(
                effect_col,
                vec3(smoothstep(.5+step_width, .5-step_width, letter_sdf)),
                vec3(smoothstep(.55+step_width, .55-step_width, letter_sdf)));
        }
    }
    // effect_col = texture(iChannel3, src_uv).rgb;
#endif

    fragColor = vec4(effect_col, 1.);
}

