// Whack-A-Mole Pistons by Blackle Mori (@blackle):
// https://www.shadertoy.com/view/WtXcWB
// Modified by @nbickford with modifications
// (textured background, reflections, depth in alpha channel)
// also released under CC0 1.0 Universal.
//
//CC0 1.0 Universal https://creativecommons.org/publicdomain/zero/1.0/
//To the extent possible under law, Blackle Mori has waived all copyright and related or neighboring rights to this work.

//uncomment for slow but pretty version
//#define PRETTY
#ifdef PRETTY
#define AA_SAMPLES 8
#define MOTION_BLUR
#else
#define AA_SAMPLES 1
#endif

//the following functions assume that p is inside the cube of radius 1 centered at the origin
//closest vertex of the cube to p
vec3 vertex(vec3 p) {
    return step(0.,p)*2.-1.;
}
//closest face of the cube to p
vec3 face(vec3 p) {
    vec3 ap = abs(p);
    if (ap.x>=max(ap.z,ap.y)) return vec3(sign(p.x),0.,0.);
    if (ap.y>=max(ap.z,ap.x)) return vec3(0.,sign(p.y),0.);
    if (ap.z>=max(ap.x,ap.y)) return vec3(0.,0.,sign(p.z));
    return vec3(0);
}
//closest edge of the cube to p
vec3 edge(vec3 p) {
    vec3 mask = vec3(1)-abs(face(p));
    vec3 v = vertex(p);
    vec3 a = v*mask.zxy, b = v*mask.yzx;
    return distance(p,a)<distance(p,b)?a:b;
}

float super(vec2 p) {
    return sqrt(length(p*p));
}

float corner(vec2 p, float h) {
    vec2 q = p - vec2(0,h);
    return super(max(q,0.)) + min(0.,max(q.x,q.y));
}

//returns rhombic dodecahedron tessalation data for p
//x: distance to circle of radius .6 in current cell
//y: distance to circle of radius .6 in closest adjacent cell
//zw: ID of cell
vec4 grid(vec3 p) {
    vec3 id = floor(p)+.5;
    vec3 m = sign(mod(id,2.)-1.);
    if (m.x*m.y*m.z<0.) id += face(p-id);
    p -= id;
    float d1 = length(p)-.6;
    p -= edge(p);
    float d2 = length(p)-.6;
    return vec4(d1,d2,id);
}

#define FBI floatBitsToInt
float hash(float a, float b) {
    int x = FBI(cos(a))^FBI(a);
    int y = FBI(cos(b))^FBI(b);
    return float((x*x+y)*(y*y-x)+x)/2.14e9;
}

//springy impulse
float spring(float x) {
    return smoothstep(-.4,.4,x) + smoothstep(-.3,.3,x) - smoothstep(-.7,.7,x);
}

float smin(float a, float b, float k) {
    float h = max(0.,k-abs(b-a))/k;
    return min(a,b) - h*h*h*k/6.;
}

vec3 smin(vec3 a, vec3 b, float k) {
    vec3 h = max(vec3(0),k-abs(b-a))/k;
    return min(a,b) - h*h*h*k/6.;
}

vec3 erot(vec3 p, vec3 ax, float ro) {
    return mix(dot(p,ax)*ax,p,cos(ro))+sin(ro)*cross(ax,p);
}

//mtime set by "pixel_color" to influence the time used by the scene
float mtime;
//lots of globals set by "scene"
vec2 gid;
vec3 glocal;
float gnd;
float gt;
float scene(vec3 p) {
    //ds1 chooses z coordinate in 2d slicing of the rhombic dodecahedron tessalation
    //by varying it over space, we get different sized circles
    float ds1 = dot(cos(p.xy/5.), sin(p.xy/4.))*.06;
    vec3 p3 = vec3(p.xy, ds1);
    vec4 g = grid(p3);
    gid = g.zw;

    float s1 = hash(gid.x,gid.y);
    float s2 = hash(s1,s1);
    gt = sin(s1*100.+mtime*mix(1.,2.,s2*.5+.5))-.4;
    float h = spring(gt)*2.-.5;

    vec2 crd = vec2(g.x,p.z);
    vec2 crd2 = vec2(g.y,p.z);
    float maxheight = 1.7;

    gnd = corner(crd*vec2(-1,1)+vec2(0.08,0.),0.)-.04; //ground holes

    //transform things into local coordinates for piston
    crd.y -= h;
    glocal = p - vec3(gid,h);
    glocal = erot(glocal,vec3(0,0,1),s1*100.+gt*2.);
    float curr = corner(crd, 0.); //distance to current piston

    //little holes on side of piston
    vec3 lp = glocal;
    lp.z = asin(sin(lp.z*5.+.5))/5.;
    curr = -smin(-curr, length(lp.yz)-0.05,.03);

    float adjacent = corner(crd2, maxheight); //distance to adjacent piston (assumes maximally extended)
    return min(gnd,min(curr, adjacent)-.02);
}

vec3 norm(vec3 p) {
    mat3 k = mat3(p,p,p)-mat3(0.01);
    return normalize(scene(p) - vec3(scene(k[0]),scene(k[1]),scene(k[2])));
}

vec3 skylight(vec3 p) {
    return texture(iChannel2, p).rgb;
    //float d = dot(p,normalize(vec3(1)));
    //return vec3(1)*d*.2+.2 + pow(max(0.,d),10.)*1.5;
}

float smpl(vec3 p, vec3 dir, float dist) {
    return smoothstep(-dist,dist,scene(p+dir*dist));
}

vec4 pixel_color(vec2 uv, float time)
{
    vec2 mouse = (iMouse.xy-0.5*iResolution.xy)/iResolution.y;
    mtime = time;
    vec3 cam = normalize(vec3(1.5,uv));
    vec3 init = vec3(-7,0,0);

    float yrot = 0.7+sin(time*.3)*.2;
    float zrot = time*.2;
    if (iMouse.z > 0.) {
        yrot = clamp(1.-4.*mouse.y,-0.,3.14/2.);
        zrot = 4.*mouse.x;
    }

    cam = erot(cam,vec3(0,1,0),yrot);
    init = erot(init,vec3(0,1,0),yrot);
    cam = erot(cam,vec3(0,0,1),zrot);
    init = erot(init,vec3(0,0,1),zrot);

    init.xy += time*vec2(.5,sqrt(2.));
    init.z += 2.;
    vec3 p =init;
    bool hit = false;
    float dist; int i;
    float total_dist = 0.f;
    for (i = 0; i < 200 && !hit; i++) {
        dist = scene(p);
        hit = dist*dist < 1e-6;
        p += dist*cam;
        total_dist += dist;
        if(distance(p,init)>50.)break;
    }
    //save globals locally
    bool g = gnd == dist;
    vec2 id = gid;
    float s1 = hash(gid.y,gid.x);
    float s2 = hash(s1,gid.x);
    vec3 local = g ? p : glocal+vec3(id,0);

    float fog = min(1.,smoothstep(5.,50.,distance(p,init))+smoothstep(100.,200.,float(i)));
    vec3 n = norm(p);
    vec3 r = reflect(cam,n);
    float ao = smpl(p,n,.1);

    //brushed metal tops. not sure if this is the right way, but it works!
    if (!g && n.z>.9) {
        float ang = atan(p.x-id.x,p.y-id.y);
        float ang2 = atan(local.x-id.x,local.y-id.y);
        local = vec3(ang2/2.,length(p.xy-id)*40.,local.z+id.x*.9+id.y*.4);
        n = normalize(vec3(cos(ang*2.),sin(ang*2.),1));
    }

    //rough texture
    float sharpness = texture(iChannel0,local/2.).x;
    sharpness = sqrt(texture(iChannel0,local*vec3(1,4,.5)+sharpness*.1).x);
    sharpness *= pow(texture(iChannel0,local/10.+sharpness*.1).x, 2.);
    sharpness = sharpness*.5+.9;

    //fake reflection occlusion
    float ro = sqrt(smpl(p,r,.9)*smpl(p,r,.5)*smpl(p,r,.2));

    float spec = length(sin(r*3.*sharpness)*.4+.6)/sqrt(3.) * smoothstep(-1.,-.0,p.z);
    float fres = 1.-abs(dot(cam,n))*.5;
    vec3 mcol = abs(erot(vec3(0.4,0.6,0.9), normalize(vec3(0,s2,2)), s1*.6));
    if (g) mcol = vec3(0.1);

    // Not reflection, just sampling the normal :shrug:
    vec3 lightDir = reflect(cam, n);
    vec3 light = texture(iChannel3, lightDir.xzy).rgb;
    vec3 col = (mcol*spec + pow(spec,10.*sharpness))*ro*ao*fres*1.5*light;
    vec3 bgcol = skylight(cam.xzy);
    vec3 fragColor = hit ? mix(col,bgcol,fog) : bgcol;
    return vec4(fragColor, total_dist);
}

vec2 weyl_2d(int n) {
    return fract(vec2(n*12664745, n*9560333)/exp2(24.));
}

float bayer(ivec2 uv) {
    return texelFetch(iChannel1, uv % 8, 0).x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    fragColor = vec4(0);
    float b = bayer(ivec2(fragCoord));
    for (int i = 0; i < AA_SAMPLES+int(min(0,iFrame)); i++) {
        vec2 uv2 = uv + weyl_2d(i)/iResolution.y*1.25;
#ifdef MOTION_BLUR
		//using yx's bayer motion blur idea https://www.shadertoy.com/view/wsfcWX
        float blur = ((float(i)+b)/float(AA_SAMPLES)-.5) * iTimeDelta;
#else
        float blur = 0.;
#endif
        fragColor += pixel_color(uv2, iTime+blur);
    }
	fragColor.xyz = sqrt(fragColor.xyz/float(AA_SAMPLES));

    fragColor.xyz = smin(fragColor.xyz,vec3(1),0.1);
}

common_code:
// "Depth of Field, but instead of getting blurry it gets more JPEG",
// but simulating the YCoCg + Discrete Cosine Transform quantization
// modules of JPEG directly instead of visually.
// Plus depth-of-field as pixelation, chromatic aberration, and
// paint splotches/flow effects inspired by Jam2go's video!
//
// Scene:
// Whack-A-Mole Pistons by Blackle Mori (@blackle):
// https://www.shadertoy.com/view/WtXcWB
//CC0 1.0 Universal https://creativecommons.org/publicdomain/zero/1.0/
//To the extent possible under law, Blackle Mori has waived all copyright and related or neighboring rights to this work.
// Modified by @nbickford with modifications
// (textured background, reflections, depth in alpha channel)
// also released under CC0 1.0 Universal.

//------------------------------------------------------------------------
// Options (you can change these)!

#define E_PIXELATE 0
#define E_JPEG 1
#define E_CHROMATIC 2
#define E_FLOW 3
#define E_CHEAP_DOF 4
#define E_MULTI -1

// Set this to change the effect.
#define EFFECT E_MULTI

// Set this to a different value to modify the strength of all the effects.
#define EFFECT_STRENGTH 1.9

// Define this to view the compression error between
// the uncompressed image and the output.
// #define VIEW_ERROR
//------------------------------------------------------------------------

// Here's roughly how the JPEG compression code is structured!
//
// Buffer A is the RGB + depth of blackle's Whack-A-Mole Pistons scene.
// I've modified it so the background and lighting uses an environment map,
// so if you move the camera the background has some additional detail.
//
// Buffers B, C, D, and Image apply different stages in JPEG compression
// and decompression. They each load 8 pixels and output 1 pixel.*
// 
// * B transforms RGB to YCoCg, emulates downsampling the CoCg channels
// by a factor of 2 on the x axis**, and performs the horizontal component
// of the Discrete Cosine Transform.
// * C emulates downsampling the CoCg channels by a factor of 2 on the y
// axis** (because the DCT is linear, the order of the horizontal DCT and
// vertical downsample doesn't matter), and applies the vertical component
// of the DCT. Then it looks up the depth from Buffer A and quantizes the
// DCT coefficients depending on how large that point's circle of confusion
// would be if this was a real image.
// (If this was a full JPEG implementation, there would be an entropy
// coding stage here - but that should*** be a lossless stage, and so this
// shader doesn't simulate it)
// * D inverts the vertical component of the DCT.
// * Image inverts the horizontal component of the DCT.

// Footnotes:
// * In an ideal world, we could combine them together with Buffer A into
// one shader by using a compute shader with an 8x8 workgroup size and use
// warp operations to perform the DCT matrix multiplies. Then we'd never
// have to read and write intermediate results out to textures, and the
// amount of duplicated work (e.g. buffer B transforms each pixel color
// from RGB to YCoCg 8 times) would be significantly reduced.
// The 4-pass approach here might not be faster than a single-pass approach
// that loads 64 values and has each thread perform a full DCT and IDCT!
// After all, 4 passes result in 32 total texture loads, vs. 64 texture
// loads for a single pass. (Maybe the 4-pass approach uses fewer registers,
// though.)
//
// ** We could do this more accurately by actually downsampling the Co
// and Cg components of the image and performing the DCT on them
// separately. That's kind of difficult, though (although I think it's
// possible in 4 buffers), and the CoCg 4:2:0 chroma subsampling
// isn't as visible as the overwhelming crunchiness of DCT quantization.
//
// *** okay so this isn't true if you have a JPEG encoder that performs
// Rate-Distortion Optimization or, more generally, modifies the
// compressed data in some way so that the entropy coding stage produces
// a smaller result but I'm not going to worry about that here, sorry

// The other effects all take place entirely within the Image shader.
// They're not really trying to be anything specific. But they're fun!

// Related work:
// Ompuco has a JPEG emulation asset pack for Unity with more quality options:
// https://ompuco.itch.io/jpgpu
// My favorite article on the JPEG file format: https://parametric.press/issue-01/unraveling-the-jpeg/
// Other JPEG simulators on Shadertoy:
// https://www.shadertoy.com/view/XtffDj (by Ultraviolet)
// https://www.shadertoy.com/view/llfyz4 (by rory618; uses a single-pass approach)
// https://www.shadertoy.com/view/ldyBRc (by rkibria; a DCT-compressed image)
// https://www.shadertoy.com/view/Dd3Xz4 (by jolle; a DCT-compressed heightmap)
// https://www.shadertoy.com/view/msdSz7 (by jolle; Dd3Xz4 + Daala deblocking)
// Hannah Rose's "pro-aliasing" concept, which takes geometric edges and
// makes them more jagged: https://twitter.com/hannaesthetic/status/1349776649802424321

// Compute circle of confusion radius; based on
// https://en.wikipedia.org/wiki/Circle_of_confusion#Determining_a_circle_of_confusion_diameter_from_the_object_field
// but replacing units and a division by 2 with ad-hoc figures.
// Multiply this by something that looks good
float cc_from_depth(float d){
    const float focus_depth = 7.f;
    const float f = .7f;
    return abs(d-focus_depth) * f /(focus_depth * abs(d - f));
}

// Table for discrete cosine transform; generated in Mathematica using
// 1/2. Table[If[u==0,1/Sqrt[2],1]Cos[(2x+1)u Pi/16],{u,0,7},{x,0,7}]//Flatten
// Picture this as an 8x8 matrix A; A*v is the DCT of v.
// This matrix has some neat properties!
// Its transpose is its inverse: A * A^t = I
// The vertical DCT is its transpose.
// An 8x8 DCT is just the horizontal DCT (apply this to each row), followed
// by the vertical DCT (apply this to each column).
float dct[64] = float[64](
0.353553,0.353553,0.353553,0.353553,0.353553,0.353553,0.353553,0.353553,
0.490393,0.415735,0.277785,0.0975452,-0.0975452,-0.277785,-0.415735,-0.490393,
0.46194,0.191342,-0.191342,-0.46194,-0.46194,-0.191342,0.191342,0.46194,
0.415735,-0.0975452,-0.490393,-0.277785,0.277785,0.490393,0.0975452,-0.415735,
0.353553,-0.353553,-0.353553,0.353553,0.353553,-0.353553,-0.353553,0.353553,
0.277785,-0.490393,0.0975452,0.415735,-0.415735,-0.0975452,0.490393,-0.277785,
0.191342,-0.46194,0.46194,-0.191342,-0.191342,0.46194,-0.46194,0.191342,
0.0975452,-0.277785,0.415735,-0.490393,0.490393,-0.415735,0.277785,-0.0975452
);

buffer_b_code:
// RGB -> YPbPr + horizontal DCT

// Elements are columns
// Modified matrix from the "JPEG conversion" section of
// https://en.wikipedia.org/wiki/YCbCr
mat3 rgb_to_ycbcr = mat3(vec3(.299, -.168736, .5), vec3(.587, -.331264, -.418688), vec3(.114, .5, -.081312));
// I'm not worrying about 128/255 vs. .5 here.
vec3 rgb_to_ycbcr_offset = vec3(0., .5, .5);

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    // Split the image into 8x1 blocks. What's my x index inside the block?
    int block_x = int(fragCoord.x) % 8;
    // Go to the start of the row in this 8x8 block
    ivec2 block_start = ivec2(fragCoord) - ivec2(block_x, 0);
    // Fetch 8 pixels in this row
    vec3 row[8];
    for(int i = 0; i < 8; i++){
          row[i] = texelFetch(iChannel0, block_start + ivec2(i, 0), 0).rgb;
    }
    // Transform each texel to YCbCr, and shift to SNORM.
    for(int i = 0; i < 8; i++){
        row[i] = rgb_to_ycbcr_offset + rgb_to_ycbcr * row[i];
    }
    // Kind of emulate the horizontal component of 4:2:0 compression
    // by averaging out the Cb and Cr components of every 2 pixels.
    // Ideally, we'd really do the image rescaling - since this
    // is sort of like a 16-wide DCT - but this should be close enough.
    // Also this is really hard to see on a 1080p screen! Shoutout to
    // the Joint Photography Experts Group.
    for(int i = 0; i < 4; i++){
        row[2*i+0].gb = row[2*i+1].gb = mix(row[2*i+0].gb, row[2*i+1].gb, .5);
    }
    // Get this component of the horizontal DCT
    vec3 dct_out = vec3(0.);
    for(int i = 0; i < 8; i++){
          dct_out += dct[8*block_x + i] * row[i];
    }
    fragColor = vec4(dct_out, 1.);
}

buffer_c_code:
// Horizontal DCT -> Full DCT + quantization

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Split the image into 8x1 blocks. What's my x index inside the block?
    int block_y = int(fragCoord.y) % 8;
    // Go to the start of the row in this 8x8 block
    ivec2 block_start = ivec2(fragCoord) - ivec2(0, block_y);
    // Fetch 8 pixels in this column
    vec3 col[8];
    for(int i = 0; i < 8; i++){
        col[i] = texelFetch(iChannel1, block_start + ivec2(0, i), 0).rgb;
    }
    // Kind of emulate the vertical part of 4:2:0 subsampling
    // by averaging out the CoCg components of each 2 pixels.
    for(int i = 0; i < 4; i++){
        col[2*i+0].gb = col[2*i+1].gb = mix(col[2*i+0].gb, col[2*i+1].gb, .5);
    }
    // Get this component of the vertical DCT:
    vec3 dct_out = vec3(0.);
    for(int i = 0; i < 8; i++){
        dct_out += dct[8*block_y + i] * col[i];
    }
    // dct_out now contains the DCT of the block at frequency (block_x, block_y)!
    // Let's quantizue it. I don't feel like embedding the official JPEG tables,
    // so I'm just going to make something up.
    int block_x = int(fragCoord.x) % 8;
    // We'll multiply block_x + block_y + 1 by some value f. Here are a few
    // test values of f:
    // 10 is noticeably crunchy; 40 is about 10% in from the left of
    // https://en.wikipedia.org/wiki/File:Felis_silvestris_silvestris_small_gradual_decrease_of_quality.png;
    // 100 is like Discord video on a slow connection;
    // 200 makes everything pink, gray, and blue;
    // 0 should be no quantization; since I want to avoid division by 0, I'll add 1.
    float depth = texelFetch(iChannel0, ivec2(fragCoord) - ivec2(block_x, block_y), 0).a;
    float f = EFFECT_STRENGTH * 450.*(exp2(2.*cc_from_depth(depth)) - 1.);
    float quantization = f * float(block_x + block_y + 1) + 1.f;
    // And then this is roughly how JPEG quantizes: we scale to full-range,
    // apply the quantization, and then scale back to normalized values.
    dct_out = round(128. * dct_out / quantization) * (quantization / 128.);
    fragColor = vec4(dct_out, 1.0);
}

