// Full DCT -> Horizontal DCT

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Split the image into 8x1 blocks. What's my x index inside the block?
    int block_y = int(fragCoord.y) % 8;
    // Go to the start of the row in this 8x8 block
    ivec2 block_start = ivec2(fragCoord) - ivec2(0, block_y);
    // Fetch 8 pixels in this column
    vec3 col[8];
    for(int i = 0; i < 8; i++){
        col[i] = texelFetch(iChannel0, block_start + ivec2(0, i), 0).rgb;
    }
    // Multiply by the inverse of the vertical DCT.
    // That's the transpose! This is the same as
    // Buffer C except we swap i and block_y.
    vec3 dct_out = vec3(0.);
    for(int i = 0; i < 8; i++){
        dct_out += dct[8*i + block_y] * col[i];
    }
    fragColor = vec4(dct_out,1.0);
}

