// Shadertoy header
void mainImage(out vec4 fragColor, in vec2 fragCoord);



// Hash function used in Perlin noise
float hash(float x) {
    return fract(sin(x) * 43758.5453);
}

// 1D Perlin noise
float noise(float x) {
    float i = floor(x);
    float f = fract(x);
    float u = f * f * (3.0 - 2.0 * f);
    return mix(hash(i), hash(i + 1.0), u);
}

// 2D Perlin noise
float noise(vec2 x) {
    vec2 i = floor(x);
    vec2 f = fract(x);
    float a = hash(i.x + i.y * 57.0);
    float b = hash(i.x + 1.0 + i.y * 57.0);
    float c = hash(i.x + i.y * 57.0 + 1.0);
    float d = hash(i.x + 1.0 + i.y * 57.0 + 1.0);
    vec2 u = f * f * (3.0 - 2.0 * f);
    return mix(a, b, u.x) + (c - a) * u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Scale the coordinates to the range [-1, 1]
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    // Get 2D Perlin noise value
    float n = noise(uv * 10.0); // You can adjust the scale (10.0) to change the noise frequency

    // Add sine wave distortion using the noise value as an offset
    float distortionAmount = 0.1; // Adjust the distortion intensity here
    float sineWaveOffset = sin(uv.x * 20.0 + n * 2.0 * 3.14159) * distortionAmount;
    uv.y += sineWaveOffset;

    // Remap noise to [0, 1] and output grayscale color based on the noise value
    fragColor = vec4(vec3(0.5 * (n + 1.0)), 1.0);
}

