#define AA  1   // optional antialiasing

#define VAR_ZERO min (nFrame, 0)

float PrBoxDf (vec3 p, vec3 b);
float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrBox2Df (vec2 p, vec2 b);
float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float PrCapsDf (vec3 p, float r, float h);
float PrCaps2Df (vec2 p, float r, float h);
float Minv2 (vec2 p);
float Maxv2 (vec2 p);
float SmoothMin (float a, float b, float r);
float SmoothMax (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 qHit, sunDir, qEye;
vec2 csHead, csEar, szKen;
float dstFar, tCur;
int nFrame, idObj;
const int idBod = 1, idHead = 2, idEar = 3, idArm = 4, idLeg = 5, idTail = 6, idNos = 7, idJaw = 8,
   idEye = 9, idCap = 10, idNeck = 11, idScarf = 12, idSeat = 13, idCush = 14, idKenWl = 15,
   idKenRf = 16, idKenFl = 17;
const float pi = 3.1415927;

#define CosSin(x) (sin ((x) + vec2 (0.5 * pi, 0.)))
#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q, qr, ePos;
  float dMin, d, db;
  dMin = dstFar;
  p.y -= 5.5;
  p.xz *= -1.;
  ePos = vec3 (0.25, 0.85, 0.3);
  q = p;
  q.xz = Rot2Cs (q.xz, csHead);
  qr = q;
  q.y -= 0.7;
  d = PrSphDf (q, 0.5);
  d = max (d, - PrSphDf (vec3 (abs (q.x), q.y + 0.7, q.z) - ePos, 0.18));
  q.yz = Rot2D (q.yz, -0.11 * pi);
  q.z -= 0.6;
  d = max (SmoothMin (d, PrSphDf (q, 0.35), 0.3), 0.06 - length (vec2 (q.y + 0.05,
     max (0.1 - q.z, 0.))));
  DMINQ (idHead);
  q.y -= -0.05;
  d = PrCylDf (q.xzy, 0.32, 0.05);
  DMINQ (idJaw);
  q.yz -= vec2 (0.22, 0.27);
  d = PrSphDf (q, 0.1);
  DMINQ (idNos);
  q = qr;
  q.y -= 0.7;
  d = max (PrSphDf (q, 0.55), min (length (q.yz - vec2 (0.45, -0.25)) - 0.35,
     min (q.z + 0.25, 0.4 - q.y)));
  DMINQ (idCap);
  q = qr;
  q.x = abs (q.x);
  qEye = q - ePos;
  d = PrSphDf (qEye, 0.17);
  DMINQ (idEye);
  q = qr;
  q.x = abs (q.x);
  q -= vec3 (0.48, 0.7, -0.05);
  d = PrCapsDf (q, 0.06, 0.12);
  q.x -= 0.04;
  q.xy = Rot2Cs (q.xy, csEar);
  d = SmoothMin (d, SmoothMax (max (PrCaps2Df (q.zy, 0.15, 0.5), q.y),
     abs (q.x) - 0.02, 0.03), 0.03);
  DMINQ (idEar);
  q = p;
  q.yz -= vec2 (0.15, -0.1);
  d = max (length (q.xz) - 0.15, abs (q.y) - 0.2);
  DMINQ (idNeck);
  q = p;
  q.xy -= vec2 (0.1, 0.07) * CosSin (2. * pi * (q.z + tCur)) * smoothstep (0.3, 0.6, - q.z);
  q.x = abs (q.x);
  q -= vec3 (0.14, 0.15, -1.3);
  d = 0.5 * PrBoxDf (q, vec3 (0.01, 0.04, 1.2));
  DMINQ (idScarf);
  q = p;
  q.yz -= vec2 (-0.35, -0.05);
  q.x *= 0.85;
  d = PrSphDf (q, 0.45);
  q.y -= -0.6;
  d = SmoothMin (d, PrSphDf (q, 0.55), 0.3);
  DMINQ (idBod);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.5, -0.3, 0.05);
  q.xz = Rot2D (q.xz, 0.15 * pi);
  q.yz = Rot2D (q.yz, -0.1 * pi);
  d = PrSphDf (q, 0.11);
  q.z -= 0.3;
  d = SmoothMin (d, PrCapsDf (q, 0.1, 0.3), 0.05);
  DMINQ (idArm);
  q.yz -= vec2 (0.06, 0.34);
  d = SmoothMax (PrCaps2Df (q.xy, 0.1, 0.05), abs (q.z) - 0.04, 0.04);
  q.x = abs (q.x);
  q.xy = Rot2D (q.xy - vec2 (0.05, 0.13), 0.1 * pi);
  q.x = abs (q.x) - 0.025;
  d = SmoothMin (d, PrSphDf (q, 0.04), 0.01);
  DMINQ (idArm);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.4, -1.35, 0.2);
  d = PrCapsDf (q, 0.13, 0.45);
  DMINQ (idLeg);
  q.yz -= vec2 (0.06, 0.52);
  d = SmoothMax (PrCaps2Df (q.xy, 0.1, 0.06), abs (q.z) - 0.04, 0.04);
  q.x = abs (q.x);
  q.xy = Rot2D (q.xy - vec2 (0.05, 0.13), 0.1 * pi);
  q.x = abs (q.x) - 0.025;
  d = SmoothMin (d, PrSphDf (q, 0.04), 0.01);
  DMINQ (idLeg);
  q = p;
  q.yz -= vec2 (-1.3, -0.6);
  d = PrCapsDf (q, 0.07, 0.3);
  DMINQ (idTail);
  q = p;
  q.yz -= vec2 (-1.55, 0.1);
  d = PrRoundBoxDf (q, vec3 (0.6, 0.08, 0.7), 0.02);
  DMINQ (idSeat);
  q = p;
  db = dot (vec2 (abs (q.x), q.y), vec2 (1.) / sqrt(2.)) + 1.1;
  q.yz -= vec2 (-5.4, 0.5);
  d = max (abs (PrBox2Df (q.xz, szKen)) - 0.07,
     max (min (- PrCaps2Df (vec2 (q.x, q.y - 0.6), 0.65, 0.75), q.z),
     max (db, abs (q.y - 2.1) - 2.)));
  DMINQ (idKenWl);
  d = max (abs (db) - 0.06, Maxv2 (abs (q.xz) - szKen - 0.3));
  DMINQ (idKenRf);
  d = PrBoxDf (q, vec3 (szKen + 0.3, 0.1).xzy);
  DMINQ (idKenFl);
  q.xz -= -0.4 * szKen;
  d = PrRoundBoxDf (q, vec3 (0.6 * szKen, 0.1).xzy, 0.05);
  DMINQ (idCush);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    p = ro + dHit * rd;
    d = ObjDf (p);
    if (d < 0.001 || dHit > dstFar || p.y < 0.) break;
    dHit += d;
  }
  if (p.y < 0.) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  int idObjT;
  idObjT = idObj;
  sh = 1.;
  d = 0.01;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05 || d > dstFar) break;
  }
  idObj = idObjT;
  return 0.5 + 0.5 * sh;
}

vec3 SkyCol (vec3 rd)
{
  rd.y = abs (rd.y);
  return mix (vec3 (0.3, 0.35, 0.7), vec3 (0.8, 0.8, 0.8),
     clamp (2. * (Fbm2 (4. * rd.xz / rd.y + 0.1 * tCur) - 0.1) * rd.y, 0., 1.));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, cb4;
  vec3 col, vn;
  vec2 u, vf;
  float dstObj, sh, nDotL;
  bool isLit;
  csHead = CosSin (0.2 * pi * sin (0.3 * pi * tCur));
  csEar = CosSin (-0.4 * pi * (1. + sin (0.5 * pi * tCur)));
  szKen = vec2 (1.6, 2.2);
  dstObj = ObjRay (ro, rd);
  isLit = false;
  vf = vec2 (0.);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    cb4 = vec4 (0.9, 0.9, 0.92, 0.05);
    if (idObj <= idJaw) {
      col4 = cb4;
      if (idObj == idBod) {
        if (qHit.z < 0.) col4 *= 0.5 + 0.5 * smoothstep (0., 0.02,
           PrCaps2Df (vec2 (qHit.x, qHit.y - 0.4), 0.03, 0.5));
      } else if (idObj == idHead) {
        col4 = (length (qEye) < 0.21) ? vec4 (0.8, 0.6, 0.2, 0.2) :
           cb4 * (0.5 + 0.5 * step (0.03, length (qHit.yz - vec2 (0.2, 0.05))));
      } else if (idObj == idEar) {
        col4 *= 0.93 + 0.07 * sin (32. * pi * qHit.z);
      } else if (idObj == idTail) {
        col4 *= 0.3 + 0.7 * step (-0.17, qHit.z);
      } else if (idObj == idNos) {
        col4 = vec4 (0.7, 0.5, 0.5, 0.);
      } else if (idObj == idJaw) {
        col4 = vec4 (0.9, 0.9, 0.8, 0.2) * (0.4 + 0.6 * min (step (0.2,
           fract (32. * (atan (qHit.z, qHit.x) / (2. * pi) + 0.5))),
           step (0.005, abs (qHit.y))));
      }
    } else if (idObj <= idCush){
      if (idObj == idCap) {
        col4 = vec4 (0.5, 0.2, 0., 0.) * (0.93 + 0.07 * sin (32. * pi * qHit.x));
        vf = vec2 (8., 0.2);
      } else if (idObj == idNeck || idObj == idScarf) {
        col4 = mix (mix (vec4 (0., 1., 1., 0.2), vec4 (1., 0.3, 0.3, 0.2),
           step (0.015, abs (qHit.y))), cb4, step (0.042, abs (qHit.y)));
      } else if (idObj == idSeat) {
        col4 = vec4 (0.6, 0.5, 0.1, 0.);
      } else if (idObj == idCush) {
        col4 = vec4 (0.7, 0.4, 0.4, 0.);
      }
    } else {
      col4 = vec4 (0.8, 0.6, 0.1, 0.);
      if (idObj == idKenWl) {
        col4 *= (0.5 + 0.5 * step (0., PrBox2Df (qHit.xz, szKen))) *
           (0.8 + 0.2 * smoothstep (0.08, 0.1, abs (fract (4. * qHit.y + 0.5) - 0.5)));
        if (abs (qHit.z) > szKen.y) {
          u = vec2 (qHit.x * sign (qHit.z - szKen.y), qHit.y - 2.75);
          if (min (max (min (max (abs (PrCaps2Df ((u - vec2 (-0.1, 0.2)).yx, 0.2, 0.2)),
             - PrBox2Df (u + vec2 (0.3, 0.), vec2 (0.3, 0.2))),
             max (abs (PrCaps2Df ((u + vec2 (-0.1, 0.2)).yx, 0.2, 0.2)),
             - PrBox2Df (u - vec2 (0.3, 0.), vec2 (0.3, 0.2)))), abs (u.x) - 0.3),
             abs (length (u.xy) - 0.65) + 0.03) < 0.06) col4 = vec4 (0.2, 1., 1., 0.2);
        }
      } else if (idObj == idKenRf) {
        col4 *= 0.8 + 0.2 * smoothstep (0.04, 0.05, abs (fract (2. * qHit.z + 0.5) - 0.5));
        vf = vec2 (16., 0.2);
      } else if (idObj == idKenFl) {
        col4 *= 0.7;
        vf = vec2 (4., 0.5);
      }
    }
    if (idObj == idEye) {
      rd = reflect (rd, vn);
      col = mix (vec3 (0., 0.3, 0.3), ((rd.y > 0.) ? SkyCol (rd) : vec3 (0.1, 0.5, 0.1)), 0.7);
    } else {
      isLit = true;
    }
  } else if (rd.y < 0.) {
    dstObj = - ro.y / rd.y;
    ro += dstObj * rd;
    vn = vec3 (0., 1., 0.);
    col4 = vec4 (0.2, 0.5, 0.2, 0.) * (0.7 + 0.3 * Fbm2 (ro.xz));
    vf = vec2 (1., 0.5);
    isLit = true;
  } else {
    col = SkyCol (rd);
  }
  if (isLit) {
    if (vf.y > 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
    nDotL = max (dot (vn, sunDir), 0.);
    if (idObj >= idBod || idObj <= idTail) nDotL *= sqrt (nDotL);
    sh = (dstObj < dstFar) ? ObjSShadow (ro + 0.01 * vn, sunDir) : 1.;
    col = col4.rgb * (0.2 + 0.2 * max (- dot (vn, sunDir), 0.) + 0.8 * sh * nDotL) +
       col4.a * step (0.95, sh) * pow (max (dot (sunDir, reflect (rd, vn)), 0.), 32.);
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, uvv;
  float el, az, zmFac, asp, sr;
  nFrame = iFrame;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  az = 0.;
  el = -0.1 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az += 0.9 * pi * SmoothBump (0.25, 0.75, 0.22, fract (0.04 * tCur)) * sign (fract (0.02 * tCur) - 0.5);
    el -= 0.05 * pi * cos (0.02 * pi * tCur);
  }
  el = clamp (el, -0.4 * pi, 0.1 * pi);
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 3., -25.);
  ro.y = max (ro.y, 0.05);
  zmFac = 5.;
  dstFar = 100.;
  sunDir = vuMat * normalize (vec3 (0.5, 0.5, -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    uvv = uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.), sr * (0.667 * a + 0.5) * pi);
    rd = vuMat * normalize (vec3 (uvv, zmFac));
    col += (1. / naa) * ((length (uvv * vec2 (max (0.8, 1. / asp), 1.)) > 0.99) ? vec3 (0.82) :
       ShowScene (ro, rd));
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (vec3 (p.xy, sign (p.z) * (max (0., abs (p.z) - h)))) - r;
}

float PrCaps2Df (vec2 p, float r, float h)
{
  return length (vec2 (p.x, sign (p.y) * (max (0., abs (p.y) - h)))) - r;
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

