/////////////////////////////////////////////////////////////////////////////
// Computes (I_a * k_a) + k_shadow * I_source * [ k_d * (N.L) + k_r * (R.V)^n ].
// Input vectors L, N and V are pointing AWAY from surface point.
// Assume all vectors L, N and V are unit vectors.
/////////////////////////////////////////////////////////////////////////////
vec3 PhongLighting( in vec3 L, in vec3 N, in vec3 V, in bool inShadow,
                    in Material_t mat, in Light_t light )
{
    if ( inShadow ) {
        return light.I_a * mat.k_a;
    }
    else {
        vec3 R = reflect( -L, N );
        float N_dot_L = max( 0.0, dot( N, L ) );
        float R_dot_V = max( 0.0, dot( R, V ) );
        float R_dot_V_pow_n = ( R_dot_V == 0.0 )? 0.0 : pow( R_dot_V, mat.n );

        return light.I_a * mat.k_a +
               light.I_source * (mat.k_d * N_dot_L + mat.k_r * R_dot_V_pow_n);
    }
}



/////////////////////////////////////////////////////////////////////////////
// Casts a ray into the scene and returns color computed at the nearest
// intersection point. The color is the sum of light from all light sources,
// each computed using Phong Lighting Model, with consideration of
// whether the interesection point is being shadowed from the light.
// If there is no interesection, returns the background color, and outputs
// hasHit as false.
// If there is intersection, returns the computed color, and outputs
// hasHit as true, the 3D position of the intersection (hitPos), the
// normal vector at the intersection (hitNormal), and the k_rg value
// of the material of the intersected object.
/////////////////////////////////////////////////////////////////////////////
vec3 CastRay(in Ray_t ray, out bool hasHit, out vec3 hitPos, out vec3 hitNormal, out vec3 k_rg,out float ior, out float fuzz)
{
    bool hasHitSomething = false;
    float nearest_t = DEFAULT_TMAX;
    vec3 nearest_hitPos;
    vec3 nearest_hitNormal;
    int nearest_hitMatID;

    float temp_t;
    vec3 temp_hitPos;
    vec3 temp_hitNormal;
    bool temp_hasHit;

    /////////////////////////////////////////////////////////////////////////////
    // TASK:
    // * Try interesecting input ray with all the planes and spheres,
    //   and record the front-most (nearest) interesection.
    // * If there is interesection, need to record hasHitSomething,
    //   nearest_t, nearest_hitPos, nearest_hitNormal, nearest_hitMatID.
    /////////////////////////////////////////////////////////////////////////////

    /////////////////////////////////
    // TASK: WRITE YOUR CODE HERE. //
    /////////////////////////////////

    temp_hasHit=false;


    for (int i = 0; i < NUM_PLANES; i++)
    {

        if (IntersectPlane(Plane[i], ray, DEFAULT_TMIN, DEFAULT_TMAX, temp_t, temp_hitPos, temp_hitNormal))
        {
            temp_hasHit = true;
            if (temp_t < nearest_t)
            {
                nearest_t = temp_t;
                nearest_hitPos = temp_hitPos;
                nearest_hitNormal = temp_hitNormal;
                nearest_hitMatID = Plane[i].materialID;
            }
        }
    }

    //head
    if (IntersectSphere(Sphere[0], ray, DEFAULT_TMIN, DEFAULT_TMAX, temp_t, temp_hitPos, temp_hitNormal))
    {
        temp_hasHit = true;
        if (temp_t < nearest_t)
        {
            nearest_t = temp_t;
            nearest_hitPos = temp_hitPos;
            nearest_hitNormal = temp_hitNormal;
            vec3 temp = temp_hitPos - Sphere[0].center;
            float r = Sphere[0].radius * 0.8;
            //is in face
            if(temp.x * temp.x + (temp.y - (-Sphere[0].radius + r)) * (temp.y - (-Sphere[0].radius + r)) <= r*r && temp.z > 0.0){
                //is in mouth
                nearest_hitMatID = 4;//white
                if(-r <= temp.y && temp.y <= 0.0)
                    if(-0.75 * Sphere[0].radius <= temp.x && temp.x <=0.75 * Sphere[0].radius)
                        if(temp.y >= 64.0 / 45.0 * (temp.x - 0.75 * Sphere[0].radius) * (temp.x + 0.75 * Sphere[0].radius))
                            nearest_hitMatID = 6;//red

            }
            else
                nearest_hitMatID = Sphere[0].materialID;//blue
        }
    }
    //others
    for (int i = 1; i < NUM_SPHERES; i++)
    {
        float temp_t;
        if (IntersectSphere(Sphere[i], ray, DEFAULT_TMIN, DEFAULT_TMAX, temp_t, temp_hitPos, temp_hitNormal))
        {
            temp_hasHit = true;
            if (temp_t < nearest_t)
            {
                nearest_t = temp_t;
                nearest_hitPos = temp_hitPos;
                nearest_hitNormal = temp_hitNormal;
                nearest_hitMatID = Sphere[i].materialID;
            }
        }
    }

    //body
    if (IntersectStick(Stick[0], ray, DEFAULT_TMIN, DEFAULT_TMAX, temp_t, temp_hitPos, temp_hitNormal))
    {
        temp_hasHit = true;
        if (temp_t < nearest_t)
        {
            nearest_t = temp_t;
            nearest_hitPos = temp_hitPos;
            nearest_hitNormal = temp_hitNormal;

            float r = 0.85 * Stick[0].radius;
            vec3 temp = temp_hitPos - Stick[0].vertexB;
            vec3 bottom = Stick[0].vertexA - Stick[0].vertexB;
            float x = temp.x - 0.5 * bottom.x;
            float y = temp.y - 0.2 * bottom.y;
            //is in belly
            if(x * x + y * y <= r * r && temp.z > 0.0){
                float rr = 0.77 * r;
                if(x * x + y * y <= rr * rr && temp.z > 0.0 && y <= 0.0){
                    float rrr = 0.73 * r;
                    if(x * x + y * y <= rrr * rrr && temp.z > 0.0 && y <= -0.04)
                        nearest_hitMatID = 4;
                    else
                        nearest_hitMatID = 7;
                }


                else
                nearest_hitMatID = 4;
            }

            else
            nearest_hitMatID = Stick[0].materialID;
        }
    }
    //others
    for (int i = 1; i < NUM_STICKS; i++)
    {
        float temp_t;
        if (IntersectStick(Stick[i], ray, DEFAULT_TMIN, DEFAULT_TMAX, temp_t, temp_hitPos, temp_hitNormal))
        {
            temp_hasHit = true;
            if (temp_t < nearest_t)
            {
                nearest_t = temp_t;
                nearest_hitPos = temp_hitPos;
                nearest_hitNormal = temp_hitNormal;
                nearest_hitMatID = Stick[i].materialID;
            }
        }
    }

    for (int i = 0; i < NUM_Ellipsoids; i++)
    {
        float temp_t;
        if (IntersectEllipsoid(Ellipsoid[i], ray, DEFAULT_TMIN, DEFAULT_TMAX, temp_t, temp_hitPos, temp_hitNormal))
        {
            temp_hasHit = true;
            if (temp_t < nearest_t)
            {
                nearest_t = temp_t;
                nearest_hitPos = temp_hitPos;
                nearest_hitNormal = temp_hitNormal;
                nearest_hitMatID = Ellipsoid[i].materialID;
            }
        }
    }

    for (int i = 0; i < NUM_ROLLING_BALLS; i++)
    {
        float temp_t;
        if (IntersectSphere(RollingBalls1[i], ray, DEFAULT_TMIN, DEFAULT_TMAX, temp_t, temp_hitPos, temp_hitNormal))
        {
            temp_hasHit = true;
            if (temp_t < nearest_t)
            {
                nearest_t = temp_t;
                nearest_hitPos = temp_hitPos;
                nearest_hitNormal = temp_hitNormal;
                nearest_hitMatID = RollingBalls1[i].materialID;
            }
        }
    }
    for (int i = 0; i < NUM_ROLLING_BALLS; i++)
    {
        float temp_t;
        if (IntersectSphere(RollingBalls2[i], ray, DEFAULT_TMIN, DEFAULT_TMAX, temp_t, temp_hitPos, temp_hitNormal))
        {
            temp_hasHit = true;
            if (temp_t < nearest_t)
            {
                nearest_t = temp_t;
                nearest_hitPos = temp_hitPos;
                nearest_hitNormal = temp_hitNormal;
                nearest_hitMatID = RollingBalls2[i].materialID;
            }
        }
    }


    for (int i = 0; i < NUM_BOXS; i++)
    {

        if (IntersectBox(Box[i], ray, DEFAULT_TMIN, DEFAULT_TMAX, temp_t, temp_hitPos, temp_hitNormal))
        {
            temp_hasHit = true;
            if (temp_t < nearest_t)
            {
                nearest_t = temp_t;
                nearest_hitPos = temp_hitPos;
                nearest_hitNormal = temp_hitNormal;
                nearest_hitMatID = Box[i].materialID;
            }
        }
    }
    hasHitSomething=temp_hasHit;


    hasHit = hasHitSomething;
    if (!hasHitSomething)
        return BACKGROUND_COLOR;

    vec3 I_local = vec3(0.0);

    /////////////////////////////////////////////////////////////////////////////
    // TASK:
    // * Accumulate lighting from each light source on the nearest hit point.
    //   They are all accumulated into I_local.
    // * For each light source, make a shadow ray, and check if the shadow ray
    //   intersects any of the objects (the planes and spheres) between the
    //   nearest hit point and the light source.
    // * Then, call PhongLighting() to compute lighting for this light source.
    /////////////////////////////////////////////////////////////////////////////

    /////////////////////////////////
    // TASK: WRITE YOUR CODE HERE. //
    /////////////////////////////////

    Ray_t shadowRay;
    shadowRay.o = nearest_hitPos;

    for (int i = 0; i < NUM_LIGHTS; i++)
    {
        shadowRay.d = normalize(Light[i].position - nearest_hitPos);
        bool intersect = false;

        // Check for shadow by casting a shadow ray to each light source.

        for (int j = 0; j < NUM_PLANES; j++)
        {
            if (IntersectPlane(Plane[j], shadowRay, DEFAULT_TMIN, distance(Light[i].position,nearest_hitPos)))
            {
                intersect = true;
                break;
            }
        }
        for (int j = 0; j < NUM_SPHERES; j++)
        {
            if (IntersectSphere(Sphere[j], shadowRay, DEFAULT_TMIN, distance(Light[i].position,nearest_hitPos)))
            {
                intersect = true;
                break;
            }
        }

        for (int j = 0; j < NUM_STICKS; j++)
        {
            if (IntersectStick(Stick[j], shadowRay, DEFAULT_TMIN, distance(Light[i].position,nearest_hitPos)))
            {
                intersect = true;
                break;
            }
        }


        for (int j = 0; j < NUM_ROLLING_BALLS; j++)
        {
            if (IntersectSphere(RollingBalls1[j], shadowRay, DEFAULT_TMIN, distance(Light[i].position,nearest_hitPos)))
            {
                intersect = true;
                break;
            }
        }
        for (int j = 0; j < NUM_ROLLING_BALLS; j++)
        {
            if (IntersectSphere(RollingBalls2[j], shadowRay, DEFAULT_TMIN, distance(Light[i].position,nearest_hitPos)))
            {
                intersect = true;
                break;
            }
        }

        for (int j = 0; j < NUM_BOXS; j++)
        {
            if (IntersectBox(Box[j], shadowRay, DEFAULT_TMIN, distance(Light[i].position,nearest_hitPos)))
            {
                intersect = true;
                break;
            }
        }
        if (!intersect)
        {
            I_local += PhongLighting(shadowRay.d, nearest_hitNormal, -ray.d, intersect, Material[nearest_hitMatID], Light[i]);
        }

    }
    hitPos = nearest_hitPos;
    hitNormal = nearest_hitNormal;
    k_rg = Material[nearest_hitMatID].k_rg;
    ior = Material[nearest_hitMatID].ior;
    fuzz = Material[nearest_hitMatID].fuzz;

    return I_local;
}






/////////////////////////////////////////////////////////////////////////////
// Execution of fragment shader starts here.
// 1. Initializes the scene.
// 2. Compute a primary ray for the current pixel (fragment).
// 3. Trace ray into the scene with NUM_ITERATIONS recursion levels.
/////////////////////////////////////////////////////////////////////////////
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    InitScene();

    // Scale pixel 2D position such that its y coordinate is in [-1.0, 1.0].
    vec2 pixel_pos = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;


    // Position the camera.
    vec2 mouse_pos = iMouse.xy /iResolution.xy;
    vec3 cam_pos = 3.0 * normalize(vec3(sin(0.35 * iTime + 3.0*mouse_pos.x), 0.2 + 0.8 * mouse_pos.y, 
                   abs(cos(0.35 * iTime + 3.0*mouse_pos.x)))) + vec3(0.0,2.0,4.0);
   // vec3 cam_pos = vec3( 0.0, 2.0, 3.0 );
    vec3 cam_lookat = vec3( 0.0, 0.0, -1.0 );
    vec3 cam_up_vec = vec3( 0.0, 1.0, 0.0 );

    // Set up camera coordinate frame in world space.
    vec3 cam_z_axis = normalize( cam_pos - cam_lookat );
    vec3 cam_x_axis = normalize( cross(cam_up_vec, cam_z_axis) );
    vec3 cam_y_axis = normalize( cross(cam_z_axis, cam_x_axis));


    // Create primary ray.
    float pixel_pos_z = -1.0 / tan(FOVY / 2.0);
    Ray_t pRay;
    pRay.o = cam_pos;
    vec3 er = normalize(vec3(pixel_pos,pixel_pos_z));
    pRay.d = normalize(mat3(cam_x_axis,cam_y_axis,cam_z_axis)*er);


    // Start Ray Tracing.
    // Use iterations to emulate the recursion.

    vec3 I_result = vec3( 0.0 );
    vec3 compounded_k_rg = vec3( 1.0 );
    Ray_t nextRay[TOTAL_RAY_NUM];
    nextRay[0] = pRay;
    nextRay[0].should_cast = true;
    nextRay[0].weight = 1.0;

    for ( int level = 0; level <= NUM_ITERATIONS; level++ )
    {
        bool hasHit;
        vec3 hitPos, hitNormal, k_rg;
        float ior,fuzz;

        vec3 I_local;
        //Cast at least one ray
        //Maybe we need to cast more ray
        for(int i=0;i<TOTAL_RAY_NUM;++i)
        {
            if(!nextRay[i].should_cast)
                break;
            I_local += CastRay( nextRay[i], hasHit, hitPos, hitNormal, k_rg,ior,fuzz)*nextRay[i].weight;
            nextRay[i].should_cast = false;
        }


        I_result += compounded_k_rg * I_local;

        if ( !hasHit ) break;

        compounded_k_rg *= k_rg;

        vec3 reflected = reflect(nextRay[0].d, hitNormal);

        if(ior<0.0){ 
            if(fuzz<0.0){
                //For specular surface, we only cast one ray
                nextRay[0] = Ray_t( hitPos, reflected,true,1.0);
            }else{
                //For glossy surface, we need to sample more ray
                for(int i=0;i<GLOSSY_RAY_NUM;++i){
                    nextRay[i] = Ray_t( hitPos, reflected+fuzz*random_in_unit_sphere(seed),true,GLOSSY_RAY_WEIGHT);
                    //refresh seed
                    seed = hash1(seed);
                }
            }
        }
        else{
        //For trasparent surface,we trace one or two ray
            vec3 dir = nextRay[0].d;
            if(dot(-dir,hitNormal)>=0.0){
                ior = 1.0/ior;
            }else{
                hitNormal = -hitNormal;
            }
            float cos_theta = min(dot(-dir, hitNormal), 1.0);
            float sin2_theta = 1.0 - cos_theta*cos_theta;
            bool total_internal_reflect = ior*ior*sin2_theta > 1.0;
            if(total_internal_reflect){
            //For total internal reflection, we only cast one ray
               nextRay[0] = Ray_t( hitPos, reflected,true,1.0);
            }
            else{
            //We cast one ray for reflection, one ray for refraction
               float reflective = reflectance(cos_theta,ior);
               nextRay[0] = Ray_t(hitPos,reflected,true,reflective);
               nextRay[1] = Ray_t(hitPos,refract(-dir,hitNormal,ior),true,1.0-reflective);
            }
       }
    }
    fragColor = vec4( I_result, 1.0 );
}

