//============================================================================
// Constants.
//============================================================================


const float PI = 3.1415926536;

const float TAU = 2.0*PI;

uint baseHash( uvec2 p ) {
    p = 1103515245U*((p >> 1U)^(p.yx));
    uint h32 = 1103515245U*((p.x)^(p.y>>3U));
    return h32^(h32 >> 16);
}

float hash1( inout float seed ) {
    uint n = baseHash(floatBitsToUint(vec2(seed+=.1,seed+=.1)));
    return float(n)/float(0xffffffffU);
}


float cuberoot( float x ) { return sign(x)*pow(abs(x),1.0/3.0); }

vec3 random_in_unit_sphere(float seed) {
    float u = hash1(seed);
    float v = hash1(seed);
    float theta = u*2.0*PI;
    float phi = acos(2.0*v-1.0);
    float r = cuberoot(hash1(seed));
    float sinTheta = sin(theta);
    float cosTheta = cos(theta);
    float sinPhi = sin(phi);
    float cosPhi = cos(phi);
    float x = r*sinPhi*cosTheta;
    float y = r*sinPhi*sinTheta;
    float z = r*cosPhi;
    return vec3(x,y,z);
}

const vec3 BACKGROUND_COLOR = vec3( 0.1, 0.2, 0.6 );

// Vertical field-of-view angle of camera. In radians.
const float FOVY = 50.0 * PI / 180.0;

// Use this for avoiding the "epsilon problem" or the shadow acne problem.
const float DEFAULT_TMIN = 10.0e-4;

// Use this for tmax for non-shadow ray intersection test.
const float DEFAULT_TMAX = 10.0e6;

// Equivalent to number of recursion levels (0 means ray-casting only).
// We are using iterations to replace recursions.
const int NUM_ITERATIONS = 3;

// Constants for the scene objects.
const int NUM_LIGHTS = 4;
const int NUM_MATERIALS = 11;
const int NUM_PLANES = 5;
const int NUM_SPHERES = 20;
const int NUM_STICKS = 40;
const int NUM_Ellipsoids=10;
const int NUM_ROLLING_BALLS = 8;
const int NUM_BOXS = 2;
// Constants for ray numbers
const int TOTAL_RAY_NUM = 10;
const int GLOSSY_RAY_NUM = 10;
const float GLOSSY_RAY_WEIGHT = 1.0/float(GLOSSY_RAY_NUM);


//============================================================================
// Define new struct types.
//============================================================================
struct Ray_t {
    vec3 o;  // Ray Origin.
    vec3 d;  // Ray Direction. A unit vector.
    bool should_cast;
    float weight;
};

struct Plane_t {
    // The plane equation is Ax + By + Cz + D = 0.
    float A, B, C, D;
    int materialID;
};

struct Sphere_t {
    vec3 center;
    float radius;
    int materialID;
};

struct Stick_t {
    vec3 vertexA;
    vec3 vertexB;
    float radius;
    int materialID;
};

struct Light_t {
    vec3 position;  // Point light 3D position.
    vec3 I_a;       // For Ambient.
    vec3 I_source;  // For Diffuse and Specular.
};

struct Ellipsoid_t {
    vec3 center;
    vec3 rad;       // radius of x,y,z axis
    int materialID;
};

struct Box_t{
    vec3 center;
    vec3 size;
    int materialID;
};

struct Material_t {
    vec3 k_a;   // Ambient coefficient.
    vec3 k_d;   // Diffuse coefficient.
    vec3 k_r;   // Reflected specular coefficient.
    vec3 k_rg;  // Global reflection coefficient.
    float n;    // The specular reflection exponent. Ranges from 0.0 to 128.0.
    float ior;  // Index of Refraction
    float fuzz; // fuzz parameter
};


//============================================================================
// Global scene data.
//============================================================================
Plane_t Plane[NUM_PLANES];
Sphere_t Sphere[NUM_SPHERES];
Light_t Light[NUM_LIGHTS];
Material_t Material[NUM_MATERIALS];
Stick_t Stick[NUM_STICKS];
Ellipsoid_t Ellipsoid[NUM_Ellipsoids];
Sphere_t RollingBalls1[NUM_ROLLING_BALLS];
Sphere_t RollingBalls2[NUM_ROLLING_BALLS];
Box_t Box[NUM_BOXS];

/////////////////////////////////////////////////////////////////////////////
// Computes intersection between a plane and a ray.
// Returns true if there is an intersection where the ray parameter t is
// between tmin and tmax, otherwise returns false.
// If there is such an intersection, outputs the value of t, the position
// of the intersection (hitPos) and the normal vector at the intersection
// (hitNormal).
/////////////////////////////////////////////////////////////////////////////
bool IntersectPlane( in Plane_t pln, in Ray_t ray, in float tmin, in float tmax,
                     out float t, out vec3 hitPos, out vec3 hitNormal )
{
    vec3 N = vec3( pln.A, pln.B, pln.C );
    float NRd = dot( N, ray.d );
    float NRo = dot( N, ray.o );
    float t0 = (-pln.D - NRo) / NRd;
    if ( t0 < tmin || t0 > tmax ) return false;

    // We have a hit -- output results.
    t = t0;
    hitPos = ray.o + t0 * ray.d;
    hitNormal = normalize( N );
    return true;
}



/////////////////////////////////////////////////////////////////////////////
// Computes intersection between a plane and a ray.
// Returns true if there is an intersection where the ray parameter t is
// between tmin and tmax, otherwise returns false.
/////////////////////////////////////////////////////////////////////////////
bool IntersectPlane( in Plane_t pln, in Ray_t ray, in float tmin, in float tmax )
{
    vec3 N = vec3( pln.A, pln.B, pln.C );
    float NRd = dot( N, ray.d );
    float NRo = dot( N, ray.o );
    float t0 = (-pln.D - NRo) / NRd;
    if ( t0 < tmin || t0 > tmax ) return false;
    return true;
}



/////////////////////////////////////////////////////////////////////////////
// Computes intersection between a sphere and a ray.
// Returns true if there is an intersection where the ray parameter t is
// between tmin and tmax, otherwise returns false.
// If there is one or two such intersections, outputs the value of the
// smaller t, the position of the intersection (hitPos) and the normal
// vector at the intersection (hitNormal).
/////////////////////////////////////////////////////////////////////////////
bool IntersectSphere( in Sphere_t sph, in Ray_t ray, in float tmin, in float tmax,
                      out float t, out vec3 hitPos, out vec3 hitNormal )
{
    /////////////////////////////////
    // TASK: WRITE YOUR CODE HERE. //
    /////////////////////////////////

    vec3 V = ray.o - sph.center;
    float a0 = dot(V, V) - sph.radius * sph.radius;
    float DdotV = dot(ray.d, V);
    float discriminate = DdotV * DdotV - a0;

    if (DdotV <= 0.0) {

        if (discriminate >= 0.0) {
            t = -DdotV - sqrt(discriminate);
            hitPos = ray.o + t * ray.d;
            hitNormal = normalize(hitPos - sph.center);
            return (t >= tmin && t<= tmax);
        }
    }
    return false;

}


/////////////////////////////////////////////////////////////////////////////
// Computes intersection between a sphere and a ray.
// Returns true if there is an intersection where the ray parameter t is
// between tmin and tmax, otherwise returns false.
/////////////////////////////////////////////////////////////////////////////
bool IntersectSphere( in Sphere_t sph, in Ray_t ray, in float tmin, in float tmax )
{
    /////////////////////////////////
    // TASK: WRITE YOUR CODE HERE. //
    /////////////////////////////////

    vec3 V = ray.o - sph.center;
    float a = dot(V,V) - sph.radius * sph.radius;
    float DdotV = dot(ray.d, V);
    float discriminate = DdotV * DdotV - a;
    if (DdotV <= 0.0) {

        if (discriminate >= 0.0) {
            float t1 = -DdotV - sqrt(discriminate);
            float t2 = -DdotV + sqrt(discriminate);
            return ((t1 >= tmin && t1<= tmax) || (t2 >= tmin && t2 <= tmax));
        }
    }
    return false;

}

/////////////////////////////////////////////////////////////////////////////
// Computes intersection between a stick and a ray.
// Returns true if there is an intersection where the ray parameter t is
// between tmin and tmax, otherwise returns false.
// If there is one or two such intersections, outputs the value of the
// smaller t, the position of the intersection (hitPos) and the normal
// vector at the intersection (hitNormal).
/////////////////////////////////////////////////////////////////////////////

// reference from https://www.shadertoy.com/view/Xt3SzX of capIntersect() and capNormal

/////////////////////////////////////////////////////////////////////////////
// Computes intersection between a stick and a ray.
// Returns true if there is an intersection, otherwise returns false.
/////////////////////////////////////////////////////////////////////////////
float capIntersect( in vec3 ro, in vec3 rd, in vec3 pa, in vec3 pb, in float r )
{
    vec3  ba = pb - pa;
    vec3  oa = ro - pa;

    float baba = dot(ba,ba);
    float bard = dot(ba,rd);
    float baoa = dot(ba,oa);
    float rdoa = dot(rd,oa);
    float oaoa = dot(oa,oa);

    float a = baba      - bard*bard;
    float b = baba*rdoa - baoa*bard;
    float c = baba*oaoa - baoa*baoa - r*r*baba;
    float h = b*b - a*c;
    if( h>=0.0 )
    {
        float t = (-b-sqrt(h))/a;
        float y = baoa + t*bard;
        // body
        if( y>0.0 && y<baba ) return t;
        // caps
        vec3 oc = (y<=0.0) ? oa : ro - pb;
        b = dot(rd,oc);
        c = dot(oc,oc) - r*r;
        h = b*b - c;
        if( h>0.0 ) return -b - sqrt(h);
    }
    return -1.0;
}

/////////////////////////////////////////////////////////////////////////////
// Computes the normal vector at the specified position on the stick surface.
/////////////////////////////////////////////////////////////////////////////
vec3 capNormal( in vec3 pos, in vec3 a, in vec3 b, in float r )
{
    vec3  ba = b - a;
    vec3  pa = pos - a;
    float h = clamp(dot(pa,ba)/dot(ba,ba),0.0,1.0);
    return (pa - h*ba)/r;
}

/////////////////////////////////////////////////////////////////////////////
// Computes intersection between a stick and a ray.
// Returns true if there is an intersection where the ray parameter t is
// between tmin and tmax, otherwise returns false.
// If there is one or two such intersections, outputs the value of the
// smaller t, the position of the intersection (hitPos) and the normal
// vector at the intersection (hitNormal).
/////////////////////////////////////////////////////////////////////////////
bool IntersectStick( in Stick_t stk, in Ray_t ray, in float tmin, in float tmax,
                      out float t, out vec3 hitPos, out vec3 hitNormal )
{
    t = capIntersect( ray.o, ray.d, stk.vertexA, stk.vertexB, stk.radius );

    if (t >= 0.0 && t <= tmax){
        hitPos = ray.o + t * ray.d;
        hitNormal = capNormal(hitPos, stk.vertexA, stk.vertexB, stk.radius);
        return true;
    } else {
        return false;
    }
}


/////////////////////////////////////////////////////////////////////////////
// Computes intersection between a stick and a ray.
// Returns true if there is an intersection where the ray parameter t is
// between tmin and tmax, otherwise returns false.
/////////////////////////////////////////////////////////////////////////////
bool IntersectStick( in Stick_t stk, in Ray_t ray, in float tmin, in float tmax)
{
    float t = capIntersect( ray.o, ray.d, stk.vertexA, stk.vertexB, stk.radius );
    if (t >= 0.0 && t >= tmin && t <= tmax){
        return true;
    } else {
        return false;
    }
}


// reference from https://www.shadertoy.com/view/llsSzn of eliIntersect() and eliNormal

/////////////////////////////////////////////////////////////////////////////
// Computes intersection between an ellipsoid and a ray.
/////////////////////////////////////////////////////////////////////////////
float eliIntersect( in vec3 ro, in vec3 rd,in Ellipsoid_t sph )
{
    vec3 oc = ro - sph.center;

    vec3 ocn = oc / sph.rad;
    vec3 rdn = rd / sph.rad;

    float a = dot( rdn, rdn );
	float b = dot( ocn, rdn );
	float c = dot( ocn, ocn );
	float h = b*b - a*(c-1.0);
	if( h<0.0 ) return -1.0;
	return (-b - sqrt( h ))/a;
}

/////////////////////////////////////////////////////////////////////////////
// Computes the normal vector at the specified position on the ellipsoid surface.
/////////////////////////////////////////////////////////////////////////////
vec3 eliNormal( in vec3 pos, in Ellipsoid_t sph )
{
    return normalize( (pos-sph.center)/sph.rad );
}

/////////////////////////////////////////////////////////////////////////////
// Computes intersection between an ellipsoid and a ray.
// Returns true if there is an intersection where the ray parameter t is
// between tmin and tmax, otherwise returns false.
// If there is one or two such intersections, outputs the value of the
// smaller t, the position of the intersection (hitPos) and the normal
// vector at the intersection (hitNormal).
/////////////////////////////////////////////////////////////////////////////
bool IntersectEllipsoid( in Ellipsoid_t tr, in Ray_t ray, in float tmin, in float tmax,
                      out float t, out vec3 hitPos, out vec3 hitNormal )
{
    t = eliIntersect( ray.o, ray.d, tr );

    if (t >= 0.0 && t <= tmax){
        hitPos = ray.o + t * ray.d;
        hitNormal = eliNormal(hitPos, tr);
        return true;
    } else {
        return false;
    }
}


/////////////////////////////////////////////////////////////////////////////
// Computes intersection between an ellipsoid and a ray.
// Returns true if there is an intersection where the ray parameter t is
// between tmin and tmax, otherwise returns false.
/////////////////////////////////////////////////////////////////////////////
bool IntersectEllipsoid( in Ellipsoid_t tr, in Ray_t ray, in float tmin, in float tmax)
{
    float t = eliIntersect( ray.o, ray.d, tr );
    if (t >= 0.0 && t >= tmin && t <= tmax){
        return true;
    } else {
        return false;
    }
}

float reflectance(float cosine, float ref_idx) {
    // Use Schlick's approximation for reflectance.
    float r0 = (1.0-ref_idx) / (1.0+ref_idx);
    r0 = r0*r0;
    float temp = 1.0-cosine;
    float temp2 = temp*temp;
    return r0 + (1.0-r0)*temp*temp2*temp2;
}

bool IntersectBox( in Box_t box, in Ray_t ray, in float tmin, in float tmax,
                     out float t, out vec3 hitPos, out vec3 hitNormal)
{
    vec3 ro = ray.o;
    ray.o -= box.center;
    vec3 m = sign(ray.d)/max(abs(ray.d), 1e-8);
    vec3 n =m*ray.o;
    vec3 k = abs(m)*box.size;

    vec3 t1 = -n - k;
    vec3 t2 = -n + k;

    float tN = max(max(t1.x, t1.y), t1.z);
    float tF = min(min(t2.x, t2.y), t2.z);

    if( tN>tF || tF<=0.0) return false;

    if(tN >= tmin && tF<=tmax){
        t = tN;
        hitPos = ro + t*ray.d;
        hitNormal = -sign(ray.d)*step(t1.yzx,t1.xyz)*step(t1.zxy,t1.xyz);
        return true;
    }
    if(tF >= tmin && tF <= tmax){
        t = tF;
        hitPos = ro + t*ray.d;
        hitNormal = -sign(ray.d)*step(t1.yzx,t1.xyz)*step(t1.zxy,t1.xyz);
        return true;
    }

    return false;
}

bool IntersectBox( in Box_t box, in Ray_t ray, in float tmin, in float tmax )
{
    ray.o -= box.center;
    vec3 m = sign(ray.d)/max(abs(ray.d), 1e-8);
    vec3 n =m*ray.o;
    vec3 k = abs(m)*box.size;

    vec3 t1 = -n - k;
    vec3 t2 = -n + k;

    float tN = max(max(t1.x, t1.y), t1.z);
    float tF = min(min(t2.x, t2.y), t2.z);

    if( tN>tF || tF<=0.0) return false;

    return (tN >= tmin && tF<=tmax)||(tF >= tmin && tF <= tmax);
}

