image_code:
/*
 * Flower of Life Trance
 *
 * A signed distance field of two repeating overlapping circle grids,
 * often known as the Flower of Life, slowly spin and change.
 *
 * Designed for and with ambient music:
 * Carbon Based Lifeforms - 6EQUJ5
 * https://www.youtube.com/watch?v=a6YU4O3zq_M
 */

#define SPEED 2.0

#define PI 3.1415926538

float sdRing(in vec2 p, in float r1, in float r2) {
  return abs(length(p) - r1) - r2;
}

float sdFlowerOfLife( in vec2 p, in float r1, in float r2) {
    float d = sdRing(p,r1,r2);

    d = min(d, sdRing(p+vec2(0.0,-r1),r1,r2));
    d = min(d, sdRing(p+vec2(0.0,r1),r1,r2));

    vec2 pos = vec2(r1 * cos(PI/6.0), r1 * sin(PI/6.0));
    vec2 rpos = reflect(pos, vec2(0.0,1.0));

    d = min(d, sdRing(p+pos,r1,r2));
    d = min(d, sdRing(p-pos,r1,r2));
    d = min(d, sdRing(p+rpos,r1,r2));
    d = min(d, sdRing(p-rpos,r1,r2));

    d = min(d, sdRing(p+vec2(r1*1.73,0.0),r1,r2));
    d = min(d, sdRing(p+vec2(-r1*1.73,0.0),r1,r2));

    vec2 pos2 = vec2(r1*1.73 * cos(PI/3.0), r1*1.73 * sin(PI/3.0));
    vec2 rpos2 = reflect(pos2, vec2(0.0,1.0));

    d = min(d, sdRing(p+pos2,r1,r2));
    d = min(d, sdRing(p-pos2,r1,r2));
    d = min(d, sdRing(p+rpos2,r1,r2));
    d = min(d, sdRing(p-rpos2,r1,r2));

    d = min(d, sdRing(p+vec2(r1*1.73,r1),r1,r2));
    d = min(d, sdRing(p+vec2(r1*1.73,-r1),r1,r2));
    d = min(d, sdRing(p+vec2(-r1*1.73,r1),r1,r2));
    d = min(d, sdRing(p+vec2(-r1*1.73,-r1),r1,r2));

    return d;
}

float sdFlowerOfLifeRepeating(in vec2 p, in float r1, in float r2) {    
    p += vec2(r1*0.865,r1);    
    p = mod(p,vec2(2.0*r1*0.865,2.0*r1))-vec2(r1*0.865,r1);
    return sdFlowerOfLife(p,r1,r2);
}

// src: https://iquilezles.org/articles/palettes/
vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ) {
    return a + b*cos(2.0*PI*(c*t+d) );
}

vec3 pEarth(in float t) {   
    return palette(t,
        vec3(0.5, 0.5, 0.5),
        vec3(0.5, 0.5, 0.5),
        vec3(1.0, 1.0, 0.5),
        vec3(0.80, 0.90, 0.30)
    );
}

vec2 rotate(vec2 uv, float th) {
  return mat2(cos(th), sin(th), -sin(th), cos(th)) * uv;
}

// src: https://www.shadertoy.com/view/7tf3Ws
float easeInOutCubic(float x) {
    return x < .5 ? 4. * x * x * x : 1. - pow(-2. * x + 2., 3.) / 2.;
}

float triangleWave(float x) {
  return abs(( mod(x, 2.0)) - 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float time = iTime*SPEED;

    // Find the diviser to handle vertical and horizontal screens.
    float resolutionDiviser = min(iResolution.y,iResolution.x);
    vec2 uv = (fragCoord.xy-iResolution.xy*.5)/resolutionDiviser;

    // Zooming
    uv *= easeInOutCubic(1.0-abs(cos(time/25.0)))*1.5+1.0;
    //uv *= 2.0;
    // Spinning
    uv = rotate(uv, easeInOutCubic(cos(time/29.0))/2.0);
    // Repeating flowers
    float d = sdFlowerOfLifeRepeating(uv, 0.5, 0.00005);
    // Repeating more flowers
    uv = rotate(uv, -easeInOutCubic(sin(time/37.0))/2.0);
    d = min(d-0.005, sdFlowerOfLifeRepeating(uv, 0.3+0.4*easeInOutCubic(triangleWave(time/41.0)), 0.00005));
    // Coloring
    vec3 col = pEarth(cos(d*10.0-time/23.0));
    // Moving
	col *= 0.7 + 0.5*cos(300.0*d + time*4.0);
    vec3 lineCol = pEarth(cos(d*30.0-time/18.0)) * vec3(1.2);
	col = mix( col, lineCol, 1.0 - smoothstep(0.0, easeInOutCubic(triangleWave(time/8.0))/58.0+0.005, d));

    // Just black and white
    //vec3 col = vec3(smoothstep(0.0, 0.01, d));

    fragColor = vec4(col,1.0);
}

// Ray marching constants
#define MAX_STEPS 300
#define SURF_DIST 0.001
#define MAX_DIST 100.0
#define PI 3.141592653592
#define TAU 6.283185307185


////////////////////////////////////////////////////////////////
// Noise
////////////////////////////////////////////////////////////////

// Hash & voronoi from iq: https://www.shadertoy.com/view/ldl3Dl
vec3 hash( vec3 x )
{
	x = vec3( dot(x,vec3(127.1,311.7, 74.7)),
			  dot(x,vec3(269.5,183.3,246.1)),
			  dot(x,vec3(113.5,271.9,124.6)));

	return fract(sin(x)*43758.5453123);
}
vec3 voronoi( in vec3 x )
{
    vec3 p = floor( x );
    vec3 f = fract( x );

	float id = 0.0;
    vec2 res = vec2( 100.0 );
    for( int k=-1; k<=1; k++ )
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec3 b = vec3( float(i), float(j), float(k) );
        vec3 r = vec3( b ) - f + hash( p + b );
        float d = dot( r, r );

        if( d < res.x )
        {
			id = dot( p+b, vec3(1.0,57.0,113.0 ) );
            res = vec2( d, res.x );			
        }
        else if( d < res.y )
        {
            res.y = d;
        }
    }

    return vec3( sqrt( res ), abs(id) );
}

////////////////////////////////////////////////////////////////
// Signed Distance Functions
////////////////////////////////////////////////////////////////
// From or adapted from iq: https://iquilezles.org/articles/distfunctions/

float sdPlane( vec3 p, vec3 n, float h )
{
  return dot(p,normalize(n)) + h;
}

float sdSphere( vec3 p, vec3 c, float r )
{
    return length(p-c) - r;
}

float sdEllipsoid( vec3 po, vec3 c, vec3 r )
{
    vec3 p = po-c;

    float k0 = length(p/r);
    float k1 = length(p/(r*r));
    return k0*(k0-1.0)/k1;
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float sdRoundedCylinder( vec3 p, float ra, float rb, float h )
{
  vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
  return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}

////////////////////////////////////////////////////////////////
// SDF Operations
////////////////////////////////////////////////////////////////
// Also from or adapted from iq: https://iquilezles.org/articles/distfunctions/

float opUnion(float d1, float d2 ) { return min(d1,d2); }

float opSubtraction(float d1, float d2) {
    //NOTE: Flipped order because it makes more sense to me
    return max(-d2, d1);
}
float opIntersection(float d1, float d2) {
    return max(d1, d2);
}

float opSmoothUnion(float d1, float d2, float k) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}
float opSmoothSubtraction(float d1, float d2, float k) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d1, -d2, h ) + k*h*(1.0-h);
}
float opSmoothIntersection(float d1, float d2, float k){
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h);
}

////////////////////////////////////////////////////////////////
// Rotations
////////////////////////////////////////////////////////////////

vec3 rotX(vec3 p, float a) {
    float s = sin(a);
    float c = cos(a);
    mat3 m = mat3(
        1., 0., 0.,
        0., c, -s,
        0., s, c
        );
    return m * p;
}

vec3 rotY(vec3 p, float a) {
    float s = sin(a);
    float c = cos(a);
    mat3 m = mat3(
        c, 0., s,
        0., 1., 0.,
        -s, 0., c
        );
    return m * p;
}

vec3 orbitControls(vec3 po) {
    vec2 m = (vec2(iMouse.x, iMouse.y) / iResolution.xy) + 0.5;
    vec3 p = po;
    p = rotY(po, -(m.x+1.)*TAU + PI * 0.95);
    return p;
}


////////////////////////////////////////////////////////////////
// Ray Marching Functions
////////////////////////////////////////////////////////////////

vec2 map(vec3 po) {

    float f = 0.06; // ribbet frequency
    vec3 p = orbitControls(vec3(po.x, po.y, po.z)); // track mouse
    vec3 pSym = vec3(abs(p.x),p.y,p.z);

    vec2 res = vec2(1.0);

    // ribbet timing
    float w = 1.5;
    float rt = sin(w * iTime);
    rt = max(rt, sin(w * iTime + PI/3.));
    rt = rt * 0.5 + 1.;

    // Body
    float body = sdEllipsoid(p, vec3(0.), vec3(0.7, 0.5, 0.7) * 1. + (0.025 * rt));
    body = opSmoothUnion(body,sdEllipsoid(p, vec3(0.,-0.1,0.025), vec3(0.8, 0.5, 0.75)), 0.1);

    // Head
    vec3 pr = rotX(p+vec3(0., -0.25, 0.4), -PI/3.);
    float head = sdEllipsoid(pr, vec3(0.), vec3(0.38, 0.325, 0.4));
    res.x = opSmoothUnion(body, head, 0.3);
    res.x += 0.0 * sin(50.*head) * smoothstep(1.,0.,5.*head);

    // eyelid
    float lid = sdEllipsoid(pSym, vec3(0.24,0.444 + (0.01 * rt), -0.525), vec3(0.16, 0.14, 0.16));
    res.x = opSmoothUnion(res.x, lid, 0.04);

    // eye
    float eye = sdSphere(pSym, vec3(0.27,0.43 + (0.012 * rt),-0.58),0.115);
    res.x -= 0.02 * sin(60.*eye) * smoothstep(1.,0.,20.*eye);
    if (eye-0.005 < res.x) {res.y = 2.0;};
    res.x = opSmoothUnion(res.x, eye, 0.006);

    //nose
    float nose = sdSphere(p, vec3(0.,0.45,-0.67),0.01);
    res.x = opSmoothUnion(res.x, nose, 0.25);


    // mouth/face
    pr = rotX(p+vec3(0., -0.12, 0.655), -0.35);
    float face = sdEllipsoid(pr, vec3(0.), vec3(0.27, 0.3, 0.1));
    float faceBool = sdEllipsoid(pr+vec3(0.,0. + (0.032 * rt),0.02), vec3(0.), vec3(0.26, 0.4, 0.04));
    res.x = opSmoothSubtraction(res.x, faceBool, 0.02);
    res.x = opSmoothUnion(res.x, face, 0.01);

    // Ribbet
    float rb = sdSphere(p, vec3(0.,0.0,-0.6),0.16);
    res.x = opSmoothUnion(res.x,rb,0.2 * rt);

    // Arms
    vec3 elbow = vec3(0.62,-0.3,-0.4);
    vec3 shoulder = vec3(0.5,-0.1,-0.4);
    vec3 wrist = vec3(0.57,-0.45,-0.45);
    float upperArm = sdCapsule(pSym, shoulder,elbow, 0.08);
    res.x = opSmoothUnion(res.x, upperArm, 0.1);
    res.x += 0.01 * sin(40.*upperArm) * smoothstep(1.,0.,5.*upperArm);
    float foreArm = sdCapsule(pSym, elbow+vec3(0.,-0.05,0.), wrist, 0.08);
    res.x = opSmoothUnion(res.x, foreArm, 0.05);

    // Fingers
    float f1 = sdCapsule(pSym, wrist-vec3(0.,0.03,0.03), wrist-vec3(0.15,0.02,0.005), 0.04);
    float f2 = sdCapsule(pSym, wrist-vec3(0.,0.03,0.03), wrist-vec3(0.2,0.04,0.06), 0.04);
    float f3 = sdCapsule(pSym, wrist-vec3(0.,0.03,0.03), wrist-vec3(0.15,0.05,0.12), 0.04);

    res.x = opSmoothUnion(res.x, f1, 0.04);
    res.x = opSmoothUnion(res.x, f2, 0.04);
    res.x = opSmoothUnion(res.x, f3, 0.04);

    // Legs
    vec3 knee = vec3(0.55,-0.45,0.425);
    float upperLeg = sdCapsule(pSym, vec3(0.4,-0.2,.475),knee, 0.07);
    res.x = opSmoothUnion(res.x, upperLeg, 0.1);
    res.x += 0.01 * sin(40.*upperLeg) * smoothstep(1.,0.,5.*upperLeg);

    // Butt
    float c = sdSphere(pSym, vec3(0.15,-0.15,.65),0.115);
    res.x = opSmoothUnion(res.x, c, 0.15);

    // Toes
    float t1 = sdCapsule(pSym, knee-vec3(0.,0.03,0.), knee-vec3(-0.18, 0.05,0.12), 0.04);
    float t2 = sdCapsule(pSym, knee-vec3(0.,0.03,0.), knee-vec3(-0.13, 0.05,0.18), 0.04);
    float t3 = sdCapsule(pSym, knee-vec3(0.,0.03,0.), knee-vec3(-0.06, 0.05,0.18), 0.04);

    res.x = opSmoothUnion(res.x, t1, 0.04);
    res.x = opSmoothUnion(res.x, t2, 0.04);
    res.x = opSmoothUnion(res.x, t3, 0.04);

    // Ground
    float gnd = sdRoundedCylinder(po+vec3(0.,0.78,0.) , 0.8, 0.1, 0.15);
    if (gnd-0.005 < res.x) {res.y = 3.0;};
    res.x = opUnion(res.x, gnd);    

    return res;
}

vec2 rayMarch(vec3 ro, vec3 rd) {
    float d = 0.0;
    float mat = 1.0;
    for (int i = 0; i < MAX_STEPS; i++) {
        if (i >= MAX_STEPS ) break;
        vec3 p = ro + rd * d;
        vec2 ds = map(p);
        d += ds.x;
        mat = ds.y;
        if (d >= MAX_DIST || ds.x < SURF_DIST) break;
        i++;
    }
    return vec2(d, mat);
}

// from iq: https://iquilezles.org/articles/rmshadows/
float softShadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k )
{
    float res = 1.0;
    float t = mint;
    for( int i=0; i<256 && t<maxt; i++ )
    {
        float h = map(ro + rd*t).x;
        if( h<0.001 )
            return 0.0;
        res = min( res, k*h/t );
        t += h;
    }
    return res;
}


vec3 gradient(vec3 p) {
    float epsilon = 0.0001;
    vec3 dx = vec3(epsilon, 0., 0.0);
    vec3 dy = vec3(0., epsilon, 0.0);
    vec3 dz = vec3(0., 0.0, epsilon);

    float ddx = map(p + dx).x - map(p - dx).x;
    float ddy = map(p + dy).x - map(p - dy).x;
    float ddz = map(p + dz).x - map(p - dz).x;

    return normalize(vec3(ddx, ddy, ddz));
}

vec3 rayDirection(vec2 p, vec3 ro, vec3 rt) {

    // screen orientation
    vec3 vup = vec3(0., 1.0, 0.0);
    float aspectRatio = iResolution.x / iResolution.y;

    // camera orientation from https://raytracing.github.io/books/RayTracingInOneWeekend.html
    vec3 vw = normalize(ro - rt);
    vec3 vu = normalize(cross(vup, vw));
    vec3 vv = cross(vw, vu);
    float theta = radians(30.); // half FOV
    float viewport_height = 2. * tan(theta);
    float viewport_width = aspectRatio * viewport_height;
    vec3 horizontal = -viewport_width * vu;
    vec3 vertical = viewport_height * vv;
    float focus_dist = length(ro - rt);
    vec3 center = ro - vw * focus_dist;

    vec3 rd = center + p.x * horizontal + p.y * vertical - ro;

    return normalize(rd);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord / iResolution.xy - 0.5) * 2.0; // normalizing

    // Ray Marching
    vec3 rt = vec3(-0.4 + 0.01 * sin(0.5*iTime), -0.2, 2.);
    vec3 ro = vec3(-0.4 + 0.1 * cos(0.5*iTime), 0.5, -5.);

    vec3 rd = rayDirection(uv, ro, rt);
    vec2 d = rayMarch(ro, rd);

    // Background
    float v = length(uv) * .75;
    vec3 col = mix(vec3(0.8, 0.8, 0.55), vec3(0.45, 0.55, 0.35), smoothstep(0.0, 1.0, uv.y));
    fragColor = vec4(col, 1.);
	fragColor += mix(vec4(0.1), vec4(0.0, 0.15, 0.0, 1.0), v); // vignette
    fragColor *= mix(vec4(0.5,0.6,0.6,1.), vec4(1.), smoothstep(-1.,1.,uv.x)); // darken left

    float vb = voronoi( 2. * vec3(2.,1.,1.) * vec3(uv, 1.)).x;
    fragColor.xyz *= mix(0.7,1.,smoothstep(0.,1.,vb));


    if (d.x <= 100.0) {
        vec3 p = ro + rd * d.x;
        vec3 pr = orbitControls(p);
        vec3 N = gradient(p);

        // Simple surface texture
        if (d.y == 1.) N += voronoi( 50.*pr).x * 0.13;

        // Lighting (partial phong shading)
        vec3 lightPos1 = vec3(1, 1,-1);
        float light1 = dot(N, normalize(lightPos1))*.5+.5;
        light1 *= 1.5;
        vec3 L1 = vec3(1, 1,-1);
        float light2 = dot(N, normalize(vec3(-1, 1,-1)))*.5+.5;
        vec3 lightVal = 0.5 * vec3(light1) + 0.5 * vec3(light2);

        // Specular highlights
        vec3 R = reflect(L1, N);
        vec3 specular = vec3(1.0) * pow(max(dot(R, rd), 0.0),10.0);
        vec3 color;

        // Color assignment
        // Color assignment method adapted from iq: https://www.youtube.com/watch?v=Cfe5UQ-1L9Q&t=8470s
        // A breakdown in my other shader: https://www.shadertoy.com/view/cdlBDl
        if (d.y == 1.) color = vec3(0.3, 0.5, 0.2) + specular * 0.0006; // body
        if (d.y == 2.) color = vec3(0.05) + specular * 0.004; // eyes
        if (d.y == 3.) color = vec3(0.15, 0.25, 0.1);

        // Skin colors
        float c = 1. - voronoi( 10.*pr).x;
        // blended based on distance from a point above the frog
        float tFac = mix(0., 1., smoothstep(0.,1., length(p-vec3(0.,0.7,0.5))-0.5));
        color = mix(color, vec3(0.16, 0.16, 0.1), c * (1.-tFac));

        // shadows
        float res = softShadow(p+N*0.01, normalize(lightPos1-p), 0.01, 5., 2.);

        if (p.y <= -0.525) {
            float res2 = softShadow(p+N*0.01, vec3(0.,1.,0.), 0.01, 5., 2.);
            color *= 1.0 - mix(0.4,0.,smoothstep(0.,1., res2));;
        }

        color *= 1.0 - mix(0.3,0.,smoothstep(0.,1., res));

        // fake fresnel
        float nDotV = dot(N, rd) + 1.;
        color += nDotV * nDotV * 0.35 * res;


        fragColor = vec4(lightVal*color, 1.0);
    }


}

float circle_distance(vec2 point, float radius){
    return length(point) - radius;
}


void mainImage( out vec4 frag_color, in vec2 frag_coord ){

    vec2 uv = (frag_coord * 2.0 - iResolution.xy) / iResolution.y;
    vec3 final_color = vec3(0.0);

    vec2 fract_uv = uv * 2.;

    for (int i = 0; i < 11; i++){
        fract_uv = fract(fract_uv);
        fract_uv -= 0.5;

        float circle = circle_distance(fract_uv, 0.5) * exp(-length(uv));
        circle = sin(circle * 8. - iTime) / 8.;
        circle = abs(circle);
        circle = smoothstep(0., 0.05, circle);
        circle = 0.01 / circle;
        final_color += circle;
    }

    frag_color = vec4(final_color, 1);

}

float circle(in vec2 p) {
    p *= 1.2;
    // distance^2 is fine, doesn't need to be actual distance.
    float left = dot(p, p);
    float right = 1.0;
    return right - left;
}

float sine(in vec2 p) {
    p *= 5.0;
    float left  = sin(p.x + iTime * 1.5);
    float right = p.y;
    return right - left;
}

float lemniscate(in vec2 p) {

    float left = p.x * p.x + p.y * p.y;
    left = left * left;
    float right = p.x * p.x - p.y * p.y;
    return right - left;
}

float folium(in vec2 p) {
    p *= 4.0;
    float left = p.x * p.x * p.x + p.y * p.y * p.y;
    float right = 6.0 * p.x * p.y;
    return right - left;
}

float witchOfAgnesi(in vec2 p) {
    p *= 8.0;
    float left = (p.x * p.x + 2.0) * p.y;
    float right = 10.0;
    return right - left;
}

float blah(in vec2 p) {
    p *= 20.0;
    float left = p.x * cos(p.y) + p.y * cos(p.x);
    float right = 0.0;
    return right - left;
}


// gPlatl
float parabel(in vec2 p) 
{ return p.x*p.x-p.y-0.8; }


// gPlatl
float Heart(in vec2 p) 
{ float x = p.x;
  float y = p.y;
  float a = x*x+y*y-0.5;
  return a*a*a-x*x*y*y*y;
}

// gPlatl
float MickeyMouse(in vec2 p) 
{ float x = p.x;
  float y = p.y;
  float a = x*x+y*y-0.5;
  return a*a*a-x*x*y*y*y+0.01;
}


// Envy24
float devil_curve(in vec2 p)
{
    p *= 2.;
    float x = p.x, y = p.y, t0 = (1.+sin(iTime))*0.5;
    return y * y * (y * y - 1.0) - x * x * (x * x - 1.65 + t0);
}

// Envy24
float some_random_func(in vec2 p)
{
    p *= 4.;
    float x = p.x, y = p.y;
    return (x*x - x*y*x) -(y*y - x*y*x)/(y*y + y*x*x) + sin(iTime)*1.0;
}

// Envy24
float gamma_correction_heart(in vec2 p)
{
    p *= 2.;
    float x = p.x, y = p.y;
    //               gamma correction of x*x)
    float term = y - pow(x * x, 1.0 / 2.2);
    return x * x + term * term - 1.0;
}

// Envy24
float unknown(in vec2 p) {
       p = (p - vec2(0.59)) * 8.0;
    float sx = sin(2.*p.x), sy = sin(p.y);    
    return sx*sx*sx + 4.*sy*sy*sy - 3.*sx*sy;
}

const int numCurves = 12;
float curve(in vec2 p, int index) {
    switch (index) {
        case 0:
            return circle(p);
        case 1:
            return lemniscate(p);
        case 2:
            return folium(p);
        case 3:
            return witchOfAgnesi(p);
        case 4:
            return sine(p);
        case 5:
            return blah(p);
        case 6:
            return parabel(p);
        case 7:
            return Heart(p);
        case 8:
            return gamma_correction_heart(p);
        case 9:
            return MickeyMouse(p);
        case 10:
            return devil_curve(p);
        case 11:
            return some_random_func(p);

        default:
            return 0.0;
    }
}

float curve(in vec2 p) {

    float t = iTime * 0.2;

    int i0 = int(floor(t)) % numCurves;
    int i1 = (i0 + 1)      % numCurves;

    float c0 = curve(p, i0);    
    float c1 = curve(p, i1);

    float c = mix(c1, c0, smoothstep(-0.4, 0.4, cos(fract(t) * 3.1415)));

    return c;
}

// thanks Envy24!
float fwidth_L2(in float d) {
    return length(vec2(dFdx(d), dFdy(d)));
}

void mainImage( out vec4 RGBA, in vec2 XY )
{
    vec2 xy = (XY - iResolution.xy / 2.0)/min(iResolution.x, iResolution.y) * 2.0;

    float d;

    // grey background
    float c = 0.5;

    // axes
    ivec2 dxy = ivec2(XY) - ivec2(iResolution.xy / 2.0);
    if (/*dxy.x * */ dxy.y == 0) {
        c *= 1.0  + -0.8* smoothstep(0.5, 0.6, cos(length(xy * 100.0)));
    }

    // shadow
    d = curve(xy - 0.015 * vec2(1.0, -1.0));
    c *= mix(1.0, 0.8, smoothstep(8.0, 0.0, abs(d) / fwidth_L2(d) - 4.0));

    // main curve
    d = curve(xy - 0.00 * vec2(1.0, -1.0));
    c = mix(c, 1.0, smoothstep(2.0, 0.0, abs(d) / fwidth_L2(d) - 1.0));

    RGBA.rgb = vec3(c);
    RGBA.a   = 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ix = texelFetch(iChannel1, ivec2(0,0), 0 ).x;
    int mono = int(texelFetch(iChannel1, ivec2(3,0), 0 ).x);
    int aa = 1-int(texelFetch(iChannel1, ivec2(5,0), 0 ).x);
    vec2 ppos = texelFetch(iChannel1, ivec2(10,0), 0 ).xy;

    float mx= iMouse.x / iResolution.x;
    vec2 mid = iResolution.xy / 2.;
    float mmul = 1.-mx;
    fragCoord = mid - (mid*mmul-fragCoord*mmul) + ppos;

    vec4 val = vec4(0.);
    int am=0, ap=aa, j=0;
    for (int j = -am; j <= ap; j++)
        for (int i = -am; i <= ap; i++)
            val += texelFetch( iChannel0, ivec2(int(fragCoord.x)+i,int(fragCoord.y)+j), 0 );
    float n=float((am+ap+1));
    if (am+ap > 0) val /= n*n-2.;

    if (aa == 0) val/=0.6;

    if (mono == 0) {
        fragColor = val;
    } else {
        float v=(1./liveval)*val.w;
        fragColor = vec4( v*0.9, v*0.95, v, 1.0 );
    }
}

buffer_a_code:
// Cellular automata buffer

const int KEY_SPACE = 32;
const int KEY_ENTER = 13;
const int KEY_LEFT  = 37;
const int KEY_RIGHT = 39;
const int KEY_UP    = 38;
const int KEY_DOWN  = 40;
const int KEY_1     = 49;
const int KEY_2     = 50;
const int KEY_3     = 51;
const int KEY_4     = 52;
const int KEY_5     = 53;
const int KEY_Z     = 90;
const int KEY_X     = 88;

bool readKey( int key )
{
	float keyVal = texture( iChannel1, vec2( (float(key)+.5)/256.0, .25 ) ).x;
	return (keyVal>.5)?true:false;
}

int cell( in ivec2 p )
{
    ivec2 r = ivec2(textureSize(iChannel0, 0));
    p = (p+r) % r;
    float val = texelFetch(iChannel0, p, 0 ).w;
    return ( val == liveval ) ? 1 : 0;
}
vec4 cellval( in ivec2 p )
{
    ivec2 r = ivec2(textureSize(iChannel0, 0));
    p = (p+r) % r;
    return texelFetch(iChannel0, p, 0 );
}

float randpix(vec2 fragCoord) {
    float rn = hash1(iTime+fragCoord.x*13.0+hash1(fragCoord.y*71.1));
    return clamp(step(1.01-density/100., rn), 0., 1.) * (liveval);
}

float restart(vec2 fragCoord, int method, bool preserve) {
    if (method == 0)
        return randpix(fragCoord);

    vec2 mid = iResolution.xy / 2.; 
    float w=100., h=100., radius=452., rradius=25.;
    if (iResolution.y < 1000.) radius=182.;
    float xrad=569.,yrad=453.;
    float thick=2., wl=w-thick, hl=h-thick;

    if (method == 1) {
        if (length(fragCoord-mid)< radius && length(fragCoord-mid) > (radius-thick))
            return liveval;
    }
    if (method == 2) {
        if (((fragCoord.x > mid.x - w && fragCoord.x < mid.x - wl) || (fragCoord.x > mid.x + wl && fragCoord.x < mid.x + w))
            && fragCoord.y > mid.y - h && fragCoord.y < mid.y + h)
            return liveval;
        if (((fragCoord.y > mid.y - h && fragCoord.y < mid.y - hl) || (fragCoord.y > mid.y + hl && fragCoord.y < mid.y + h))
            && fragCoord.x > mid.x - w && fragCoord.x < mid.x + w)
            return liveval;
    }
    if (method == 3) {
        if (length(vec2((fragCoord.x-mid.x)*0.793,(fragCoord.y-mid.y)*1.0))< radius)
            return liveval;
    }
    if (method == 4) {
        float xmul=1.; if (iResolution.y < 1000.) xmul=1.75;
        if (fragCoord.x > mid.x - rradius*xmul && fragCoord.x < mid.x + rradius*xmul+1. && fragCoord.y > mid.y - rradius && fragCoord.y < mid.y + rradius +1.)
            return liveval;
    }
    return preserve? -1. : 0.;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ix = texelFetch(iChannel2, ivec2(0,0), 0 ).x;
    float xres = texelFetch(iChannel2, ivec2(0,1), 0 ).x;
    int method = int(texelFetch(iChannel2, ivec2(1,0), 0 ).x);
    int speed = int(texelFetch(iChannel2, ivec2(2,0), 0 ).x);
    int mono = int(texelFetch(iChannel2, ivec2(3,0), 0 ).x);
    int rows = 2+ int(texelFetch(iChannel2, ivec2(4,0), 0 ).x);

    float xsq = 3., ysq = float(rows);
    float wsq = iResolution.x / xsq;
    float hsq = iResolution.y / ysq;
    float thick = 3.;

    int cix = (int(fragCoord.x / wsq) + int(fragCoord.y / hsq)*int(xsq) + int(ix*10.)) % 9;
    setRules(cix, vec3(0.));
    if (cix==3 && iResolution.y < 400.) { rp*=1.8; gp*=1.8; bp*=1.8; } 

    float dt=density; if (density2 >= 0.) density=density2; if (cix==0 && iResolution.y > 400.) density=0.;
    float i, bsh=1.;
    for (i=0.; i<=xsq; i++) {
        if (fragCoord.x > wsq*i-thick && fragCoord.x < wsq*i+thick ) {
            fragColor = vec4(bsh,bsh,bsh,randpix(fragCoord));
            return;
        }
    }
    for (i=0.; i<=ysq; i++) {
        if (fragCoord.y > hsq*i-thick && fragCoord.y < hsq*i+thick ) {
            fragColor = vec4(bsh,bsh,bsh,randpix(fragCoord));
            return;
        }
    }
    density=dt;

    ivec2 px = ivec2( fragCoord );
    vec4 curr = cellval(px);
    float ev = curr.w;

    if( iFrame==0 || readKey(KEY_SPACE) || readKey(KEY_ENTER) || readKey(KEY_Z) || readKey(KEY_X) || int(xres) != int(iResolution.x) ) {
        fragColor = vec4( 0.0, 0.0, 0.0, restart(fragCoord, setmethod, false));
        return;
    }

    if( readKey(KEY_1) || readKey(KEY_2) || readKey(KEY_3) || readKey(KEY_4) || readKey(KEY_5) ) {
        fragColor = vec4( 0.0, 0.0, 0.0, restart(fragCoord, method, false));
        return;
    }

    if (iFrame % (speed+1) > 0) {
        fragColor = curr;
        return;
    }

    int k=0;

    if (nh == 0) {
        // ..X..
        // .X.X.
        // X.*.X
        // .X.X.
        // ..X..
        k =   cell(px+ivec2(0,-2)) + cell(px+ivec2(-1,-1)) + cell(px+ivec2(1,-1))
            + cell(px+ivec2(-2, 0))                        + cell(px+ivec2(2, 0))
            + cell(px+ivec2(-1, 1)) + cell(px+ivec2(1, 1)) + cell(px+ivec2(0, 2));
    } else if (nh == 4 || nh==5 || nh == 6) {
        // .XXX. 4
        // XXXXX
        // XX*XX
        // XXXXX
        // .XXX.
        k =   cell(px+ivec2(-1, 2)) + cell(px+ivec2(0, 2)) + cell(px+ivec2(1, 2))
            + cell(px+ivec2(-1, -2)) + cell(px+ivec2(0, -2)) + cell(px+ivec2(1, -2))
            + cell(px+ivec2(-2, -1)) + cell(px+ivec2(-2, 0))
            + cell(px+ivec2(-2, 1))
            + cell(px+ivec2(2, -1)) + cell(px+ivec2(2, 0))
            + cell(px+ivec2(2, 1));
        // XXXXX 6
        // X...X
        // X.*.X
        // X...X
        // XXXXX
        if (nh > 4) k += cell(px+ivec2(2, 2)) + cell(px+ivec2(-2, -2)) + cell(px+ivec2(-2, 2)) + cell(px+ivec2(2, -2));
        // XXXXX 5
        // XXXXX
        // XX*XX
        // XXXXX
        // XXXXX
        if (nh < 6)
            k += cell(px+ivec2(0,-1))
            + cell(px+ivec2(-1, 0)) + cell(px+ivec2(1, 0))
            + cell(px+ivec2(0, 1))  + cell(px+ivec2(-1,-1)) + cell(px+ivec2(1,-1))
            + cell(px+ivec2(-1, 1)) + cell(px+ivec2(1, 1));
    } else if (nh == 10) {
        // XXXXX
        // ..X..
        // ..*..
        // ..X..
        // XXXXX    
        k =   cell(px+ivec2(0,-1)) + cell(px+ivec2(0,1)) +
            + cell(px+ivec2(-2, -2)) + cell(px+ivec2(-1, -2)) + cell(px+ivec2(0, -2)) + cell(px+ivec2(1, -2)) + cell(px+ivec2(2, -2))
            + cell(px+ivec2(-2, 2)) + cell(px+ivec2(-1, 2)) + cell(px+ivec2(0, 2)) + cell(px+ivec2(1, 2)) + cell(px+ivec2(2, 2));
    } else {
        // XXX
        // X*X
        // XXX
        k =   cell(px+ivec2(-1,-1)) + cell(px+ivec2(0,-1)) + cell(px+ivec2(1,-1))
            + cell(px+ivec2(-1, 0))                        + cell(px+ivec2(1, 0))
            + cell(px+ivec2(-1, 1)) + cell(px+ivec2(0, 1)) + cell(px+ivec2(1, 1));
    }

    float ff = 0.;
    if (ev > 0.5) {
        if (decimate > 0.) ff = ev-decimate;
        if ((stayset & (1<<(k-1))) > 0 ) { ff = float(k); if (clampstay && ff > liveval) ff = liveval; }
    }
    else {
        ff = (bornset & (1<<(k-1))) > 0 ? liveval : 0.;
    }

    if (mono == 0) {
        if (ff >= 1.0) {
            int st = int(ff);

            float mulbase = float(k);
            if (colch == 1) mulbase = ff;
            else if (colch == 2) mulbase = 1.;
            else if (colch == 3) mulbase = float(k^st);

            if ((st & ra) > 0) curr.x += cstep*mulbase*rp;
            if ((st & ga) > 0) curr.y += cstep*mulbase*gp;
            if ((st & ba) > 0) curr.z += cstep*mulbase*bp;

            if (staypatt == 5 || staypatt == 6) curr.z=curr.y=curr.x;	

        } else {
            float stayt = cstep * stayval;

            if (staypatt == 0 || staypatt > 5) {
                curr.x -= cstep*rm;
                curr.y -= cstep*gm;
                curr.z -= cstep*bm;
            }
            else if (staypatt == 1) {
                if(curr.x > cstep) {
                    curr.x -= cstep*rm;
                    curr.y -= cstep*gm;
                    curr.z -= cstep*bm;
                }
            }
            else if (staypatt == 2) {
                if(curr.y > cstep) {
