                if(curr.z > cstep) {
                    curr.x -= cstep*rm;
                    curr.y -= cstep*gm;
                    curr.z -= cstep*bm;
                }
            }
            else if (staypatt == 4) {
                if(curr.z > cstep) {
                    curr.x -= cstep*rm;
                    curr.y -= cstep*gm;
                }
                if(curr.x > cstep) curr.z -= cstep*bm;
            }
            else if (staypatt == 5) {
                curr.x -= cstep*rm;
                curr.z=curr.y=curr.x;
            }
        }
    }

	fragColor = vec4( clamp(curr.xyz,0.,1.), ff );
}

