const vec2 OFFSET = vec2(69.0, 420.0);
const float SCALE = 3.0;
const float AMPLITUDE = 0.3;
const int OCTAVES = 4;
const float LACUNARITY = 1.2;
const float GAIN = 0.9;

float fbm(in vec3 q) {
    float value = 0.0;
    float amplitude = AMPLITUDE;

    for (int i = 0; i < OCTAVES; i++) {
        float n = cnoise(q);
        value += amplitude * n;
        q.xy *= LACUNARITY;
        amplitude *= GAIN;
    }
    return value;
}

float getNoise(in vec2 uv) {
    float t = 10.0 * fbm(vec3(uv, 5.0 * fbm(vec3(uv, 0.5 * iTime))));
    vec3 q = vec3(uv.xy, t);
    return fbm(q);
}

vec3 gamma(in vec3 color)
{
    return pow(max(color, 0.0), vec3(1.0 / 2.2));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = 2.0 * (gl_FragCoord.xy / iResolution.xy) - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec2 p = SCALE * uv + OFFSET;
    float n = getNoise(p);
    vec3 color = vec3(n);
    color = gamma(color);

    fragColor = vec4(color, 1.0);
}

