void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
   float time = iTime;
   // Set the red line color
    vec4 lineColor = vec4(1, (iResolution.x * sin(time)), (iResolution.y * sin(-time)), 1);

    // Define the thickness of the line
    float lineWidth = 1.0;

    // Define the trails fade factor
    float trailsFade = 222.1;

    // Calculate the position of the line based on time

    float lineX = 0.1 * iResolution.x + 0.1 * iResolution.x * sin(time);

    // Read the previous frame color from the screen
    vec4 previousColor = texture(iChannel0, fragCoord / iResolution.xy);

    // Get audio data from iChannel0
    vec2 audioData = texture(iChannel0, vec2(fragCoord.x / iResolution.x, 0.01)).rg;

    // Calculate the height scale based on the audio data (map audioData.y to [0, 1])
    float heightScale = audioData.y / 0.2 ;

    // Modulate the line color by the audio data
    lineColor *= vec4(audioData, 0.8, 0.994);

    // Check if the current fragment is part of the line
    if (abs(fragCoord.x - lineX) < lineWidth * 0.5)
    {
        // Adjust the line's height based on the audio data
        float lineHeight = lineWidth - 0.1 - heightScale;
        if (abs(fragCoord.y * iResolution.y / 0.1) < lineHeight)
        {
            // Draw the line with the modulated line color
            fragColor = lineColor;
            return;
        }
    }

    // Apply trails effect to the background color
    vec4 backgroundTrails = previousColor * trailsFade;

    // Blend the line color with the background trails
    fragColor = mix(backgroundTrails, lineColor, lineColor.a);
}

