float PI = 3.14159265358979323846;
float TWOPI = 6.28318530718;
float PIONTWO = 1.57079632679;
float PIONFOUR = 0.78539816339;
float E = 2.71828182846;

float atan2(float y, float x)
{
    //this computes arctan(y/x)
    float angle = atan(y,x);
    if(angle < 0.0){angle+= TWOPI;}
    return(angle);
}

vec2 times(vec2 a , vec2 b)
{
    return( vec2(a.x*b.x - a.y*b.y , a.x*b.y + a.y*b.x) );
}

vec2 itimes(vec2 uv)
{
    return( vec2(-uv.y,uv.x) );
}

float norm(vec2 uv)
{
    return( uv.x*uv.x + uv.y*uv.y );
}

vec2 oneover(vec2 uv)
{
    return( vec2(uv.x , -uv.y)/norm(uv) );
}

vec2 square(vec2 uv)
{
    return( vec2(uv.x*uv.x - uv.y*uv.y, 2.0*uv.x*uv.y) );
}

vec2 cube(vec2 uv)
{
    float x2 = uv.x*uv.x ; float y2 = uv.y*uv.y;
    return( vec2(x2*uv.x - 3.0*uv.x*y2 , 3.0*x2*uv.y - y2*uv.y) );
}

vec2 quad(vec2 uv)
{
    float x2 = uv.x*uv.x ; float y2 = uv.y*uv.y; float xy = uv.x*uv.y;
    return( vec2(x2*x2 - 6.0*x2*y2 + y2*y2 , 4.0*xy*(x2 - y2) ) );
}

vec2 exp2d(vec2 uv)
{
    return( exp(uv.x)*vec2(cos(uv.y),sin(uv.y)));
}

vec2 log2d(vec2 uv)
{
    return(vec2(log(length(uv)),atan(uv.y,uv.x)));
}

vec2 cos2d(vec2 uv)
{
    return ( vec2 (exp2d(itimes(uv)) + exp2d(itimes(-uv)))*0.5);
}

vec2 sin2d(vec2 uv)
{
    return ( vec2 (-itimes((exp2d(itimes(uv)) - exp2d(itimes(-uv)))*0.5)));
}

vec2 cosh2(vec2 uv)
{
    float expo = exp(uv.x); float co = cos(uv.y); float si = sin(uv.y);
    return ( (expo*vec2(co,si) + exp(-uv.x)*vec2(co,-si))*0.5 );
}

vec2 sinh2(vec2 uv)
{
    float expo = exp(uv.x); float co = cos(uv.y); float si = sin(uv.y);
    return ( (expo*vec2(co,si) - exp(-uv.x)*vec2(co,-si))*0.5 );
}

vec2 tanh2(vec2 uv)
{
    vec2 expo = exp2d(2.0*uv);
    return( vec2(expo - 1.0)/(expo + 1.0) );
}

vec2 rotate(vec2 uv,float angle)
{
    float co = cos(angle);float si = sin(angle);
    uv *= mat2(co,-si,si,co);
    return(uv);
}

vec2 power(vec2 uv, float n)
{
    float modulus = length(uv);
    float angle = atan2(uv.y,uv.x);
    angle *= n;

    return( exp(n*log(modulus))*vec2(cos(angle),sin(angle)));
}

vec2 zpowlnz(vec2 uv)
{
    float modulus = length(uv);
    float angle = atan2(uv.y,uv.x);
    float LOG = log(modulus);
    float value = 2.0*angle*LOG;
    vec2 result = vec2(cos(value),sin(value))*exp(-angle*angle + LOG*LOG);
    return(result);
}

