}

vec4 threeLayerColoring( float t ) {
    float iterationAmount = t;

    float scale = 0.5;
    float periodFast = 10.0*scale;
    float residueFast = mod(iterationAmount,periodFast);
    float indexFast = float(residueFast)/float(periodFast);
    vec3 colorFast = palette3(indexFast);

    float periodMedium = 20.0*scale;
    float residueMedium = mod(iterationAmount,periodMedium);
    float indexMedium = float(residueMedium)/float(periodMedium);
    vec3 colorMedium = palette3(indexMedium);

    float periodSlow = 100.0*scale;
    float residueSlow = mod(iterationAmount,periodSlow);
    float indexSlow = float(residueSlow)/float(periodSlow);
    vec3 colorSlow = palette3(indexSlow);

    float finalIndex = (indexFast + indexMedium + indexSlow)/3.;
    vec3 finalColor = (colorSlow + colorMedium + colorFast)/3.;

    return(vec4(finalColor, finalIndex));
}

float logplus(float t)
