    float tol = pow(10.0,-10.0);
    float DistanceToPrevious = 1.0;
    float currentModulus = length(uv);
    vec2 previousIteration;
    float iterationAmount;
    int testAmount = 5000;
    float bailout = pow(10.0,2.0);

    vec2 dummyuv = uv; vec2 dz = vec2(1.0,0.0);
    int i;

    for (i = 0; i < testAmount; i++)
    {
        previousIteration = dummyuv;
        dz = times(dz, 2.0*dummyuv) + vec2(1.0,0.0);
        dummyuv = iteration(dummyuv,c);

        currentModulus = norm(dummyuv);
        DistanceToPrevious = norm(dummyuv - previousIteration);

        if(DistanceToPrevious < tol){break;}
        if(currentModulus > bailout)
        {
            break;
        } 
    }
    iterationAmount = float(i);

    //Coloring the interior of the set

    if (currentModulus < bailout)
    {

        fragColor = vec4(0.0,0.0,0.0,1.0);
    }
    //Coloring the exterior of the set

    else
    {
        int colorType = 3; // 0 = usual , 1 = log-potential, 2 = (log-potential) + normal map;
        //3 = distance estimation + normal map + log-potential (mu)

        if (colorType == 0){
            iterationAmount = 4.0*log(iterationAmount+1.0)+2.0;
            float i = iterationAmount+1.0;
            vec3 color1; float finalIndex; vec4 result = threeLayerColoring(iterationAmount);
            color1 = result.rgb; finalIndex = result.t;
