            vec3 color1 = threeLayerColoring(iterationAmount).rgb;
            vec3 color2 = threeLayerColoring(iterationAmount+1.0).rgb;
            vec3 finalColor = mix(color1,color2,0.5+0.5*sin(iterationAmount));
            if(nu > 1.0){finalColor = vec3(0.0);}

            fragColor = vec4(finalColor,1.0);
        }

        if (colorType == 2){
            float height = 1.5; float lightAngle = PIONFOUR; 
            vec2 lightVector = vec2(cos(lightAngle),sin(lightAngle));
            vec2 u = times(dummyuv,oneover(dz)); u = u/length(u);
