            vec3 finalColor = vec3( estimation );

            fragColor = vec4(finalColor*intensity,1.0);
        }

    }


}

const vec2 OFFSET = vec2(69.0, 420.0);
const float SCALE = 1.0;
const float AMPLITUDE = 1.5;
const int OCTAVES = 6;
const float LACUNARITY = 1.9;
const float GAIN = 1.2;

float fbm(in vec2 p) {
    float t = iTime / 2.0;
    float value = 0.0;
    float amplitude = AMPLITUDE;
    vec3 q = vec3(p.xy, t);

    for (int i = 0; i < OCTAVES; i++) {
        float n = cnoise(q);
        n = 0.005 / n;
        value += amplitude * n;
        q.xy *= LACUNARITY;
        amplitude *= GAIN;
    }
    return value;
}

float getNoise(in vec2 uv) {
    return fbm(uv);
}

vec3 gamma(in vec3 color)
{
    return pow(max(color, 0.0), vec3(1.0 / 2.2));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    vec2 p = SCALE * uv + OFFSET;
    float n = getNoise(p);
    vec3 color = vec3(n);
    color = gamma(color);

    fragColor = vec4(color, 1.0);
}

