vec2 screen_to_world(vec2 coord)
{
    vec2 v = (2.0 * coord - iResolution.xy) / min(iResolution.x, iResolution.y);
    v *= 5.0;
    return v;
}

float sd_scene(vec2 p)
{
    vec2 circ1_pos = vec2(0.0);
    if (iMouse.z > 0.0)
        circ1_pos = screen_to_world(iMouse.xy);

    vec2 circ2_pos = vec2(4.5 + sin(TAU * 0.6 * iTime) * 0.25, cos(TAU * 0.22 * iTime) * 2.0);
    vec2 circ3_pos = vec2(-4.5 - cos(TAU * 0.6 * iTime) * 0.25, cos(TAU * 0.22 * iTime + PI) * 2.0);

    float d = 10000000.0;
    #if 1
    d = min(d, distance(p, circ1_pos) - 1.0);
    d = min(d, distance(p, circ2_pos) - 0.7);
    d = min(d, distance(p, circ3_pos) - 0.7);
    #else
    d = min(d, distance_alt(p, circ1_pos) - 1.0);
    d = min(d, distance_alt(p, circ2_pos) - 0.7);
    d = min(d, distance_alt(p, circ3_pos) - 0.7);
    #endif
    return d;
}

bool march(vec2 ro, vec2 rd)
{
    // Constants
    const int max_steps = 100;
    const float max_dist = 100.0;
    const float hit_thres = 0.005;

    // March
    float sd = 1.0;
    float traveled = 0.0;
    for (int i = 0; i < max_steps; i++)
    {
        // Get SD
        sd = sd_scene(ro);

        // Are we inside an object?
        if (sd < 0.0)
            break;

        // Move forward
        ro += rd * sd;
        traveled += sd;

        // Did we hit something?
        if (sd < hit_thres)
            break;

        // Did we go too far?
        if (traveled > max_dist)
            break;
    }

    return (sd < hit_thres);
}

void mainImage( out vec4 frag_color, in vec2 frag_coord )
{
    // Initialize PRNG
    eprng_init(frag_coord.xy / max(iResolution.x, iResolution.y), iTime);

    // Calculate occlusion
    const int num_angles = 100;
    float v = 0.0;
    for (int i = 0; i < num_angles; i++)
    {
        // Ray origin
        vec2 ro = screen_to_world(frag_coord + vec2(random(), random()) - 0.5);

        // Ray direction
        #if 1
        float angle = TAU * random();
        vec2 rd = vec2(cos(angle), sin(angle));
        #else
        float angle = random();
        vec2 rd = vec2(cos_alt_use_t(angle), sin_alt_use_t(angle));
        #endif

        // March
        if (!march(ro, rd)) v += 1.0;
    }
    v /= float(num_angles);

    // Colorize
    v = clamp(v * v * 1.15, 0.0, 1.0);
    vec3 col = mix(vec3(0.7, 0.03, 0.02), vec3(1.0, 0.95, 0.75), v);

    // Output
    col = pow(col, vec3(1.0 / 2.2));
    frag_color = vec4(col,1.0);
}

