#define PI 3.1415926535897932384626433832795
#define TAU 6.283185307179586476925286766559

// Not faster than built-in functions
// t = angle_in_radians / tau
float cos_alt_use_t(float t)
{
    t = fract(t);
    t -= .25 + floor(t + .25);
    t *= 16. * (abs(t) - 0.5);
    #if 1
    t += .225 * t * (abs(t) - 1.);
    #endif
    return t;
}
float sin_alt_use_t(float t)
{
    return cos_alt_use_t(t - 0.25);
}

// Not faster than built-in functions,
// I'm just experimenting
float distance_alt(vec2 a, vec2 b)
{
    a -= b;
    return intBitsToFloat(0x1fbb4000 + (floatBitsToInt(a.x*a.x+a.y*a.y) >> 1));  
}



// ***********************************************
// PRNG
// Source: https://www.shadertoy.com/view/WdSSRt
// ***********************************************

/* Usage Example:

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Initialize PRNG
    eprng_init(fragCoord.xy / max(iResolution.x, iResolution.y), iTime);

    // Using the function:
    float a = random();
    vec3 b = vec3(random(), random(), random());

    ...
}

*/

uint eprng_state[2];

uint eprng_rot(uint x, int k)
{
    return (x << k) | (x >> (32 - k));
}

float random()
{
    uint s0 = eprng_state[0];
    uint s1 = eprng_state[1];
    uint result = eprng_rot(s0 * 0x9e3779bbu, 5) * 5u;
    s1 ^= s0;
    eprng_state[0] = eprng_rot(s0, 26) ^ s1 ^ (s1 << 9);
    eprng_state[1] = eprng_rot(s1, 13);
    // Not sure this is actually the best way to get evenly-distributed floats -
    // precision will be lost converting from int to float, but how much depends on the order of magnitude
    // I could use bit magic to build the bits of a float directly, then use uintBitsToFloat,
    // but that also loses precision - just in a different way.
    // I guess precision loss is inevitable, since if we want floats in the range 0.0 <= r <= 1.0 then
    // there's only so many bits to work with. It's just a matter of how they're distributed.
    return float(result) / float(0xffffffffu);
}

void eprng_init(vec2 uv, float iTime)
{
    uv *= (iTime + 10.258);

    eprng_state[0] = floatBitsToUint(uv.x);
    eprng_state[1] = floatBitsToUint(uv.y);

    random();
}

// ***********************************************
// End
// ***********************************************

