/**
    A quasi-realistic looking carbon fiber texture pattern based on overlapping two different sets of uv maps
    mixed using a zigzag checkered pattern. The checkered pattern is stored in x channel of the carbon fiber
    map. The anisotropic brushed "metal" look is generated by stretching two sets of perlin fbm noise along the
    x and y axis and then mixing them using the same zigzag checkered pattern. A second map is also generated
    that stores the heightmap for each "weave" in the mesh. Finally everything is mixed together using these 
    two masks with 2 distinct colors to differentiate between the horizontal and vertical weaves. Some fake lighting
    is also added to enhance the effect of the heightmap & the anisotropic brushes on each weave of the mesh. Try playing
    around with different settings below to customize the look of the texture. 
*/

#define Saturate(x) clamp(x, 0.0, 1.0)

#define WEAVE_SCALE 50.0
#define HORIZONTAL_WEAVE_COLOR vec3(0.165,0.165,0.165) // #2A2A2A
#define VERTICAL_WEAVE_COLOR   vec3(0.329,0.329,0.329) // #545454

#define ANISOTROPIC_MASK 1
#define ANISOTROPIC_ANIMATION 1
#define ANISOTROPIC_THRESHOLD 0.3
#define ANISOTROPIC_ANIMATION_SPEED 0.02

#define LIGHTING 1
#define SPECULAR 1

#define DIFFUSE_SCALE 2.0
#define SPECULAR_SCALE 0.5

float Hash12(vec2 p, float f)
{
    p = abs(mod(p, f)); // we want the same random points for each subsection
	uvec2 q = uvec2(ivec2(p)) * uvec2(1597334673U, 3812015801U);
	uint n = (q.x ^ q.y) * 1597334673U;
	return float(n) * (1.0 / float(0xffffffffU));
}

// Noise function by morgan3d (modified)
float PerlinNoise(vec2 x, float freq) 
{
    x *= freq; // tile

    vec2 f = fract(x);
    x = floor(x);

	float a = Hash12(x, freq);
    float b = Hash12(x + vec2(1.0, 0.0), freq);
    float c = Hash12(x + vec2(0.0, 1.0), freq);
    float d = Hash12(x + vec2(1.0, 1.0), freq);

    vec2 u = f * f * (3.0 - 2.0 * f);
	return mix(a, b, u.x) + (c - a) * u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}

float PerlinFbm (vec2 uv, float freq, int octaves)
{
    float amp = .5;
    float noise = 0.;

    for (int i = 0; i < octaves; ++i)
    {
        noise += amp * PerlinNoise(uv, freq);
        amp *= .5;
        freq *= 2.;
    }
    return noise;
}

vec2 GenerateCarbonFiberMap(in vec2 uv)
{
    vec2 gridUv = uv * WEAVE_SCALE * vec2(1.0, iResolution.y / iResolution.x); // Scale grid row size based on aspect ratio.
    vec2 gridId = floor(gridUv);
    float maskScaleInverse = 0.5;
    vec2 maskOffset = (gridUv + gridId.yx) * maskScaleInverse;

    float patternMask = mod(floor(maskOffset.x), 2.0); // zigzag pattern
    vec2 gradients = 1.0 - abs(fract(maskOffset) - 0.5); // horizontal and vertical gradient patterns
    gradients -= 0.5;
    gradients = Saturate(pow(gradients, vec2(0.25)));
    float heightMap = mix(gradients.y, gradients.x, patternMask); // mix both gradients based on the zigzag pattern

#if ANISOTROPIC_MASK
    float anisotropicBrush = mix(PerlinFbm(uv * vec2(50.0, 0.1) + vec2(0.0, uv.x * 8.0) 
#if ANISOTROPIC_ANIMATION
                                           + iTime * ANISOTROPIC_ANIMATION_SPEED
#endif
                                           , 32.0, 8), // vertical anisotropic
                                 PerlinFbm(uv * vec2(0.1, 50.0) + vec2(uv.y * 8.0, 0.0)
#if ANISOTROPIC_ANIMATION
                                           + iTime * ANISOTROPIC_ANIMATION_SPEED
#endif
                                           , 32.0, 8), // horizontal anisotropic
                                 patternMask);
    heightMap = mix(anisotropicBrush * ANISOTROPIC_THRESHOLD, heightMap, heightMap);
#endif
    return vec2(patternMask, heightMap);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 carbonFiber = GenerateCarbonFiberMap(uv);
    vec3 outColor = vec3(1.0);

#if LIGHTING
    vec3 normal = normalize(vec3(dFdx(carbonFiber.y) * 2.0, dFdy(carbonFiber.y) * 2.0, carbonFiber.y)); // I've literally pulled the multiply by 2 out of my ass because it looks good. 
    vec3 lightDirection = normalize(vec3(iMouse.y < 0.5 ? vec2(iResolution.xy * 0.5) : iMouse.xy, 500.0) - vec3(fragCoord, 0.0));
    outColor = vec3(max(0.0, dot(normal, lightDirection))) * DIFFUSE_SCALE; // diffuse "lighting"
#if SPECULAR
    vec3 r = reflect(-lightDirection, normal);
    outColor += pow(max(0.0, dot(normalize(vec3(vec2(0.5), 1000.0) - vec3(uv, 0.0)), r)), 8.0) * SPECULAR_SCALE; // specular "lighting" 
#endif
#else
    outColor *= 2.0;
#endif

    outColor *= carbonFiber.y * mix(VERTICAL_WEAVE_COLOR, HORIZONTAL_WEAVE_COLOR, carbonFiber.x);
    fragColor = vec4(outColor, 1.0);
}

#define MAX_STEPS 50
#define MAX_DIST 50.
#define MAX_SHADOW_DIST 30.
#define SURF_DIST .01

float sdCapsule(vec3 p, vec3 a, vec3 b, float r)
{
    vec3 ab = b - a; 
    vec3 ap = p - a; 
    float t = dot(ap, ab) / dot(ab, ab);
    t = clamp(t, 0., 1.);
    vec3 c = a + t * ab;

    return length(c - p) - r;
}

float sdCylinder(vec3 p, vec3 a, vec3 b, float r)
{
    vec3 ab = b - a; 
    vec3 ap = p - a; 
    float t = dot(ap, ab) / dot(ab, ab);
    vec3 c = a + t * ab;

    float x = length(p - c) - r;
    float y = (abs(t - .5) - .5) * length(ab);
    float e = length(max(vec2(x, y), 0.));
    float i = min(max(x, y), 0.);
    return e + i;
}

float sdTours(vec3 p, vec2 r)
{
    float x = length(p.xz) - r.x;
    return length(vec2(x, p.y)) - r.y;
}

float sdBox(vec3 p, vec3 size)
{
    vec3 d = abs(p) - size;
    float e = length(max(d, 0.));
    float i = min(max(max(d.x, d.y), d.z), 0.);
    return e + i;
}

float GetDist(vec3 p)
{
    vec4 sphere = vec4(0, 1, 6, 1);
    float sphereDist = length(p - sphere.xyz) - sphere.w;
    float planeDist = p.y;
    float cd = sdCapsule(p, vec3(0, 1, 6), vec3(1, 2, 6), .2);
    float td = sdTours(p - vec3(0, .5, 6), vec2(1.5, .3));
    float bd = sdBox(p - vec3(-3, .75, 6), vec3(.75));
    float cyld = sdCylinder(p, vec3(0, .3, 3), vec3(2, .3, 5), .3);
    float d = min(planeDist, cd);
    d = min(td, d);
    d = min(bd, d);
    d = min(cyld, d);
    return d;
}

float RayMarch(vec3 ro, vec3 rd)
{
    float dO = 0.;
    for (int i = 0; i < MAX_STEPS; i++)
    {
        vec3 p = ro + dO * rd;
        float dS = GetDist(p);
        dO += dS;
        if (dO > MAX_DIST || dO < SURF_DIST) break;
    }
    return dO;
}

vec3 GetNormal(vec3 p)
{
    float d = GetDist(p);
    vec2 e = vec2(.01, 0);
    //vec3 normal = d - vec3
    //(
    //    GetDist(p - e.xyy),
    //    GetDist(p - e.yxy),
    //    GetDist(p - e.yyx)
    //);
    vec3 normal = vec3
    (
        GetDist(p + e.xyy),
        GetDist(p + e.yxy),
        GetDist(p + e.yyx)
    );
    return normalize(normal);
}

float shadow(vec3 p, vec3 lightDir, float ldis)
{
    float shadow = 1.0;
    float t = 0.001;
    for (int i = 0; i < MAX_STEPS; i++)
    {
        float d = GetDist(p + t * lightDir);
        t += d;
        if (t > MAX_SHADOW_DIST) break;
        if (d < SURF_DIST) return 0.;
        shadow = min(2. * d / t, shadow);
    }
    return shadow;
}

float ao(vec3 p, vec3 n)
{
    float a = 0.;
    float e = .1;
    int ignored;
    float weight = .5;

    for (int i = 1; i <= 5; i++)
    {
        float d = e * float(i);
        a += weight * (1.0 - (d - GetDist(p + n * d)));
        weight *= .5;
    }

    return a;
}

vec3 GetLight(vec3 p)
{
    vec3 lightPos = vec3(0, 5, 0);
    lightPos += vec3(5. * cos(iTime) * .5, 0, 5. * sin(iTime) * .5);
    vec3 lightDir = normalize(lightPos - p);
    vec3 normal = GetNormal(p);
    float dif = clamp(dot(lightDir, normal), 0., 1.);
    float ldis = length(lightPos - p);
    dif *= shadow(p + normal * SURF_DIST * 2., lightDir, ldis);

    vec3 ambient = ao(p, normal) * vec3(.2);
    return vec3(dif) + ambient;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 2, 0); 
    vec3 rd = normalize(vec3(uv.x, uv.y - .2, 1));

    vec3 p = ro + rd * RayMarch(ro, rd);
    col = GetLight(p);
    //col = GetNormal(p);

    // Output to screen
    fragColor = vec4(col,1.0);
}

//
// GLSL textureless classic 3D noise "cnoise",
// with an RSL-style periodic variant "pnoise".
// Author:  Stefan Gustavson (stefan.gustavson@liu.se)
// Version: 2011-10-11
//
// Many thanks to Ian McEwan of Ashima Arts for the
// ideas for permutation and gradient selection.
//
// Copyright (c) 2011 Stefan Gustavson. All rights reserved.
// Distributed under the MIT license. See LICENSE file.
// https://github.com/ashima/webgl-noise
//

vec3 mod289(vec3 x)
{
    return x-floor(x*(1./289.))*289.;
}

vec4 mod289(vec4 x)
{
    return x-floor(x*(1./289.))*289.;
}

vec4 permute(vec4 x)
{
    return mod289(((x*34.)+1.)*x);
}

vec4 taylorInvSqrt(vec4 r)
{
    return 1.79284291400159-.85373472095314*r;
}

vec3 fade(vec3 t){
    return t*t*t*(t*(t*6.-15.)+10.);
}

// Classic Perlin noise
float cnoise(vec3 P)
{
    vec3 Pi0=floor(P);// Integer part for indexing
    vec3 Pi1=Pi0+vec3(1.);// Integer part + 1
    Pi0=mod289(Pi0);
    Pi1=mod289(Pi1);
    vec3 Pf0=fract(P);// Fractional part for interpolation
    vec3 Pf1=Pf0-vec3(1.);// Fractional part - 1.0
    vec4 ix=vec4(Pi0.x,Pi1.x,Pi0.x,Pi1.x);
    vec4 iy=vec4(Pi0.yy,Pi1.yy);
    vec4 iz0=Pi0.zzzz;
    vec4 iz1=Pi1.zzzz;

    vec4 ixy=permute(permute(ix)+iy);
    vec4 ixy0=permute(ixy+iz0);
    vec4 ixy1=permute(ixy+iz1);

    vec4 gx0=ixy0*(1./7.);
    vec4 gy0=fract(floor(gx0)*(1./7.))-.5;
    gx0=fract(gx0);
    vec4 gz0=vec4(.5)-abs(gx0)-abs(gy0);
    vec4 sz0=step(gz0,vec4(0.));
    gx0-=sz0*(step(0.,gx0)-.5);
    gy0-=sz0*(step(0.,gy0)-.5);

    vec4 gx1=ixy1*(1./7.);
    vec4 gy1=fract(floor(gx1)*(1./7.))-.5;
    gx1=fract(gx1);
    vec4 gz1=vec4(.5)-abs(gx1)-abs(gy1);
    vec4 sz1=step(gz1,vec4(0.));
    gx1-=sz1*(step(0.,gx1)-.5);
    gy1-=sz1*(step(0.,gy1)-.5);

    vec3 g000=vec3(gx0.x,gy0.x,gz0.x);
    vec3 g100=vec3(gx0.y,gy0.y,gz0.y);
    vec3 g010=vec3(gx0.z,gy0.z,gz0.z);
    vec3 g110=vec3(gx0.w,gy0.w,gz0.w);
    vec3 g001=vec3(gx1.x,gy1.x,gz1.x);
    vec3 g101=vec3(gx1.y,gy1.y,gz1.y);
    vec3 g011=vec3(gx1.z,gy1.z,gz1.z);
    vec3 g111=vec3(gx1.w,gy1.w,gz1.w);

    vec4 norm0=taylorInvSqrt(vec4(dot(g000,g000),dot(g010,g010),dot(g100,g100),dot(g110,g110)));
    g000*=norm0.x;
    g010*=norm0.y;
    g100*=norm0.z;
    g110*=norm0.w;
    vec4 norm1=taylorInvSqrt(vec4(dot(g001,g001),dot(g011,g011),dot(g101,g101),dot(g111,g111)));
    g001*=norm1.x;
    g011*=norm1.y;
    g101*=norm1.z;
    g111*=norm1.w;

    float n000=dot(g000,Pf0);
    float n100=dot(g100,vec3(Pf1.x,Pf0.yz));
    float n010=dot(g010,vec3(Pf0.x,Pf1.y,Pf0.z));
    float n110=dot(g110,vec3(Pf1.xy,Pf0.z));
    float n001=dot(g001,vec3(Pf0.xy,Pf1.z));
    float n101=dot(g101,vec3(Pf1.x,Pf0.y,Pf1.z));
    float n011=dot(g011,vec3(Pf0.x,Pf1.yz));
    float n111=dot(g111,Pf1);

    vec3 fade_xyz=fade(Pf0);
    vec4 n_z=mix(vec4(n000,n100,n010,n110),vec4(n001,n101,n011,n111),fade_xyz.z);
    vec2 n_yz=mix(n_z.xy,n_z.zw,fade_xyz.y);
    float n_xyz=mix(n_yz.x,n_yz.y,fade_xyz.x);
    return 2.2*n_xyz;
}

vec4 getFromColor(vec2 uv){
    return texture(iChannel0,uv);
}

vec4 getToColor(vec2 uv){
    return texture(iChannel1,uv);
}

float sdCircle(vec2 p,float r)
{
    return length(p)-r;
}

vec4 transition(vec2 uv){
    float progress=iMouse.x/iResolution.x;
    float ratio=iResolution.x/iResolution.y;

    vec2 p=uv;
    p-=.5;
    p.x*=ratio;

    float noise=cnoise(vec3(p*10.,0.));
    float pr=progress+noise*.1;

    float d=sdCircle(p,pr*sqrt(2.));
    float c=smoothstep(-.1,-.05,d);

    return mix(getFromColor(uv),getToColor(uv),1.-c);
}

void mainImage(out vec4 fragColor,in vec2 fragCoord){
    vec2 uv=fragCoord/iResolution.xy;

    vec4 col=transition(uv);

    fragColor=col;
}

const vec2 OFFSET = vec2(69.0, 420.0);
const float SCALE = 1.6;
const float AMPLITUDE = 0.4;
const int OCTAVES = 4;
const float LACUNARITY = 2.4;
const float GAIN = 0.5;

float fbm(in vec2 p) {
    float t = iTime / 2.0;
    float value = 0.0;
    float amplitude = AMPLITUDE;
    vec3 q = vec3(p.xy, t);

    for (int i = 0; i < OCTAVES; i++) {
        float n = snoise(q);
        n = abs(n);
        value += amplitude * n;
        q.xy *= LACUNARITY;
        amplitude *= GAIN;
    }
    return value;
}

float getNoise(in vec2 p) {
    return fbm(p);
}

vec3 gamma(in vec3 color)
{
    return pow(max(color, 0.0), vec3(1.0 / 2.2));
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    vec2 p = SCALE * uv + OFFSET;
    float n = getNoise(p);
    n = 0.02 / n;
    n = pow(n, 1.9);
    vec3 color = vec3(n);
    color = gamma(color);

    fragColor = vec4(color, 1.0);
}

