const float pi   = 3.14159265359;
const float pipi = pi * 2.0;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord - iResolution.xy/2.0;


    float angle = fract((atan(xy.y, xy.x) + pipi) / pipi);
    float dist  = length(xy);

    float numLanes = 17.0;

    float lane = floor(angle * numLanes);

    float distCompressed = pow(dist, 0.2) * 520.0;

    float c = sin(distCompressed * 0.1 - 8.0 * iTime * (0.7 + 0.3 * sin(iTime * 0.01 + 5.0 * lane / numLanes * pipi)));
    c = smoothstep(-0.4, 0.4, c);

    float minContrast = 0.0;
    float maxContrast = 0.6;
    float d = dist/iResolution.x * 2.0;

    c *=       mix(minContrast, maxContrast, d);
    c += 1.0 - mix(minContrast, maxContrast, d);   

    fragColor = vec4(c, c, c, 1);
}

vec3 hash( uvec3 x )
{  
    //https://www.shadertoy.com/view/4lXyWN

    //multiply large prime value first.
    uint k = 1103515245U;
    x*= k;
    //mix x, y, z values.
    //Without shift operator, x, y and z value become same value.
    x = ((x>>2u)^(x.yzx>>1u)^x.zxy)*k;

    return (vec3(x)*(1.0/float(0xffffffffU)));
}

vec3 noise(vec3 p)
{
    uvec3 fl = uvec3(floor(p));
    vec3 fr = fract(p);
    fr = smoothstep(0.0,1.0,fr);
    vec3 tr = hash(fl + uvec3(0,0,0));
    vec3 tl = hash(fl + uvec3(1,0,0));
    vec3 br = hash(fl + uvec3(0,1,0));
    vec3 bl = hash(fl + uvec3(1,1,0));
    vec3 tr2 = hash(fl + uvec3(0,0,1));
    vec3 tl2 = hash(fl + uvec3(1,0,1));
    vec3 br2 = hash(fl + uvec3(0,1,1));
    vec3 bl2 = hash(fl + uvec3(1,1,1));


    vec3 res = mix(mix(mix(tr, tl, fr.x), mix(br, bl, fr.x), fr.y)
            ,mix(mix(tr2,tl2,fr.x), mix(br2,bl2, fr.x), fr.y),fr.z);

    vec3 result = (1.0 + res)*0.5; //map to [0,1]

    return res;
}


vec3 fbm_levels(vec3 p, int levels)
{
    vec3 r = vec3(0);

    float m = 1.0f;
    for (int i = 0; i < levels; i++)
    {
       m *= 0.5f;
       r += m * noise(p); 
       p *= 2.03;
    }
    return r;
}

vec3 fbm(vec3 p)
{
    return fbm_levels(p,4);
}


vec3 pattern(vec3 p)
{
    vec3 q = fbm(p);
    vec3 r = fbm(p + 4.*q);

    vec3 result;
    result.x = fbm(p + fbm(p + 2.*fbm(p + q))).x;
    result.y = q.x;
    result.z = r.x;
    return result;
}

vec3 sampleFloor(vec2 coord)
{    
    coord = coord * 1.8f + 0.25;
    float g1 = 0.10f;
    float g2 = 0.025f;
    bool b1 = ((1.0 - fract(coord.x - g1/2.0f)) < g1 || (1.0 - fract(coord.y - g1/2.0f) < g1));
    bool b2 = ((1.0 - fract(coord.x - g2/2.0f)) < g2 || (1.0 - fract(coord.y - g2/2.0f) < g2));

    vec3 baseCol;

    vec3 nRes = noise(vec3(coord * 100.0f,1.0f));
    if (!b1 && !b2)
    {
        vec3 fRes = fbm_levels(vec3(coord * 5.0f,1.0f),3);
        baseCol = vec3(0.3 + fRes.r * 0.3f+ nRes.r * 0.2f);
    }
    else if (b1 && !b2)
    {
        baseCol = vec3(0.1 + 0.2 * nRes.y);
    }
    else
    {
        baseCol = vec3(0.7 + 0.2 * nRes.z);
    }

    //
    vec3 timed_p = vec3(coord * 0.3f,iTime*0.4f + fbm(vec3(coord + 5.0,10.0))); 
    vec3 fRes = fbm_levels(timed_p,2);

    float cPow = 50.0f;
    vec3 cCol = vec3(1);

    vec3 caustics = vec3(0);
    caustics += pow(clamp(1.0-abs((fRes.y - 0.5f)),0.0f , 1.0f),cPow) * cCol;
    caustics += pow(clamp(1.0-abs((fRes.x - 0.5f)),0.0f , 1.0f),cPow) * cCol;
    caustics += pow(clamp(1.0-abs((fRes.z - 0.5f)),0.0f , 1.0f),cPow) * cCol;

    return clamp(baseCol + caustics * 0.2f,0.0,1.0);
}

vec2 CalcUV(vec2 coord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = coord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    float zoom = 5.0f;
    uv *= zoom;
    uv += vec2(50.0,25.0);
    return uv;
}

float sampleWater(vec2 coord)
{
    vec2 uv = CalcUV(coord);

    // Time varying pixel color
    vec3 p = vec3(uv,iTime/3.0f + fbm(vec3(uv + 5.0,10.0)));
    vec3 res = pattern(p);
    vec3 color;

    color = mix(vec3(0.101961,0.619608,0.666667),
                vec3(0.666667,0.666667,0.498039),
                clamp((res.x*res.x)*4.0,0.0,1.0));

    color = mix(color,
                vec3(0,0,0.164706),
                1.0-res.y);

    color = mix(color,
                vec3(0.666667,1,1),
                1.0-res.z);

    return (color.r + color.g + color.b)/3.0f;
}

// normals using dfdx dfdy, same for the entire quad
#define CHEAP_NORMALS 1

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = CalcUV(fragCoord);
    vec3 finalCol = sampleFloor(uv);
#if 1
    float greyscale = sampleWater(fragCoord);

    // normal 
    float height = 0.05f * iResolution.x; // scales with resolution, because noise also scales with the resolution.
#if CHEAP_NORMALS
    float offset = 1.0f;
    float greyscale_dx = dFdx(greyscale);
    float greyscale_dy = dFdy(greyscale);
#else
    float offset = 0.1f;
    float greyscale_dx = (sampleWater(fragCoord + vec2(offset,0)) - greyscale);
    float greyscale_dy = (sampleWater(fragCoord + vec2(0,offset)) - greyscale);
#endif
    vec2 dxdy = vec2(greyscale_dx, greyscale_dy);
    vec3 normal = normalize(vec3(dxdy,offset/height));

    // light
    vec3 viewDir = vec3(0,0,-1);
    vec3 lDir = normalize(vec3(-0.477,-0.477,-0.277));

    vec3 reflectDir = reflect(lDir, normal);
    float spec = pow(max(dot(reflectDir,viewDir),0.0f),2.0f);

    // Output to screen
    vec3 waterCol = vec3(145, 239, 255) / 255.0f;

    vec2 samplePos = uv + greyscale;
    finalCol = sampleFloor(samplePos) * waterCol + spec * 0.5f;
#endif
    fragColor = vec4(finalCol,0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mid = iResolution.xy / 2.;
    float mmul = 1. - (iMouse.x / iResolution.x);
    fragCoord = mid - (mid*mmul-fragCoord*mmul);

    vec4 val = vec4(0.);
    for (int j = 0; j <= 1; j++)
        for (int i = 0; i <= 1; i++)
            val += texelFetch( iChannel0, ivec2(int(fragCoord.x)+i,int(fragCoord.y)+j), 0 );
    val /= 2.;

    if (!readKeyToggle(KEY_M)) {
        fragColor = val;
    } else {
        float v=(1./liveval)*val.w;
        fragColor = vec4( v*0.9, v*0.95, v, 1.0 );
    }    
}

