// Cellular automata buffer

vec4 cellval(ivec2 p) {
    ivec2 r = ivec2(textureSize(iChannel0, 0));
    p = (p+r) % r;
    return texelFetch(iChannel0, p, 0 );
}
int cell(ivec2 p) { return (cellval(p).w == liveval ) ? 1 : 0; }

float restart(vec2 fragCoord, float rradius) {
    vec2 mid = iResolution.xy * 0.5; 
    float xmul=1.; if (iResolution.y < 1000.) xmul=1.75;
    if (fragCoord.x > mid.x - rradius*xmul && fragCoord.x < mid.x + rradius*xmul+1. && fragCoord.y > mid.y - rradius && fragCoord.y < mid.y + rradius +1.)
        return liveval;
    return 0.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 col = vec3(-3.5*sin((iTime-10.)*0.012), 3.5*sin(iTime*0.0105), 3.5*sin(iTime*0.02));
    setRules(col);
    ivec2 px = ivec2(fragCoord);
    vec4 curr = cellval(px);
    float xres = texelFetch(iChannel2, ivec2(0,1), 0 ).x;

    if( iFrame<2 || readKey(KEY_SPACE) || int(xres) != int(iResolution.x) || (iResolution.y < 800. && mod(iTime, 69.) < 0.2)   ) {
        fragColor = vec4( 0.0, 0.0, 0.0, restart(fragCoord, 25.));
        return;
    }
    int k = cell(px+ivec2(0,-2)) + cell(px+ivec2(-1,-1)) + cell(px+ivec2(1,-1))
          + cell(px+ivec2(-2, 0))                        + cell(px+ivec2(2, 0))
          + cell(px+ivec2(-1, 1)) + cell(px+ivec2(1, 1)) + cell(px+ivec2(0, 2));

    float ff = 0.;
    if (curr.w > 0.5) {
        if ((stayset & (1<<(k-1))) > 0 ) { ff = float(k); }
    }
    else {
        ff = (bornset & (1<<(k-1))) > 0 ? liveval : 0.;
    }
    if (ff >= 1.0) {
        int st = int(ff);
        if ((st & ra) > 0) curr.x += cstep*rp;
        if ((st & ga) > 0) curr.y += cstep*gp;
        if ((st & ba) > 0) curr.z += cstep*bp;
    } else {
        curr.x -= cstep*rm; curr.y -= cstep*gm; curr.z -= cstep*bm;
    }
	fragColor = vec4( clamp(curr.xyz,0.,1.), ff );
}

