const float cstep=1./256.;

float liveval, rp,gp,bp, rm,gm,bm;
int stayset, bornset, ra,ga,ba;

void setRules(vec3 col) {
    stayset = 23; bornset = 86;
    liveval=5., rp=10., gp=7., bp=20., rm=2., gm=2.,bm=2.,  ra=1,ga=2,ba=4;
    rp=rp+col.x; gp=gp+col.y; bp=bp+col.z;
}

const int KEY_SPACE = 32;
const int KEY_M     = 77;
#define readKey(key) (texelFetch( iChannel3, ivec2(key, 0), 0).x > .5)
#define readKeyToggle(key) (texelFetch( iChannel3, ivec2(key, 2), 0).x > .5)

