vec3 rdr(vec2 uv)
{
    vec2 off = vec2((hash11(uv.y+iTime*0.01)-0.5)*.2, 0.);
    if (iTime < 65.0)
        off *= 0.;
    off *= sat(sin(iTime*10.)*.5+.5)*(1.-sat(1.5*abs(uv.y-.5)))*.25;
    vec3 col = vec3(0.);
    col.x =texture(iChannel0, uv+off).x;
    col.y =texture(iChannel0, uv+0.5*off).y;
    col.z =texture(iChannel0, uv-off).z;
     return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = rdr(uv)*2.;
    fragColor = vec4(col,1.0);
}

