float _seed;
float rand()
{
    _seed++;
    return hash11(_seed);
}
// Thanks iq :)
float smin( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }
vec2 map(vec3 p)
{
    vec2 acc = vec2(10000.,-1.);

    vec3 rep = vec3(10.);
    vec3 id = floor((p+rep*.5)/rep);
    p = mod(p+rep*.5,rep)-rep*.5;
    p.xy *= r2d(id.x);
    p.yz *= r2d(id.z);
    //p.x += 1.-pow(texture(iChannel3, vec2(0.,p.y*2.)).x,.5)*.9;

    float shape = 10000.;
    for (float i = 0.; i < 8.; ++i)
    {
        vec3 pos = p+vec3(sin(i+iTime*.7), sin(i*5.+iTime*.25), sin(.33*iTime+i*3.3));
        float curshape = length(pos)-.5-1.5*texture(iChannel3, vec2(i*.1,0.)).x;
        shape = smin(shape, curshape, .2);
        //acc = _min(acc, vec2(length(pos)-1., 0.));
    }
            acc = _min(acc, vec2(shape, 0.));

    float cube = _cucube(p, vec3(2.), vec3(.02));
    cube = max(cube, -(length(p)-3.1));
    if (iTime > 65.)
        acc = _min(acc, vec2(cube, 1.));

    p += sin(p+iTime);
    p.xy *= r2d(iTime);
    float laser = length(p.xz)-.05;
    laser = max(laser, sin(p.y*1.-sign(p.y)*20.*iTime));
    if (iTime > 65.)
        acc = _min(acc, vec2(laser, 1.));

    float laser2 = length(p.xy)-.02;
    laser2 = max(laser2, sin(p.z*1.-sign(p.z)*10.*iTime)+.5);
    if ((iTime > 35. && iTime < 60.) || iTime > 65.)
        acc = _min(acc, vec2(laser2, 2.));

    float laser3 = length(p.yz)-.02;
    laser3 = max(laser3, sin(p.x*2.-sign(p.x)*10.*iTime)+.5);

    acc = _min(acc, vec2(laser3, 3.));


    return acc;
}

vec3 getNorm(vec3 p, float d)
{
    vec2 e = vec2(0.001, 0.);
    return normalize(vec3(d)-vec3(map(p-e.xyy).x, map(p-e.yxy).x, map(p-e.yyx).x));
}

vec3 trace(vec3 ro, vec3 rd, int steps)
{
    vec3 p = ro;
    for (int i = 0; i < steps && distance(p, ro) < 30.; ++i)
    {
        vec2 res = map(p);
        if (res.x < 0.001)
            return vec3(res.x, distance(p, ro), res.y);
        p+=rd*res.x*.5;
    }
    return vec3(-1.);
}

vec3 getMat(vec3 p, vec3 n, vec3 rd, vec3 res)
{
    vec3 col = vec3(0.1);
    if (res.z == 1.)
        col = vec3(0.247,0.675,0.788)*1.;
    if (res.z == 2.)
        col = vec3(1.000,0.039,0.329)*1.;
    if (res.z == 3.)
        col = vec3(0.922,0.502,0.020)*1.;
    vec3 refl = normalize(reflect(rd, n));
    col += texture(iChannel2, refl*vec3(1.,-1.,1.)).xyz*.2;
    return col;
}

vec3 rdr(vec2 uv, vec2 ouv)
{
    float t = iTime*.17;
    float d = 5.;
    float tanim = iTime*.5;
    float off = mix((hash11(floor(tanim))-0.5)*3., 
    (hash11(floor(tanim+1.))-0.5)*3., 
    sat(pow(fract(tanim), 15.)));

        float tanim2 = iTime*mix(2.5, 0., float(iTime < 12.));
    float off2 = mix((hash11(floor(tanim2)))*3., 
    (hash11(floor(tanim2+1.)))*3., 
    sat(pow(fract(tanim2), 15.)));
    d*= off2;
    uv *= r2d(.5*sin(iTime*.3)-off);
    vec3 ro = vec3(sin(t)*d,1.,cos(t)*d);
    vec3 ta = vec3(0.,0.,0.);
    vec3 rd = normalize(ta-ro);

    rd = getCam(rd, uv);
    vec3 col = pow(texture(iChannel2, rd*vec3(1.,-1.,1.)).xyz, vec3(.35))*.15;
    col = (1.-sat(pow(abs(uv.y), .1)))*
    vec3(texture(iChannel3, vec2(abs(uv.x),0.)).x, 0.5,0.2).zxy;
    vec3 res = trace(ro, rd, 128);
    if (res.y > 0.)
    {
        vec3 p = ro+rd*res.y;
        vec3 n = getNorm(p, res.x);
        float pix = .1;
        col = n*.5+.5;//floor((n*.5+.5)/pix+texture(iChannel2, ouv*40.).x*2.)*pix;
        col = getMat(p, n, rd, res);
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 ouv = (fragCoord)/iResolution.xy;

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xx;
    _seed = iTime+texture(iChannel0, uv).x;
    vec2 off = .2*(vec2(rand(), rand())-.5)*
    pow(texture(iChannel3, vec2(0.5,0.)).x,8.);
    vec3 col = rdr(uv+off+vec2(.2,0.)*0., ouv);
    col = sat(col);

    col = mix(col, texture(iChannel1, fragCoord/iResolution.xy).xyz, .5);
    fragColor = vec4(col,1.0);
}

