float HexDist(vec2 p)
{
    p = abs(p);
    float c = dot(p, normalize(vec2(1, 1.73)));
    c = max(c, p.x);
    return c;
}

vec4 HexCoords(vec2 uv)
{
    vec2 r = vec2(1., 1.73);
    vec2 h = .5 * r;
    vec2 a = mod(uv, r) - h;
    vec2 b = mod(uv - h, r) - h;
    vec2 gv = dot(a, a) < dot(b, b) ? a : b; 
    vec2 id = uv - gv;

    return vec4(gv, id);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3(0);

    uv *= 5.;

    col.rg = HexCoords(uv).xy;

    // Output to screen
    fragColor = vec4(col, 1.0);
}

// 

struct Ray
{
    vec3 org;
    vec3 dir;
};

struct Hit
{
    float distanceToHitpoint;
    vec3 normal;
};

// 
float FilmWidth() { return iResolution.x / 100.0; }
float FilmHeight() { return iResolution.y / 100.0;  }
float FilmDistance() { return 8.0; }

vec3 CameraFrom() { return vec3(0.0, 0.0, -3.0); }
vec3 CameraTo() { return vec3(0.0, 0.0, 1.0); }
vec3 CameraUp() { return vec3(0.0, 1.0, 0.0); }

float LargeFloat() { return 1e+6; }

// 
void createOrthoNormalBasis(
    vec3 from, vec3 to, vec3 up,
    out vec3 u, out vec3 v, out vec3 w, out vec3 e
)
{
    w = normalize(from - to);
    u = normalize(cross(up, w));
    v = cross(w, u);
    e = from;
}

vec3 convertToCameraCoordinateSystem(
    vec2 pixelCoordinate
)
{

	float time = 32.0 + iTime*1.5;
    vec3 offset = vec3(2.0 * cos(0.1 * time), 0, 2.0 * sin(0.1 * time));

    float filmWidth = FilmWidth();
    float filmHeight = FilmHeight();
    float filmDistance = FilmDistance();
    vec3 cameraFrom = CameraFrom();
    vec3 cameraTo = CameraTo() + offset;
    vec3 cameraUp = CameraUp();

    vec3 u, v, w, e;
    createOrthoNormalBasis(cameraFrom, cameraTo, cameraUp, u, v, w, e);

    vec3 filmPlaneCenter = cameraFrom - w * filmDistance;
    vec3 filmPlaneRight = u * filmWidth * (pixelCoordinate.x - iResolution.x / 2.0) / iResolution.x;
    vec3 filmPlaneUp = v * filmHeight * (pixelCoordinate.y - iResolution.y / 2.0) / iResolution.y;

    return normalize(filmPlaneCenter + filmPlaneRight + filmPlaneUp - e);
}

Ray generateCameraRay(
    vec2 pixelCoordinate
)
{
    vec3 dir = convertToCameraCoordinateSystem(pixelCoordinate);
    vec3 org = CameraFrom();
    return Ray(org, dir);
}

bool intersectToSphere(
    vec3 center, float radius, Ray ray,
    out Hit hit
)
{
    vec3 oc = ray.org - center;
    float a = dot(ray.dir, ray.dir);
    float b = 2.0 * dot(oc, ray.dir);
    float c = dot(oc, oc) - radius * radius;
    float discriminant = b * b - 4.0 * a * c;
    if (discriminant < 0.0) {
        return false;
    }
    float t = (-b - sqrt(discriminant)) / (2.0 * a);
    if (t < 0.0) {
        t = (-b + sqrt(discriminant)) / (2.0 * a);
        if (t < 0.0) {
            return false;
        }
    }
    hit.distanceToHitpoint = t;
    hit.normal = normalize(ray.org + t * ray.dir - center);
    return true;
}

bool intersectToPlane(vec3 center, vec2 size, Ray ray, out Hit hit) {
    vec3 normal = vec3(0.0, 0.0, 1.0);
    vec3 p = center - ray.org;
    float t = dot(p, normal) / dot(ray.dir, normal);
    if (t < 0.0) {
        return false;
    }
    vec3 hitpoint = ray.org + t * ray.dir;
    vec2 halfSize = size * 0.5;
    if (abs(hitpoint.x - center.x) > halfSize.x || abs(hitpoint.y - center.y) > halfSize.y) {
        return false;
    }
    hit.distanceToHitpoint = t;
    hit.normal = normal;
    return true;
}


bool intersect(Ray ray, out Hit hit)
{
    hit.distanceToHitpoint = LargeFloat();

    vec3 plane_center = vec3(0.5, 0.0, 0.0);
    vec2 plane_size = vec2(0.5, 1.0);

    vec3 sphere_center = vec3(-0.5, 0.0, 0.0);
    float radius = 0.5;

    if(intersectToPlane(plane_center, plane_size, ray, hit)) return true;
    if(intersectToSphere(sphere_center, radius, ray, hit)) return true;
    return false;
}

vec3 shade(Ray ray, Hit hit)
{
    return hit.normal * 0.5 + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    Ray ray = generateCameraRay(fragCoord);
    float time = 32.0 + iTime*1.5;

    Hit hit;
    if (intersect(ray, hit)) {
        fragColor = vec4(shade(ray, hit), 0.0);
    } else {
        fragColor = vec4(0.0);
    }
}

