#define TONEWIDTH (1./400.)

vec3 hsv2rgb(vec3 c) {
  c = vec3(c.x, clamp(c.yz, 0.0, 1.0));
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // The main purpose of this thing is to always keep time
    // within a sensible range for sin()
    float time = timesawtooth(iTime, TIMEPERIOD, TIMETRANS);

    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = 0.5 + 0.5*cos(time+uv.xyx+vec3(0,2,4));
    fragColor = vec4(col, 1.0);
    if (fragCoord.x > 5. && fragCoord.y > 5. && fragCoord.x < iResolution.x-5. && fragCoord.y < iResolution.y-5.) {
        fragColor.rgb *= envMid(uv.x, uv.y);
    }

    float t = mod(time, RANGE);                        // time = [0 .. RANGE]
    float tf = t / RANGE;                              // time = [0 .. 1]
    float tfe = (exp2(tf) - 1.) / (exp2(RANGE) - 1.);  // exp time = [0..1]
    float tfe1 = tfe + 1.;                             // exp time = [1..2]

    fragColor.rgb *= smoothstep(0.3, 0., 0.5-abs(fract(uv.x - tf)-0.5));

    if (abs(octf(PEAKOCTAVE) - uv.y) <= 1./iResolution.y || abs(octf(PEAKOCTAVE) - uv.x) <= 1./iResolution.x) fragColor += vec4(0.3);

    for (float i = floor(OCTAVEMIN); i < ceil(OCTAVEMAX); i++) {
        if (abs(octf(i) - uv.x) <= 1./iResolution.x) fragColor.g += 0.4;
    }
    for (float i = floor(OCTAVEMIN); i < ceil(OCTAVEMAX); i+=STEP) {
        vec2 d = vec2(abs(octf(tf+i) - uv.x), envMid(uv.x, octf(PEAKOCTAVE)));
        if (uv.y <= d.y) fragColor.rg += vec2(d.y*0.4  *  smoothstep(3./iResolution.x, 0., d.x - TONEWIDTH));
    }

    //if (abs(octf(tf) - uv.x) <= 1./iResolution.x) fragColor.rgb += vec3(0.4);
}

