// Speed, octaves per sec
#define SPD           0.06

// Peak volume at this octave (-3 ... 5)
#define PEAKOCTAVE    0.8

// Number of tones per octave (1 .. 10)
#define TONES_PER_OCTAVE  1.

// Dynamic range, dB
#define DYNRANGE_DB   40.

// Ramp-up / ramp down oscillation period, seconds
#define TIMEPERIOD    150.

// Transition time between up/down / startup time, seconds
#define TIMETRANS     1.5

// Min hearable frequency, HZ - should be slightly above the hearable
#define FREQMIN       50.

// Max hearable frequency, HZ - should be slightly below the hearable
#define FREQMAX       12000.

// Rumbling effect by modulation with this freq, HZ (can comment out)
#define MODULATE_BY   65.

// Tone goes first up, then down  (comment out for down-up)
#define UPDOWN














float note2freq(float note) { return 440. * exp2((-10.)/12.) * exp2(note/12.); }
float octave2freq(float note) { return 440. * exp2((-10.)/12.) * exp2(note); }
#define OCTAVEMIN   log2(FREQMIN / 440. / exp2((-10.)/12.))
#define OCTAVEMAX   log2(FREQMAX / 440. / exp2((-10.)/12.))
//float sinhz(float x) { return sin(6.28318530718 * x); }                         // sin
float sinhz(float x) { return abs(fract(x-0.25)-0.5)*4.-1.; }                   // triangle
//float sinhz(float x) { return fract(x)*2.-1.; }                                 // saw
//float sinhz(float x) { return sign(fract(x)-0.5); }                             // square
//float sinhz(float x) { return smoothstep(-1.,1.,sin(x*6.28318530718))*2.-1.; }  // smoothsquare 1
//float sinhz(float x) { return smoothstep(0.+0.15,0.5-0.15,abs(fract(x)-0.5))*2.-1.; }  // smoothsquare 2

// Sine for modulation
float sinhz_mod(float x) { return sin(6.28318530718 * x); }

// Envelope:
//   https://graphtoy.com/?f1(x,t)=sin(3.14159265359%20*%20x)&v1=true&f2(x,t)=smoothstep(0,1,x)&v2=true&f3(x,t)=sin(3.14159265359%20*%20smoothstep(0,1,x))&v3=true&f4(x,t)=smoothstep(0,1,smoothstep(0,1,x))&v4=true&f5(x,t)=sin(3.14159265359%20*%20smoothstep(0,1,smoothstep(0,1,x)))&v5=true&f6(x,t)=pow(sin(3.14159265359%20*%20x),%204)&v6=true&grid=1&coords=0.5,0.5,0.6666666666666666
// * https://graphtoy.com/?f1(x,t)=sin(3.14159265359*x)&v1=true&f2(x,t)=&v2=false&f3(x,t)=&v3=false&f4(x,t)=4*(x-x*x)&v4=true&f5(x,t)=&v5=false&f6(x,t)=&v6=false&grid=2&coords=0.5,0.5,0.6666666666666666
//float envelope(float x) { return sin(3.14159265359 * x); }
//float envelope(float x) { return sin(3.14159265359 * smoothstep(0., 1., x)); }
//float envelope(float x) { return sin(3.14159265359 * x); }
//float envelope(float x) { return pow(4. * (x - x*x), 0.7); }
float _envelope(float x) { return 4. * (x - x*x); }
float envelope(float x) { return 1. - pow(1. - _envelope(x), 1.5); }

// Skew:
//   https://graphtoy.com/?f1(x,t)=sin(3.14159265359%20*%20x)&v1=true&f2(x,t)=pow(x,%202)&v2=false&f3(x,t)=pow(x,%200.4)&v3=true&f4(x,t)=sin(3.14159265359%20*%20pow(x,%202))&v4=false&f5(x,t)=sin(3.14159265359%20*%20pow(x,%200.4))&v5=true&f6(x,t)=sin(3.14159265359%20*%20(1-pow(1-x,%200.4)))&v6=true&grid=1&coords=0.5,0.5,0.6666666666666666
//   https://graphtoy.com/?f1(x,t)=sin(3.14159265359*x)&v1=true&f2(x,t)=4*(x-x*x)&v2=true&f3(x,t)=pow(x,0.4)&v3=true&f4(x,t)=f2(pow(x,2))&v4=false&f5(x,t)=f2(pow(x,0.4))&v5=true&f6(x,t)=f2(1-pow(1-x,0.4))&v6=true&grid=2&coords=0.5,0.5,0.6666666666666666
//   https://graphtoy.com/?f1(x,t)=pow(pow(x*2-1,2)-1,2)&v1=true&f2(x,t)=pow(x,1/0.4)&v2=false&f3(x,t)=pow(pow(pow(x,1/0.4)*2-1,2)-1,2)&v3=true&f4(x,t)=pow(pow((1-pow(1-x,1/0.4))*2-1,2)-1,2)&v4=true&f5(x,t)=&v5=false&f6(x,t)=&v6=false&grid=1&coords=0.5,0.5,0.6666666666666666
// * https://graphtoy.com/?f1(x,t)=4*(x-x*x)&v1=true&f2(x,t)=pow(x,0.5)&v2=false&f3(x,t)=pow(1-x,0.5)&v3=false&f4(x,t)=&v4=false&f5(x,t)=f1(f2(x))&v5=true&f6(x,t)=f1(f3(x))&v6=true&grid=2&coords=0.5,0.5,0.6666666666666666
float _skewf(float x, float n) { return pow(clamp(x, 0., 1.), n); }
float _skew1(float x, float mid) { return _skewf(x, log(0.5)/log(mid)); }
float skew(float x, float mid) { return mid <= 0.5 ? _skew1(x, mid) : _skew1(1. - x, 1. - mid); }
//float skew(float x, float mid) { return _skew1(x, 0.5-abs(fract(mid)-0.5)); }

float envMid(float x, float mid) { return envelope(skew(x, mid)); }

float db2amp(float x) { return pow(10., (x-1.)*DYNRANGE_DB/20.); }

float octf(float n) { return clamp((n - OCTAVEMIN)/(OCTAVEMAX - OCTAVEMIN), 0., 1.); }

// Skew:
//   https://graphtoy.com/?f1(x,t)=pow(x,%200.25)&v1=true&f2(x,t)=0.5&v2=true&f3(x,t)=log(0.5)/log(x)&v3=true&f4(x,t)=&v4=false&f5(x,t)=&v5=false&f6(x,t)=&v6=false&grid=2&coords=0.5,0.5,0.6666666666666666

// https://www.shadertoy.com/view/tlcfRn
float sabs(float x, float k) { return sqrt(x * x + k * k); }
// Better smooth abs functions with smooth 1st derivative and converging to linear: https://graphtoy.com/?f1(x,t)=mix(x,3,clamp(-x+3,0,3)/3/2)&v1=false&f2(x,t)=f1(abs(x))&v2=true&f3(x,t)=sqrt(x*x%20+%203*3)&v3=true&f4(x,t)=log(exp(x)+exp(3))%20+%20log(exp(-x)+exp(3))%20-%203&v4=true&f5(x,t)=&v5=false&f6(x,t)=&v6=false&grid=1&coords=2.2601521561494273,5.57995368872829,23.26586969184914
//float ssabs(float x, float k) { return mix(x, k, clamp(-x + k, 0. , k)/k/2.); }
//float ssabs(float x, float k) { return x >= k? x : -sqrt(k*k/(0.7071067812*0.7071067812)-x*x)+k*2.; }
//float ssabs(float x, float k) { return log(exp(x)+exp(k))+log(exp(-x)+exp(k))-k; }
float ssabs(float x, float k) { return x > k*10. ? x : log(exp(x)+exp(k))+log(exp(-x)+exp(k))-k; }

float sawtooth(float x, float len) { return abs(mod(x, len*2.) - len); }
//float ssawtooth(float x, float len) { return sinhz(x/len)/2. + 0.5; }
float ssawtooth(float x, float len, float trans) {
    // https://graphtoy.com/?f1(x,t)=sqrt(x%20*%20x%20+%20.1%20*%20.1)&v1=true&f2(x,t)=mod(x,%2010)-5&v2=false&f3(x,t)=f1(f2(x))&v3=false&f4(x,t)=5-f3(x+5)&v4=false&f5(x,t)=0.5%20-%20cos(x*6.28318530718/10)/2&v5=false&f6(x,t)=mix(f4(x),%20f3(x),%20f5(x))&v6=true&grid=1&coords=1.5548003398470707,2.9056924384027223,9.91735537190086
    float len2 = len/2.;
    x += len2;
    float ssaw_bot = sabs(mod(x, len) - len2, trans);
    float ssaw_top = len2 - sabs(mod(x + len2, len) - len2, trans);
    float mixk = 0.5 - cos(x*6.28318530718/len)/2.;
    return mix(ssaw_top, ssaw_bot, mixk);
}

float timesawtooth(float x, float len, float trans) {
    // https://graphtoy.com/?f1(x,t)=sqrt(x*x+.3*.3)&v1=false&f2(x,t)=exp(1/(5-x)/5)+4&v2=false&f3(x,t)=clamp(mod(x,5)-(5-0.3),%200,%201)&v3=false&f4(x,t)=&v4=false&f5(x,t)=mix(f1(x),%20f2(x),%20f3(x))&v5=false&f6(x,t)=f5(abs(mod(x+5,10)-5))&v6=true&grid=1&coords=2.314344811316915,2.631207494963487,14.446274591184533
    float len2 = len/2.;
#ifndef UPDOWN
    x += len2;
#endif
    x = abs(mod(x + len2, len) - len2);
    float smoothlow = ssabs(x, trans); // sabs(x, trans);
    float xtrans = x - len2 + trans;
    if (xtrans <= 0.) return smoothlow;
    float mixk = clamp(xtrans, 0., trans) / trans;
    // https://graphtoy.com/?f1(x,t)=tan(tan(x/3%20*%20atan(atan(100./3))))*3%20+%205%20-%203&v1=true&f2(x,t)=f1(3)&v2=true&f3(x,t)=&v3=false&f4(x,t)=&v4=false&f5(x,t)=&v5=false&f6(x,t)=&v6=false&grid=1&coords=-1.1158959084603164,1.2951708211315938,8.154545393121996
    float peak = tan(tan(xtrans/trans * atan(atan(10.))))*trans + len2 - trans;
    return mix(smoothlow, peak, mixk);
}

#define RANGE   (1./SPD)
#define STEP    (1./TONES_PER_OCTAVE)

