vec2 mainSound( int samp, float time )
{
    time = timesawtooth(time, TIMEPERIOD, TIMETRANS);

    float t = mod(time, RANGE);                        // time = [0 .. RANGE]
    float tf = t / RANGE;                              // time = [0 .. 1]
    // https://graphtoy.com/?f1(x,t)=&v1=false&f2(x,t)=exp(x*log(2))&v2=true&f3(x,t)=pow(2,%20x*log(2))&v3=true&f4(x,t)=(exp(x*log(2))-1)%20/%203&v4=true&f5(x,t)=((exp(x*log(2))-1)%20/%203)%20+%201&v5=true&f6(x,t)=&v6=false&grid=1&coords=0.7276582190451031,1.6451697223764041,3.8041556081466807
    float tfe = (exp2(tf) - 1.) / (exp2(RANGE) - 1.);  // exp time = [0..1]
    float tfe1 = tfe + 1.;                             // exp time = [1..2]

    float v = 0.;

    float iter = floor(time / RANGE);
    for (float i = floor(OCTAVEMIN); i < ceil(OCTAVEMAX); i+=STEP) {
        float f = octave2freq(tf+i);
        if (f < FREQMIN || f > FREQMAX) continue;
        v += sinhz(f*(t + i*RANGE)) * db2amp(envMid(octf(tf+i), octf(PEAKOCTAVE)));
    }

#ifdef MODULATE_BY
    v *= abs(sinhz_mod(MODULATE_BY / 2. * time));
#endif

    return vec2(v/(OCTAVEMAX - OCTAVEMIN)*STEP);
}

