float rand2(vec2 co) { return fract(sin(dot(co, vec2(12.9898, 70.233))) * 43758.5453); }
vec2 Rot(vec2 p, float th) { return vec2(p.x*cos(th)-p.y*sin(th), p.x*sin(th)+p.y*cos(th)); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy; 
    vec3 r = normalize(vec3(Rot(uv - vec2(.5, .5 + .35*sin(.157 * iTime)), .4 * sin(.3 * iTime)), 1.));
    fragColor = vec4(0,0,0,1);

    for(float a = 0.015; a < .3; a += .012)
    {
        float k = -a / r.y;
        vec2 m = (k * r.xz * vec2(10., 20.3 * a)) - vec2(23. * rand2(vec2(a)) , sign(r.y) * .15 * iTime);
        vec3 col2 = vec3(rand2(floor(m * 60.)) < .97);       
        float p = smoothstep(.8, .9, (1. - 2. * length(fract(m * 60.) - vec2(.5))));
        col2 = p * smoothstep(.5, .3, col2) / pow(abs(k), .9);
        float p2 = 1. - 2. * length(fract(m * 3.) - vec2(.5));
        col2 += pow(p2, 2.) * vec3(.3,0,.3) * rand2(floor(m * 3.));        
        fragColor.xyz = max(fragColor.xyz, pow(abs(r.y), .4) * col2 / (20. * a + 1.));
    }

    fragColor.xyz += 1.3 * (1. - 1.4 * abs(r.x)) * (pow(1. - abs(r.y), 2.) * vec3(.0, .2, .8) + pow(1. - abs(r.y), 40.4) * vec3(.1, .7, .95));
    fragColor.xyz += .9 * pow(1. - 2. * length(r.xy), 6.);
}

