float wf(float t, float ramp, float pulse) { return 2. * ((smoothstep(0., ramp, fract(t)) - smoothstep(ramp + pulse, 1., fract(t))) - .5); }
float PSin(float t) { return .5 + .5 * sin(t); }
float S(float time) { return wf(55. * time, .3 * PSin(.3*time) + .02, .5) 
                            + wf(55.3 * time, .15 * PSin(.3*time) + .02, .5) 
                            + wf(55. * (4./3.) * time, .15 * PSin(.3*time) + .05, .5); }

vec2 mainSound( int samp, float time )
{
    return .05*vec2( S(time) + .5*S(time-.7), S(time + .3) + .5*S(time - .4));
}

