// Fragment Shader Based Upon:
// aleklesovoi's "CRT TV Shader" Shadertoy Example (https://www.shadertoy.com/view/DldXWS)

// SynthEyes Lens Distortion:
// http://www.ssontech.com/content/lensalg.htm

vec2 SynthEyesDistortion(in vec2 uv, float aspect)
{
    // Distortion
    float kCoeff = 0.1;
    float kCube = 0.8;

    // SynthEyes uses UV coordinates with a [-1:1] range
    uv = (uv * 2.0) - 1.0;

    // Compute the distortion
    float r2 = (aspect * aspect) * (uv.x * uv.x) + (uv.y * uv.y);
    float f = 1.0 + r2 * (kCoeff * kCube * sqrt(r2));

    uv.x *= f;
    uv.y *= f;

    // Transform the UV coordinates back to a [0:1] range
    uv = (uv * 0.5) + 0.5;

    return uv;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Use a "Screen Space" UV Coordinate [0;1] range
    vec2 uv = fragCoord.xy / iResolution.xy;
    float aspect = iResolution.x / iResolution.y;

    // Apply the lens distortion
    uv = SynthEyesDistortion(uv, aspect);
    vec3 result = texture(iChannel0, uv).rgb;

    // Return the final output
    fragColor = vec4(result, 1.0);
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
}

buffer_a_code:
bool lessThan_(vec4 a, vec4 b) {
    // Comparison function for sorting
    return length(a.rgb) < length(b.rgb);
}

vec4 get(int i) {
    int w = textureSize(iChannel0, 0).x;
    return texelFetch(iChannel0, ivec2(i % w, i / w), 0);
}

bool lessThan_(vec4 a, int i, vec4 b, int j) {
    // Comparison function that uses pixel index for stable sort
    return lessThan_(a, b) ? true : lessThan_(b, a) ? false : i < j;
}

vec4 kth(int arr1, int m, int arr2, int n, int k) {
    // Returns the kth element (zero-indexed) from the result of merging
    // sorted arrays arr1 and arr2 with lengths m and n respectively in
    // O(log(k)) time.

    // Taken with modification from
    // https://www.geeksforgeeks.org/k-th-element-two-sorted-arrays/

    // we do a little recursive tomfoolery
    #define RECURSE(a, b, c, d, e) {             \
        int A = a, B = b, C = c, D = d, E = e;   \
        arr1 = A, m = B, arr2 = C, n = D, k = E; \
        continue;                                \
    }

    ++k; // original algorithm had k one-indexed

    while (true) {
        if (k > m + n || k < 1) {
            return vec4(0.0, 1.0, 0.0, 1.0);
        }

        if (m > n) {
            RECURSE(arr2, n, arr1, m, k);
        }

        if (m == 0) {
            return get(arr2 + k - 1);
        }

        if (k == 1) {
            vec4 a = get(arr1);
            vec4 b = get(arr2);
            return lessThan_(a, arr1, b, arr2) ? a : b;
        }

        int i = min(m, k / 2);
        int j = min(n, k / 2);
        int ai = arr1 + i - 1;
        int bi = arr2 + j - 1;

        if (lessThan_(get(ai), ai, get(bi), bi)) {
            RECURSE(arr1 + i, m - i, arr2, n, k - i);
        }

        RECURSE(arr1, m, arr2 + j, n - j, k - j);
    }
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    ivec2 r = ivec2(iResolution);
    ivec2 p = ivec2(fragCoord);

    if (iFrame == 0) {
        // Initialize data to sort on first frame
        fragColor = texture(iChannel1, fragCoord / iResolution.xy);
        return;
    }

    int N = r.x * r.y;
    int t = int(ceil(log2(float(N)))); // number of passes
    int i = p.x + r.x * p.y;

    if (iFrame > t) {
        // Sorting finished
        fragColor = get(i);
        return;
    }

    int arr1 = i >> iFrame << iFrame;
    int m = min(arr1 + (1 << iFrame - 1), N) - arr1;
    int arr2 = arr1 + m;
    int n = min(arr2 + m, N) - arr2;
    int k = i - arr1;
    fragColor = kth(arr1, m, arr2, n, k);
}

