// ---------------------------------------------------------------------------------------
//	Created by fenix in 2023
//	License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//
//  Inspired by this video by Nils Berglund:
//
//      https://www.youtube.com/watch?v=pN-gi_omIVE
//
//  One of the interesting things I learned from Mr. Berglund's video is that you can
//  get some interesting effects with the wave equation by allowing the speed of wave
//  propagation to vary spatially. My result looks very different from his, but I think
//  it looks pretty interesting anyway.
//
//  I also learned that the constant that determine's the wave speed is called the
//  Courant number:
//
//      https://www.idealsimulations.com/resources/courant-number-cfd/
//
//  I also learned that Richard Courant publushed a very early paper on computational
//  fluid dynamics in 1928, "ber die partiellen Differenzengleichungen der mathematischen
//  Physik", along with some other guys named Friederich, and Lewy (CFL), so I'm naming
//  this shader in his honor. Hopefully the name doesn't get confused for a type of
//  noise, because I'm just using 3D simplex noise from iq:
//
//      https://www.shadertoy.com/view/XsX3zB
//
//  Click and drag the mouse to emit more waves! Space to reset.
//
//  The simualtion itself is substantially the same as my previous shader this is
//  branched from. The absorbing walls are still the same, but their deficiencies
//  are not as obvious with all the motion. Mainly I just modulated the Courant
//  number and then added some lighting and color. Lighting-wise, the only really
//  interesting thing is the depth-based shadows (disable with shift).
//  
//  There are a couple of alternate color schemes available: change COLOR_SCHEME
//  below. I can't decide...which one is best?
//
// ---------------------------------------------------------------------------------------

#define COLOR_SCHEME 0 // choose 0, 1, or 2

vec2 gradient(vec2 fragCoord)
{
    if (fragCoord.y == iResolution.y - .5) return vec2(0);

    vec2 delta = vec2(1, 0);
    float xGrad = texture(iChannel0, (fragCoord + delta.xy) / iResolution.xy).a - 
        texture(iChannel0, (fragCoord - delta.xy) / iResolution.xy).a;

    float yGrad = texture(iChannel0, (fragCoord + delta.yx) / iResolution.xy).a - 
        texture(iChannel0, (fragCoord - delta.yx) / iResolution.xy).a;

    return vec2(xGrad, yGrad);
}

vec4 getColorFromHeight(float d)
{
#if COLOR_SCHEME == 0
    vec4 c = sin(d * 800. * vec4(9, 2, 5, 0));
    return c * .7 + vec4(.02,.02,.3,0);
#elif COLOR_SCHEME == 1
    int i = int(pow(abs(d), .49) * 80.);
    if (i <= 0) return vec4(.02,.02,.3,0);
    switch(i)
    {
        case 1: return vec4(1.0, 0.0, 0.0, 2.0);
        case 2: return vec4(1.0, 0.5, 0.0, 3.0);
        case 3: return vec4(1.0, 1.0, 0.0, 4.0);
        case 4: return vec4(0.0, 1.0, 0.0, 5.0);
        case 5: return vec4(0.0, 0.0, 1.0, 6.0);
        case 6: return vec4(0.25, 0.0, 0.5, 7.0);
        default: case 7: return vec4(0.5, 0.0, 0.7, 8.0);
    }    
#else
    vec4 c = pow(abs(d), .5) * vec4(1, .4, .2, 0) * 70. + .2;
    return c;
#endif
}

float computeShadow(vec2 u, float pd)
{
    if (keyDown(KEY_SHIFT)) return 1.;

    vec2 del = vec2(-1, 1);
    float sh = 1.;
    for (float i = 0.; i < 20.; ++i)
    {
        u += del;
        float nd = texelFetch(iChannel0, ivec2(u), 0).x;
        sh *= smoothstep(nd + float(i) * 5e-7, nd + float(i) * 1e-6, pd);
    }

    return sh;
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 g = -gradient(u);

    const vec3 ld = normalize(vec3(-1, 1, 1)); // light dir
    const vec3 vd = vec3(0, 0, 1); // view dir

    float wh = texelFetch(iChannel0, ivec2(u), 0).x; // wave height
    float sh = computeShadow(u, wh); // shadow
    vec3 n = normalize(vec3(g, .0001)); // normal
    O = vec4(dot(n, ld)) * (sh * .5 + .5); // diffuse

    O *= getColorFromHeight(wh); // colorize
}

