#define U(u) texelFetch(prevBuffer, ivec2(u), 0) //
const float FOCUS_DIST = .45;

// From iq's 3d simplex noise: https://www.shadertoy.com/view/XsX3zB

/* discontinuous pseudorandom uniformly distributed in [-0.5, +0.5]^3 */
vec3 random3(vec3 c)
{
	float j = 4096.0*sin(dot(c,vec3(17.0, 59.4, 15.0)));
	vec3 r;
	r.z = fract(512.0*j);
	j *= .125;
	r.x = fract(512.0*j);
	j *= .125;
	r.y = fract(512.0*j);
	return r-0.5;
}

/* skew constants for 3d simplex functions */
const float F3 =  0.3333333;
const float G3 =  0.1666667;

/* 3d simplex noise */
float simplex3d(vec3 p) {
	 /* 1. find current tetrahedron T and it's four vertices */
	 /* s, s+i1, s+i2, s+1.0 - absolute skewed (integer) coordinates of T vertices */
	 /* x, x1, x2, x3 - unskewed coordinates of p relative to each of T vertices*/

	 /* calculate s and x */
	 vec3 s = floor(p + dot(p, vec3(F3)));
	 vec3 x = p - s + dot(s, vec3(G3));

	 /* calculate i1 and i2 */
	 vec3 e = step(vec3(0.0), x - x.yzx);
	 vec3 i1 = e*(1.0 - e.zxy);
	 vec3 i2 = 1.0 - e.zxy*(1.0 - e);

	 /* x1, x2, x3 */
	 vec3 x1 = x - i1 + G3;
	 vec3 x2 = x - i2 + 2.0*G3;
	 vec3 x3 = x - 1.0 + 3.0*G3;

	 /* 2. find four surflets and store them in d */
	 vec4 w, d;

	 /* calculate surflet weights */
	 w.x = dot(x, x);
	 w.y = dot(x1, x1);
	 w.z = dot(x2, x2);
	 w.w = dot(x3, x3);

	 /* w fades from 0.6 at the center of the surflet to 0.0 at the margin */
	 w = max(0.6 - w, 0.0);

	 /* calculate surflet components */
	 d.x = dot(random3(s), x);
	 d.y = dot(random3(s + i1), x1);
	 d.z = dot(random3(s + i2), x2);
	 d.w = dot(random3(s + 1.0), x3);

	 /* multiply d by w^4 */
	 w *= w;
	 w *= w;
	 d *= w;

	 /* 3. return the sum of the four surflets */
	 return dot(d, vec4(52.0));
}

/* const matrices for 3d rotation */
const mat3 rot1 = mat3(-0.37, 0.36, 0.85,-0.14,-0.93, 0.34,0.92, 0.01,0.4);
const mat3 rot2 = mat3(-0.55,-0.39, 0.74, 0.33,-0.91,-0.24,0.77, 0.12,0.63);
const mat3 rot3 = mat3(-0.71, 0.52,-0.47,-0.08,-0.72,-0.68,-0.7,-0.45,0.56);

/* directional artifacts can be reduced by rotating each octave */
float simplex3d_fractal(vec3 m) {
    return   0.5333333*simplex3d(m*rot1)
			+0.2666667*simplex3d(2.0*m*rot2)
			+0.1333333*simplex3d(4.0*m*rot3)
			+0.0666667*simplex3d(8.0*m);
}

#define keyClick(ascii)   ( texelFetch(keyBuffer,ivec2(ascii,1),0).x > 0.)
#define keyDown(ascii)    ( texelFetch(iChannel3,ivec2(ascii,0),0).x > 0.)

#define KEY_SHIFT 16
#define KEY_CTRL 17
#define KEY_SPACE 32

const float BOUND_GAMMA = .01; // damping factor for boundary

vec2 initWave(vec2 u, vec2 c, vec2 R)
{
    vec2 delta = u - c;
    float d2 = dot(delta, delta);

    float df = sqrt(d2);
    return 3. * R.x * vec2(sin(df), cos(df)) / (1. + d2 * 1e6);
}

void updateBuffer(out vec4 O, vec2 u, vec2 R, int iFrame, vec4 iMouse, sampler2D prevBuffer, sampler2D keyBuffer, bool bufferA)
{
    vec2 o = vec2(1, 0),
         p = (u - .5 * R) / R.y;

    vec4 state = texelFetch(prevBuffer, ivec2(0), 0);

    // handle persistent state
    if (ivec2(u) == ivec2(0))
    {
        if (iFrame == 0 ||
            state.w != R.x * R.y ||
            keyClick(KEY_SPACE))
        {
            O = vec4(0);
            O.w = R.x * R.y;
            return;
        }

        O = state;

        if (bufferA && ++O.z > 10.)
            O.z = 0.;

        return;
    }

    if (iFrame == 0 ||
        state.w != R.x * R.y ||
        keyClick(KEY_SPACE))
    {
        // init
        O = vec4(0);
        return;
    }
    else
    {
        float C = simplex3d_fractal(vec3(p * 10., float(iFrame) * .01)); // Courant
        C = .5 * pow(C * .5 + .5, 4.);

        O = U(u).xxyy; // copies the current value from x to y coordinate to record new previous, put current previous into z for us to use

        if (int(u.y) < 5)
            O.x = O.x - C * (O.x - U(u + o.yx).x) - BOUND_GAMMA*(O.x-O.z); // absorbing bottom boundary
        else if (int(u.y) == int(R.y) - 1)
            O.x = O.x - C * (O.x - U(u - o.yx).x) - BOUND_GAMMA*(O.x-O.z); // absorbing top boundary
        else if (int(u.x) == 0)
            O.x = O.x - C * (O - U(u + o)).x - BOUND_GAMMA*(O.x-O.z); // absorbing left boundary
        else if (int(u.x) == int(R.x) - 1)
            O.x = O.x - C * (O - U(u - o)).x - BOUND_GAMMA*(O.x-O.z); // absorbing right boundary
        else
            O.x = -O.z + 2.*O.x + C*(U(u + o) + U(u - o) + U(u + o.yx) + U(u - o.yx) - 4. * O).x; // apply wave equation
    }

    if (bufferA)
    {
        vec2 h = vec2(.5);

        // add disturbance at the center
        if (state.z < 1. || iFrame == 0)
            O.xy += initWave(u, h * R.xy, R);

        // add disturbance for mouse click
        if (iMouse.z > 0. && (state.z < 1. || iMouse.w > 0.))
            O.xy += initWave(u, iMouse.xy, R);
    }

    O *= .9995;
}

