void mainImage( out vec4 O, vec2 I )
{  
    I = 4. * I / iResolution.y + 1e2 + .4 * iTime;   
    mat2 M = -mat2(.766,.643, -.643,.766); // rot(a = 21*pi/27)
    O -= O;
    for (int i; i++ < 27;) {   
        I *= M;
        //pow(fract(I.y), 2.); is good too
        O.x += pow(4. * fract(I.y) * (1. - fract(I.y)), 40.);
        O.y += cos(2. * O.x);
        O.yz *= M;
        O.z += cos(O.y);
        O.zw *= M;
        O.w += cos(O.z);
    }
    O = tanh(.5 * O.yyyy);    
}

// Convert rgb color to cmyk
vec4 cmyk(vec3 c) {
    float k = 1.0 - max(c.r, max(c.g, c.b));
    return vec4(
        (1.0 - c.r - k) / (1.0 - k),
        (1.0 - c.g - k) / (1.0 - k),
        (1.0 - c.b - k) / (1.0 - k),
        k
    );
}

// Grid pattern rotated by 'a' radians with the density 'dens'
vec2 grid(vec2 uv, float a, float dens) {
    vec2 aspect = iResolution.xy / iResolution.y;
    return vec2(
        mod(((uv.x * aspect.x) * cos(a) - uv.y * sin(a)) * dens, 1.0), 
        mod(((uv.x * aspect.x) * sin(a) + uv.y * cos(a)) * dens, 1.0)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;

    //Color of the image, converted to cmyk
    vec4 cmyk = cmyk(col);

    //Density of the dots
    float dens = 100.0;

    //Rotation angles for different ink colors
    //http://the-print-guide.blogspot.com/2009/05/halftone-screen-angles.html
    float ca = 0.261;
    float ma = 1.309;
    float ya = 0.0;
    float ka = 0.785;

    vec2 cgrid = grid(uv, ca, dens);

    float cb = cmyk.x * 0.8 / length(cgrid - vec2(0.5));
    cb = pow(cb, 5.0);
    cb = max(min(cb, 1.0), 0.0);
    vec3 cv = vec3(cb, 0.0, 0.0);

    vec2 mgrid = grid(uv, ma, dens);

    float mb = cmyk.y * 0.9 / length(mgrid - vec2(0.5));
    mb = pow(mb, 5.0);
    mb = max(min(mb, 1.0), 0.0);
    vec3 mv = vec3(0.0, mb, 0.0);

    vec2 ygrid = grid(uv, ya, dens);

    float yb = cmyk.z * 0.8 / length(ygrid - vec2(0.5));
    yb = pow(yb, 5.0);
    yb = max(min(yb, 1.0), 0.0);
    vec3 yv = vec3(0.0, 0.0, yb);

    vec2 kgrid = grid(uv, ka, dens);

    float kb = cmyk.w * 0.6 / length(kgrid - vec2(0.5));
    kb = pow(kb, 5.0);
    kb = max(min(kb, 1.0), 0.0);
    vec3 kv = vec3(kb);

    // Output to screen
    fragColor = vec4(vec3(1.0) - cv - mv - yv - kv,1.0);
}

const float pi = 3.1415926536;
const float maxDist = 1000.0;
const float epsilon = 0.001;

struct Material {
    vec3 color;
    bool lit;
    float roughness;
};


/* List of materials */

Material sky = Material(2.5 * vec3(0.1, 0.5, 1.0), false, 0.0);
Material mat0 = Material(vec3(0.3), true, 0.3);
Material matRed = Material(vec3(0.6, 0.05, 0.02), true, 0.1);

struct SurfacePoint {
    vec3 pos;
    vec3 normal;
    vec3 incidentRayDir;
    Material material;
};

struct Ray {
    vec3 origin;
    vec3 dir;
};

struct Intersection {
    float depth;
    vec3 normal;
    Material material;
};

SurfacePoint getSurfacePoint(Ray ray, Intersection hit) {
    SurfacePoint surface = SurfacePoint(ray.origin + ray.dir * hit.depth, hit.normal, ray.dir, hit.material);

    // mat0 Checkerboard texture
    if (surface.material == mat0) {
       surface.material.color = mix(surface.material.color, vec3(0.05), floor(mod(surface.pos.x + floor(mod(surface.pos.z, 2.0)), 2.0)));
    }
    return surface;
}

/**
 * Returns the closer of two intersections
 */
Intersection nearest(Intersection current, Intersection new) {
    if (new.depth < current.depth && new.depth > 0.0) {
        return new;
    }
    return current;
}


/* Intersection Functions */

Intersection sphere(Ray ray, vec3 pos, float radius, Material material) {
    vec3 offset = pos - ray.origin;
    float dot1 = dot(offset, ray.dir);
    if (dot1 < 0.0) {
        return Intersection(-1.0, vec3(0), material);
    }
    float v = radius * radius - (dot(offset, offset) - dot1 * dot1);
    if (v > 0.0) {
        float near = sqrt(v);
        float depth = dot1 - near;
        vec3 normal = (ray.dir * depth - offset) / radius;
        return Intersection(depth, normal, material);
    }
    return Intersection(-1.0, vec3(0), material);
}

Intersection plane(Ray ray, vec3 normal, float dist, Material material) {
    return Intersection(-(dot(ray.origin, normal) + dist)/dot(ray.dir, normal), normal, material);
}

/*
 * Ray intersection with the scene
 */
Intersection raycast(Ray ray, float maxDist) {
    Intersection hit = Intersection(maxDist, vec3(0), sky);
    hit = nearest(hit, sphere(ray, vec3(0, 1, 5), 1.0, matRed));
    hit = nearest(hit, plane(ray, vec3(0, 1, 0), 0., mat0));
    return hit;
}

/*
 * Schlick's Approximation
 */
float fresnel(float n1, float n2, vec3 normal, vec3 incident) {
    float r0 = (n1 - n2)/(n1 + n2);
    r0 *= r0;
    float a = (1.0 - dot(normal, incident));
    float b = a * a;
    b *= b;
    return r0 + (1.0 - r0) * (a * b);
}

/* 
 * The GGX normal distribution function. This is
 * the D term of the GGX BRDF.
 */
float ggxD(float alpha, float NdotH) {
    float a2 = alpha * alpha;
    float k = NdotH * NdotH * (a2 - 1.0) + 1.0;
    return a2/(pi * k * k);
}

/*
 * The Smith shadowing/masking function for GGX.
 * This is G1, and is part of the G term of the GGX BRDF.
 */
float ggxG1(float alpha, float VdotN) {
    float a2 = alpha * alpha;
    return 2.0 * VdotN/(VdotN + sqrt(a2 + VdotN * VdotN * (1.0 - a2)));
}

// Bi-directional Reflectance Distribution Function
vec3 brdf(SurfacePoint surface, vec3 light, float NdotL) {

    // The microsurface normal, calculated as halfway between the light and incident directions.
    vec3 halfway = normalize(light - surface.incidentRayDir);

    float NdotH = dot(halfway, surface.normal);
    float alpha = surface.material.roughness * surface.material.roughness;

    // The D and G terms of the BRDF
    float D = ggxD(alpha, NdotH);
    float G = ggxG1(alpha, dot(-surface.incidentRayDir, surface.normal)) * ggxG1(alpha, dot(light, surface.normal));

    // GGX specular BRDF (without fresnel)
    float specular = (G * D)/(4.0 * abs(dot(surface.normal, -surface.incidentRayDir)) * abs(NdotL));

    // Final output - mix between refraction and specular with fresnel
    return mix(surface.material.color / pi, vec3(specular), fresnel(1.0, 1.5, halfway, -surface.incidentRayDir));
}

vec3 directionalLight(SurfacePoint surface, vec3 light, vec3 intensity, float dist) {
    light = normalize(light);
    float NdotL = dot(surface.normal, light);
    return float(raycast(Ray(surface.pos + epsilon * surface.normal, light), dist).depth >= dist) * intensity * brdf(surface, light, NdotL) * max(0.0, NdotL);
}

vec3 directionalLight(SurfacePoint surface, vec3 light, vec3 intensity) {
    return directionalLight(surface, light, intensity, maxDist);
}

vec3 pointLight(SurfacePoint surface, vec3 lightPos, vec3 intensity) {
    vec3 light = lightPos - surface.pos;
    float dist = length(light);
    return directionalLight(surface, light, intensity/(dist * dist), dist);
}

vec3 lighting(SurfacePoint surface) {

    Material mat = surface.material;

    vec3 col = mat.color;
    if (mat.lit) {
        col = directionalLight(surface, vec3(sin(iTime - 4.0), 1, cos(iTime - 4.0)), 30.0 * vec3(1));
        col += mat.color * sky.color; // Ambient lighting, which is probably a little less accurate
    }

    if (mat == sky) {
        col.b += 10.0 * surface.incidentRayDir.y;
        col.g -= 1.5 * surface.incidentRayDir.y;
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec3 camPos = vec3(0, 1.0, 0);

    Ray ray = Ray(camPos, normalize(vec3(uv, 1.0)));
    Intersection hit = raycast(ray, 10000.0);

    SurfacePoint surface = getSurfacePoint(ray, hit);

    float exposure = 1.0;
    col = exposure * lighting(surface);

    // Lazy Tonemapping
    col = vec3(atan(col.r), atan(col.g), atan(col.b))/(0.5 * pi);

    // Gamma Correction
    col = pow(col, vec3(1.0/2.2));

    //col = hit.normal;
    //col = vec3(uv, 0.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI 3.141592653592

float Circle(vec2 p, vec2 c, float r) {
    return length(p-c) - r;
}

vec2 polarArray(vec2 p, float n, vec2 c, float a ) {
    // p: the point to transform
    // c: the center of the array
    // a: the offset angle

    p = mat2(cos(a), -sin(a), sin(a), cos(a)) * p; //rotate in XY plane

    // Array parameters
    float theta = 2. * PI / n;                    
    float segment = round(atan(p.y,p.x) / theta); 
    float alpha = segment * theta;                

    vec2 q = p;

    q.xy = mat2(cos(alpha), -sin(alpha), sin(alpha), cos(alpha)) * q.xy; //rotate in XY plane

    return q;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5*iResolution .xy)/iResolution.y;


    // Colors
    vec3 col1 = vec3(244, 162, 97) / 255.; // yellow
    vec3 col2 = vec3(231, 111, 81) / 255.; // orange
    vec3 col3 = vec3(38, 70, 83) / 255.;   // blue
    vec3 col4 = vec3(233, 196, 106) / 255.;   // green


    vec2 p = uv; // the "local" coordinate point

    // Rotating the whole array
    float a = 0.1*iTime;
    p = mat2(cos(a), -sin(a), sin(a), cos(a)) * p; //rotate in XY plane


    // Array parameters
    float N = round(12. + 6.*sin(2.*iTime));      // Number of instances
    float theta = 2. * PI / N;                    // angle between instances
    float segment = round(atan(p.y,p.x) / theta); // the instance/segment number
    float alpha = segment * theta;                // rotation angle


    // Rotating each point back to position 0
    vec2 q = p;

    // ==================================================
    // 2D Rotation of point (x,y) by angle a in XY plane:
    // | cos(a) -sin(a) || x |
    // | sin(a)  cos(a) || y |
    // ==================================================

    q.xy = mat2(cos(alpha), -sin(alpha), sin(alpha), cos(alpha)) * q.xy; //rotate in XY plane

    // Radius of polar array
    float r = 0.35 + 0.1*sin(iTime);

    // distance/map function. This is the circle that will be arrayed
    float d = Circle(q, vec2(r,0.), 0.05);

    // Visualization
    vec3 col = col1;
    float c = abs(Circle(p, vec2(0.), r)) * mod(segment,2.); // segment colors
    col = mix(col2, col, smoothstep(0.,1.,250.*c));          // array path

    // Axes
    col = mix(vec3(1.), col, smoothstep(0.,1.,500.*abs(uv.x)));
    col = mix(vec3(1.), col, smoothstep(0.,1.,500.*abs(uv.y)));

    // Array instances
    col = mix(col3,col, smoothstep(0.,1.,200.*d));


    // Higlighting the starting instance
    float f = Circle(p, vec2(r,0.), 0.05);

    col = mix(vec3(1.),col, smoothstep(0.,1.,400.*abs(f)));


    // Version that packs it into a function
    vec2 q2 = polarArray(uv, 8., vec2(0.), 0.);
    float d2 = Circle(q2, vec2(0.15,0.), 0.025);

    col = mix(col4,col, smoothstep(0.,1.,200.*d2));

    fragColor = vec4(col,1.0);


}

vec3 palette(float t)
{
	vec3 a = vec3(0.450, 0.450, 0.35);
	vec3 b = vec3(0.4, 0.5, 0.3);
	vec3 c = vec3(2., 3.0, 4.0);
	vec3 d = vec3(0.6, 0.5, 0.3);

	return a + b * cos(6.28318 * (c * t + d));
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 xy = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
	float t = iTime;
	const float PI = 3.14159265358979323846264338327950288;
	float d0 = length(xy);

	vec3 fColor = vec3(0, 0, 0);

	for (float i = 0.0; i < 3.0; i++)
	{

		xy = fract(xy * (1. + (sin(.5 * PI * mod(t,11.) * d0)))) - 0.5;

		float d = length(xy);

		float p = sin(1. * PI * d + 0.75 * mod(t,17.));
		p = abs(p);
		float q = 2. * PI * p;

		float r = 1. - abs(sin(q) - p) / 2.;
		float g = 1. - abs(cos(q) - p) / 2.;
		float b = (pow(r, 2.) + pow(g, 2.));

		b = pow(0.1 / b, 0.5);

		vec3 col = palette(d0 * t * 0.01 * b);

		col *= b;

		fColor += col;
	}

	fragColor = vec4(fColor-0.1, 1.0);
}

// Original shader (Image tab): "Fnaf 3D shader" by noClueBruh. https://shadertoy.com/view/DdsBzl

// CRT shader (Buffer A): "FixingPixelArt" by TimothyLottes. https://www.shadertoy.com/view/XsjSzR

// The rotating crewmate: "3D low poly rotating Amogus" by GetItemFromBlock. https://www.shadertoy.com/view/dsVXRm

float str = 1.;
float ok = 1.5;

vec2 warp(vec2 inp)
{
    inp.y -= (inp.y - .5)* str * pow(abs(inp.x - .5), ok);
    return inp;
}

vec4 col(vec2 inp)
{
    inp = fract(inp*5.);

    if (inp.x < 0. || inp.x > 1. || inp.y < 0. || inp.y > 1.)
    {
        return vec4(0,0,0,1);
    }
    return texture(iChannel0, inp);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = warp(fragCoord/iResolution.xy);
    fragColor = col(uv);
}

