vec4 processTriangle(ivec2 coord)
{
	mat4 mvp = mat4(
        texelFetch(iChannel0,ivec2(0,0),0),
        texelFetch(iChannel0,ivec2(0,1),0),
        texelFetch(iChannel0,ivec2(0,2),0),
        texelFetch(iChannel0,ivec2(0,3),0)
    );
    vec3 ep0 = (mvp * vec4(vectors[triangles[coord.x].a],1.0)).xyz;
    vec3 ep1 = (mvp * vec4(vectors[triangles[coord.x].b],1.0)).xyz;
    vec3 ep2 = (mvp * vec4(vectors[triangles[coord.x].c],1.0)).xyz;

    // transform to clip space
    float w0 = 1.0/ep0.z;
    float w1 = 1.0/ep1.z;
    float w2 = 1.0/ep2.z;

    if (coord.y == 0)
    {
        return vec4(w0,w1,w2,0);
    }

    vec2 cp0 = 2.0*ep0.xy * -w0;
    vec2 cp1 = 2.0*ep1.xy * -w1;
    vec2 cp2 = 2.0*ep2.xy * -w2;
    if (coord.y == 1)
    {
        return vec4(cp0, cp1);
    }
    else if (coord.y == 2)
    {
        return vec4(cp2, 0, 0);
    }
    float minY = min(min(cp0.y, cp1.y), cp2.y);
    float maxY = max(max(cp0.y, cp1.y), cp2.y);
    float minX = min(min(cp0.x, cp1.x), cp2.x);
    float maxX = max(max(cp0.x, cp1.x), cp2.x);
    return vec4(minX, maxX, minY, maxY);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 coord = ivec2(fragCoord);
    if (coord.x < TRIANGLE_COUNT)
    {
        if (coord.y < 4)
        {
            fragColor = processTriangle(coord);
        }
    }
}

