// Fork of "Space Ship with Anaglyph 3D" by GetItemFromBlock. https://shadertoy.com/view/DdcSz8
// GetItemFromBlock - 2023-04-07

// Based on "Rasterizer - Cube" by iq. https://shadertoy.com/view/XdlGzn

// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

// Update - 2023-05-30
// Added reflection on the glass, not sure if this is an improvement...

vec3 rasterize(in vec2 fragCoord)
{
    vec2 px = (2.0*fragCoord-iResolution.xy) / iResolution.y;

    vec3 color = vec3(0.478,0.478,0.478);
    // clear zbuffer
    float mindist = -1000000.0;

    for (int i = 0; i < TRIANGLE_COUNT; i++)
    {
        vec4 bounds = texelFetch(iChannel0, ivec2(i, 3), 0);
        if (px.x < bounds.x || px.x > bounds.y || px.y < bounds.z || px.y > bounds.w)
        {
            continue;
        }
        vec4 frb = texelFetch(iChannel0, ivec2(i, 1), 0);
        vec4 frc = texelFetch(iChannel0, ivec2(i, 2), 0);
        vec2 cp0 = frb.xy;
        vec2 cp1 = frb.zw;
        vec2 cp2 = frc.xy;

        vec3 di = vec3( cross2d( cp1 - cp0, px - cp0 ), 
					    cross2d( cp2 - cp1, px - cp1 ), 
					    cross2d( cp0 - cp2, px - cp2 ) );

        if( all(greaterThan(di,vec3(0.0))) )
        {
            vec4 fra = texelFetch(iChannel0, ivec2(i, 0), 0);
            float w0 = fra.x;
            float w1 = fra.y;
            float w2 = fra.z;

            vec3 p0 = vectors[triangles[i].a] * w0;
            vec3 p1 = vectors[triangles[i].b] * w1;
            vec3 p2 = vectors[triangles[i].c] * w2;

            // calc barycentric coordinates
            vec3 ba = di.yzx / (di.x+di.y+di.z);

            // barycentric interpolation of attributes and 1/z
            float iz = ba.x*w0 + ba.y*w1 + ba.z*w2;

            // recover interpolated attributes
            float z = 1.0/iz;

			// depth (-1/z) buffer test
			if( z>mindist )
			{
				mindist = z;

                vec3 na = vectors[triangles[i].na] * w0;
                vec3 nb = vectors[triangles[i].nb] * w1;
                vec3 nc = vectors[triangles[i].nc] * w2;
                vec3 n = ba.x*na + ba.y*nb + ba.z*nc;

				// perform lighting/shading 
                vec3 cameraPos = texelFetch(iChannel1, ivec2(0,4), 0).rgb;
                vec3 worldPos = p0 * ba.x + p1 * ba.y + p2 * ba.z;
                worldPos *= z;
                n = normalize(n*z);
                vec3 viewdir = normalize(cameraPos-worldPos);
				color = shader(-viewdir, n, vectors[triangles[i].col]);
                if (vectors[triangles[i].col].r < .5) color = mix(texture(iChannel2, reflect(viewdir, n)).rgb, color, .9);
			}
        }
    }
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(rasterize(fragCoord), 1);
}

